import { useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

export default function MobileGallery({ pictures, videos, color, onImageClick }) {
  const [currentSlide, setCurrentSlide] = useState(0);

  const settings = {
    arrow: false,
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    swipeToSlide: true,
    beforeChange: (oldIndex, newIndex) => {
      setCurrentSlide(newIndex);
    }
  };

  // Combine pictures and videos into a single array
  const media = [
    ...pictures.map((picture) => ({ type: 'image', path: picture.path })),
    ...videos.map((video) => ({ type: 'video', path: video.path }))
  ];

  return (
    <div>
      <Slider {...settings}>
        {media.map((item, index) => (
          <div key={index}>
            {item.type === 'video' ? (
              <video
                className='gallery__main-video'
                controls
                autoPlay
                muted
                src={`https://dolina.shop/videos/${item.path}`}
                alt={`Video ${index}`}
                onClick={onImageClick}
              />
            ) : (
              <img
                src={`https://dolina.shop/photos/${item.path}`}
                alt={`Photo ${index}`}
                onClick={onImageClick}
              />
            )}
          </div>
        ))}
      </Slider>
      <div style={{ textAlign: 'center', marginTop: '10px' }}>
        <span>
          {currentSlide + 1} / {media.length}
        </span>
      </div>
    </div>
  );
}
