import './Product.css';
import Header from '../Header/Header';
import Crumbs from '../Crumbs/Crumbs';
import SizeCard from '../SizeCard/SizeCard';
import productsApi from '../../utils/Api';
import { useState, useEffect } from 'react';
import crystalIcon from '../../images/crystal-icon.svg';
import tagIcon from '../../images/tag.svg';
import GalleryPopup from './GalleryPopup';

export default function Product({ productData }) {
  let mainPicture = productData.mainPicture;
  mainPicture = mainPicture.replace(/'/g, ''); // kolkhoz, исправить на бэке
 
  let temporarySizes = [
    15, 15.5, 16, 16.5, 17, 17.5, 18, 18.5 // времянка до получения массива с размерами колец
  ]
  let pictures = productData.pictures;
  pictures = JSON.parse(pictures); // это должно быть в api 
  const [isPreview, setIsPreview] = useState(mainPicture); //поднять стейт?
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  function handleImageClick() {
    setIsPopupOpen(true);
    console.log('работает');
  }
 
return (
  <>
  <Header/>
  <Crumbs/>

  <main className='product'>
    {/* <h1 className='product__title border-red'>{productData.title}</h1> */}
    <div className='product__container'>
      <div className='gallery'>
        <div className='gallery__previews'>

        {pictures.map((picture) => (
              <div className='' > 
                <img className='gallery__preview' src={picture} onClick={(evt) => {
                  // console.log('и че')
                  setIsPreview(picture)
                }}></img>
              </div>
            ))}

        </div>
        <div className='gallery__container'>
          <div className='card__image-overlay'></div>
          <img className='gallery__main-pic' src={isPreview} onClick={handleImageClick}></img>
        </div>
      </div>

      <div className='product_block_2'>

        <section className='product__sizes-container'>
        <h2 className='product__subtitle'>Выберите размер:</h2>
        <ul className='product__sizes'>
          {
            temporarySizes.map((size) => (
              <SizeCard size={size}/> // жирновато для такой мелочи иметь свой компонент
            ))
          }
        </ul>
        </section>

        <section className='product__chars-container'>
          <h2 className='product__subtitle'>Характеристики</h2>

          <table className='product__table'>
            <tbody className='product_font_main'>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Камень</th>
                <div></div>
                <td className='product__table-right'>Норм камень</td>
              </tr>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Металл</th>
                <div></div>
                <td className='product__table-right'>{productData.metalType || 'Мельхиор'}</td>
              </tr>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Вес</th>
                <div></div>
                <td className='product__table-right'>{productData.weight} гр</td>
              </tr>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Длина изделия</th>
                <div></div>
                <td className='product__table-right'>120 см</td>
              </tr>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Происхождение камня</th>
                <div></div>
                <td className='product__table-right'>{productData.stoneOrigin || 'Урал'}</td>
              </tr>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Страна производства</th>
                <div></div>
                <td className='product__table-right'>Россия</td>
              </tr>

            </tbody>
          </table>

        </section>
        <section className='product__description-container'>
          <h2 className='product__subtitle'>Описание</h2>
          <p className='product__description product_font_main'>{productData.text}</p>
        </section>
      </div>
      <div className='product_block_3'>
        <div className=''>
          <form className='product__form'>
            <div className='product__buttons'>
            <h1 className='product__title'>{productData.title}</h1>
            <button type='button' className='product__like-button pointer'></button>
            </div>
            
            <span className='product__price'>{productData.price} &#8381;</span>
            <span className=''>В наличии {productData.unique} шт.</span>
              <button type='submit' className='product__submit-button hover_type_normal pointer'>Добавить в корзину</button>
              <ul className='product__advantages'>
                <li className='product__advantages-item'>
                  <img className='product__advantages-icon' src={crystalIcon}/>
                  <span className='product__advantages-text'>Натуральный камень</span>
                </li>
                <li className='product__advantages-item'>
                  <img className='product__advantages-icon' src={tagIcon}/>
                  <span className='product__advantages-text'>Примерка</span>
                </li>
              </ul>
          </form>
        </div>
      </div>
    </div>
    <GalleryPopup mainImage={mainPicture} images={pictures} isOpen={isPopupOpen}/>
  </main>

  </>
)
}

// Что в товаре: 
// - тайтл +
// - цена +
// - артикул
// - кол-во в наличии +
// - "просматривают"
// - кнопка в корзину +
// - кнопка в избранное +
// - "все товары категории" !
// - "все товары из того-то" !
