import './Product.css';
import Header from '../Header/Header';
import Crumbs from '../Crumbs/Crumbs';
import productsApi from '../../utils/Api';
import { useState, useEffect } from 'react';

export default function Product({ productData }) {

  let temporaryPic = productData.mainPicture;
  temporaryPic = temporaryPic.replace(/'/g, ''); // kolkhoz

 

return (
  <>
  <Header/>
  <Crumbs/>

  <main className='product'>
    {/* <h1 className='product__title border-red'>{productData.title}</h1> */}
    <div className='product__container'>
      <div className='product_block_1 border-red'>
        <img className='mainPic' src={temporaryPic}></img>
      </div>
      <div className='product_block_2 border-red'>
        <section className='product__chars-container'>
          <h2 className='product__subtitle'>Характеристики</h2>

          <table>
            <tbody className='product_font_main'>
              <tr>
                <th>Камень</th>
                <td></td>
              </tr>
              <tr>
                <th>Металл</th>
                <td>{productData.metalType}</td>
              </tr>
              <tr>
                <th>Вес</th>
                <td>{productData.weight}</td>
              </tr>
              <tr>
                <th>Длина изделия</th>
                <td></td>
              </tr>
              <tr>
                <th>Страна происхождения камня</th>
                <td>{productData.stoneOrigin}</td>
              </tr>
              <tr>
                <th>Страна производства</th>
                <td></td>
              </tr>
            </tbody>
          </table>

        </section>
        <section className='product__description-container'>
          <h2 className='product__subtitle'>Описание</h2>
          <p className='product__description product_font_main'>{productData.text}</p>
        </section>
      </div>
      <div className='product_block_3 border-red'>
        <div className=''>
          <form className='product__form'>
            <h1 className='product__title'>{productData.title}</h1>
            <span className='product__price'>{productData.price} &#8381;</span>
            <span className=''>В наличии {productData.unique} шт.</span>
            <div className='product__buttons'>
              <button type='submit' className='product__submit-button'>Добавить в корзину</button>
              <button type='button' className='product__like-button'></button>
            </div>
          </form>
        </div>
      </div>
    </div>
  </main>

  </>
)
}

