import './Header.css';
import logo from '../../images/logo2.png';
import UsersNavBar from './UsersNavBar/UsersNavbar';
import SearchForm from '../SearchForm/SearchForm';
import { Link, NavLink, useLocation } from 'react-router-dom';
import { useMediaQuery } from 'react-responsive';
import BurgerMenu from './BurgerMenu/BurgerMenu';
import { useEffect, useState } from 'react';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';
// import { useState, useEffect } from "react";


export default function Header({ terms, onCatClick, activeCategory, isBurgerOpen, onBurgerClose, search, setSearch, cart, orders }) {
  const isBigScreen = useMediaQuery({ minWidth: 768 }); // надо бы перенести в константы
  const isSmallScreen = useMediaQuery({ maxWidth: 520 });

  function handleClick(event) {
    event.preventDefault();
    onCatClick(event.target.dataset.id);
  }  
  
  function resetCategory() {
    onCatClick('');
  }

  let cartNumber = Object.keys(cart).length;


  // const [isBurgerOpen, setIsBurgerOpen] = useState(false);
  // // console.log(isBurgerOpen)

  // function handleBurgerOpening() {
  //   setIsBurgerOpen(true);
  // }

  // function handleBurgerClosing() {
  //   setIsBurgerOpen(false);
  // }

  //  if (!terms) {
  //   return (
  //     <div>
  //       <Skeleton height={42} width={818} />
  //     </div>
  //   );
  // }

  
  return (
    <>
      {isBigScreen && ( <header className="header">
      <div className='header_block_2'>
        <Link to="/">
          <img src={logo} className="logo_theme_light" alt="Логотип" onClick={resetCategory} />
        </Link>
        <UsersNavBar
        ordersNumber={orders.length}
        cartNumber={cartNumber}/>
      </div>
      <nav className='header_block_3'>
          <div className='header__menu'>
            <NavLink to='/catalog/' className='navlink_size_m' onClick={resetCategory}>Каталог</NavLink>
            {
            !terms ? ( // радостно не работает ни в каком виде
              <div>
              <NavLink to='/' className='navlink_size_m'>Кольца</NavLink>
              <NavLink to='/' className='navlink_size_m'>Бусы</NavLink>
              <NavLink to='/' className='navlink_size_m'>Серьги</NavLink>
              <NavLink to='/' className='navlink_size_m'>Подвески</NavLink>
              <NavLink to='/' className='navlink_size_m'>Комплекты</NavLink>
              <NavLink to='/' className='navlink_size_m'>Браслеты</NavLink>
              <NavLink to='/' className='navlink_size_m'>Колье</NavLink>
              <NavLink to='/' className='navlink_size_m'>Броши</NavLink>
            </div>

            ) : (
              terms.categories && terms.categories.map((term) => (
                <NavLink to='' className={'navlink_size_m' + (term.termId == activeCategory ? ' navlink_active' : '')} onClick={handleClick} key={term.termId} data-id={term.termId}>{term.name}</NavLink>
              ))
            )
            }
            <NavLink to='/new' className='navlink_size_m'>Новинки</NavLink>
            <NavLink to='/sale' className='navlink_size_m'>Распродажа</NavLink>
          </div>
          <section className=''>
          <SearchForm 
            search={search} 
            setSearch={setSearch} 
          />
          </section>
      </nav>
      {/* <BurgerMenu/> */}
    </header>)}
    { isSmallScreen && (
      <header className='header_type_mobile'>
        <SearchForm 
            search={search} 
            setSearch={setSearch} 
        />
        <BurgerMenu 
        isOpen={isBurgerOpen} 
        onClose={onBurgerClose}
        terms={terms}
        onCatClick={onCatClick}
        activeCategory={activeCategory}
        />
      </header>
    )}
    </>
  )
}

{/* <Link to="/">
<img src={logo} className="logo_theme_light" alt="Логотип" />
</Link> */}

// {isSmallScreen && (
//   <div className='gallery_mobile'>
//   <MobileGallery 
//   pictures={pictures} 
//   onImageClick={handleImageClick}/>
//   <button type='button' className={(isSaved ? 'card__save-button card__save-button_active' : 'card__save-button')} onClick={handleLike}></button>
//   <div className='card__image-overlay'></div>
// </div>
// )}

// Что есть в шапочке: 
// - лого и название
// - форма поиска 
// - номер телефона 
// - пользов-е иконки: корзина, избранное, профиль (мб, заказы)
// - основное меню по товарам
// - хлебные крошки как на ламоде?
// - опционально: инфа о доставке, город покупателя, инфа об оплате, как мы работаем, о нас
