import './ShoppingCart.css';
import Header from '../Header/Header';
import CartProduct from './CartProduct';

export default function ShoppingCart({ products }) {

  mainPicture = mainPicture.replace(/'/g, ''); // kolkhoz

  // Для вёрстки используется всё тот же временный массив продуктов

  return (
    <>
    <Header/>
    <main className='cart'>
    <h1 className='cart__header'>Корзина</h1>
    <section className='cart__products'>
      <ul className='cart__product-list'>
        {products.map((product) => (
          <CartProduct
          name={product.title} 
          mainPicture={product.mainPicture}
          price={product.price}
          onCardClick={onCardClick}
          id={product.productId}
          />
        ))}
      </ul>
    </section>
    <div className='cart__summary'>
      <form className='product__form'>
        
      </form>
    </div>
    </main>
    </>
  )
}
