import loading from '../../images/loading.svg';
import './FileUploader.css';

import React, { useState, useCallback, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';

function FileUploader({ onAttach, onColorChoose }) {

  const [files, setFiles] = useState([]);
  const [suggestedColors, setSuggestedColors] = useState([]);
  const [imagesLoading, setImagesLoading] = useState(false);

  // const onDrop = useCallback(async acceptedFiles => {
  //   const formData = new FormData();
  //   acceptedFiles.forEach(file => {
  //     formData.append('image', file);
  //   });

  //   try {
  //     setImagesLoading(true);
  //     const uploadedFiles = [];
      
  //     for (const file of acceptedFiles) {
  //       const formData = new FormData();
  //       formData.append('image', file);
  
  //       const response = await fetch('http://backend.dolina.shop:5000/admin/upload', {
  //         method: 'POST',
  //         body: formData
  //       });
  
  //       if (!response.ok) {
  //         throw new Error('Failed to upload file');
  //       }
  
  //       const data = await response.json(); 
  //       uploadedFiles.push({ preview: data.file, name: data.file, colors: data.colors });
  //     }
  
  //     setImagesLoading(false);
  //     setFiles(prevFiles => [...prevFiles, ...uploadedFiles]);
  //   } catch (error) {
  //     console.error('Error uploading files:', error);
  //   }
    

    
  // }, []);

  const onDrop = useCallback(acceptedFiles => {
  return Promise.all(acceptedFiles.map(file => {
    const formData = new FormData();
    formData.append('image', file);

    return fetch('http://backend.dolina.shop:5000/admin/upload', {
      method: 'POST',
      body: formData
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Failed to upload file');
      }
      return response.json();
    })
    .then(data => ({
      preview: data.file,
      name: data.file,
      colors: data.colors
    }))
    .catch(error => {
      console.error('Error uploading file:', error);
      throw error;
    });
  }))
  .then(uploadedFiles => {
    setImagesLoading(false);
    setFiles(prevFiles => [...prevFiles, ...uploadedFiles]);
    onAttach(prevFiles => [...prevFiles, ...uploadedFiles]); // колхоз
    console.log(files)
  })

  .catch(error => {
    console.error('Error uploading files:', error);
  });
}, []);


  // меняем цвета когда меняются файлы, берём у первого
  useEffect(() => {
    if (files[0]) setSuggestedColors(files[0].colors);
    // console.log(files)
  }, [files]);


  const removeFile = (name) => {
    setFiles(prevFiles => prevFiles.filter(file => file.name !== name));
  };

  const { getRootProps, getInputProps } = useDropzone({ onDrop, multiple: true });



  const handleDragStart = (e, index) => {
    e.dataTransfer.setData('index', index);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e, newIndex) => {
    e.preventDefault();
    const oldIndex = e.dataTransfer.getData('index');
    const newFiles = [...files];
    const movedItem = newFiles.splice(oldIndex, 1)[0];
    newFiles.splice(newIndex, 0, movedItem);
    setFiles(newFiles);
  };


  const thumbs = files.map((file, index) => (
    <div
      className='thumbnail-item'
      key={file.preview}
      draggable
      onDragStart={(e) => handleDragStart(e, index)}
      onDragOver={handleDragOver}
      onDrop={(e) => handleDrop(e, index)}
    >
      <div>
        <img src={"https://dolina.shop/photos/temp/" + file.preview} alt={file.name} style={{ width: '90px', margin: '0 8px'}} />
      </div>
      
      <button className='thumbnail-button' onClick={() => removeFile(file.name)}></button>
    </div>
  ));


  const colors = suggestedColors.map((color, index) => (
      <div style={{backgroundColor: color}} data-id={color} key={index}></div>
  ));


  return (
    <div>
      <div {...getRootProps({ className: 'dropzone' })}>
        <input {...getInputProps()} />
        <p>Сюда можно перетащить фото и видео</p>
      </div>
        <aside className='thumbnail-div' style={{ marginTop: '20px' }}>
        {imagesLoading && (
          <div className='loading'>
            <img src={loading} /> грузим, грузим
          </div>
        )}
        {thumbs}
      </aside>
      
    <div className='suggestedColors'>
      <h3>Цвет кнопки товара</h3>
      {colors}
    </div>
    </div>
  );
};

export default FileUploader;
