import './Tags.css';
import { useEffect, useState } from 'react';

export default function Tags({ tags, categories, onTagClick, activeCategory }) {

    const [selectedTags, setSelectedTags] = useState([]);
    const [filteredTags, setFilteredTags] = useState([]);

    const handleTagToggle = (tag) => {
      let activeTags = [];
      if (selectedTags.includes(tag)) {
        activeTags = selectedTags.filter(selectedTag => selectedTag !== tag)
        setSelectedTags(activeTags);
      } else {
        activeTags = [...selectedTags, tag];
        setSelectedTags([...selectedTags, tag]);
      }
      activeTags = activeTags.map(item => item.termId)
      onTagClick(activeTags);
    };

    useEffect(() => {
        if (tags) {
            setFilteredTags(tags.filter(tag => tag.count != 0));
            if (activeCategory) {
                const activeCategoryContent = categories.filter(item => item.termId == activeCategory)[0];
                setFilteredTags(tags.filter(tag => activeCategoryContent.category_tags.includes(tag.termId)));
            }
        }
    }, [tags, activeCategory]);

  
    return (
        <div className="tags">
          {tags && filteredTags.map(tag => (
            <label key={tag.termId}>
              <input
                type="checkbox"
                value={tag.termId}
                checked={selectedTags.some(selectedTag => selectedTag.termId === tag.termId)}
                onChange={() => handleTagToggle(tag)}
              />
              {tag.name}
            </label>
          ))}
        </div>
      );


};
