import './Tags.css';
import { useEffect, useState } from 'react';
// import SearchForm from '../SearchForm/SearchForm';
import caret from '../../images/caret-down_mini.svg';
import sorting from '../../images/sorting_2.svg';
// import { NavLink } from 'react-router-dom';
import { useMediaQuery } from 'react-responsive';
import { NavLink, useLocation } from 'react-router-dom';

export default function Tags({ terms, tags, categories, onTagClick, onColorClick, onSizeClick, activeCategory, activeTags, sortBy, setSortBy, sortOrder, setSortOrder, productFilters, setProductFilters, onCatClick }) {
  const [selectedTags, setSelectedTags] = useState([]);
  const [filteredTags, setFilteredTags] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [selectedSizes, setSelectedSizes] = useState([]);
  const [openDropdownIndex, setOpenDropdownIndex] = useState(null);
  const [stonesInputValue, setStonesInputValue] = useState('');
  const [selectedCategoryName, setSelectedCategoryName] = useState('');
  const isBigScreen = useMediaQuery({ minWidth: 771 }); // надо бы перенести в константы
  const isSmallScreen = useMediaQuery({ maxWidth: 770 });
  const location = useLocation();
  // console.log(tags);

  const handleTagToggle = (tag) => {
    let activeTagsTemporary = [];
    if (selectedTags.includes(tag)) {
      activeTagsTemporary = selectedTags.filter(selectedTag => selectedTag !== tag)
    } else {
      activeTagsTemporary = [...selectedTags, tag];
    }
    setSelectedTags(activeTagsTemporary);
    onTagClick(activeTagsTemporary);
  };

  // console.log(terms)

  function handleCategoryClick(event) {
    event.preventDefault();
    const selectedCategoryId = event.target.dataset.id;
    onCatClick(selectedCategoryId);

    setOpenDropdownIndex(null)
  }


  const handleColorToggle = (color) => {
    let activeColorsTemporary = [];
    if (selectedColors.includes(color)) {
      activeColorsTemporary = selectedColors.filter(selectedColor => selectedColor !== color)
    } else {
      activeColorsTemporary = [...selectedColors, color];
    }
    setSelectedColors(activeColorsTemporary);
    onColorClick(activeColorsTemporary);
  };

    const handleSizeToggle = (size) => {
    let activeSizesTemporary = [];
    if (selectedSizes.includes(size)) {
      activeSizesTemporary = selectedSizes.filter(selectedSize => selectedSize !== size)
    } else {
      activeSizesTemporary = [...selectedSizes, size];
    }
    setSelectedSizes(activeSizesTemporary);
    onSizeClick(activeSizesTemporary);
  };


  const toggleDropdown = (index, event) => {
    event.stopPropagation(); // Останавливаем распространение события клика
    setOpenDropdownIndex(prevIndex => (prevIndex === index ? null : index)); // Открываем или закрываем текущий выпадающий список
  };


  useEffect(() => {
    if (tags) {
      setFilteredTags(tags.filter(tag => tag.count !== 0));
      if (productFilters.category > 0) {
        const activeCategoryTags = JSON.parse(categories.filter(item => item.termId == productFilters.category)[0].category_tags);
        setFilteredTags(tags.filter(tag => activeCategoryTags.includes(Number(tag.termId))));
      }
      if (stonesInputValue.length > 0) {
        setFilteredTags(filteredTags.filter(tag => tag.name.toLowerCase().includes(stonesInputValue.toLowerCase())))
      } else {
      }
    }
  }, [tags, productFilters, stonesInputValue]);

  useEffect(() => {
    if (productFilters.tags.length === 0) setSelectedTags([]);
    if (productFilters.colors.length === 0) setSelectedColors([]);
  }, [productFilters]);

  useEffect(() => {
    setSelectedColors(productFilters.colors);
  }, []);

  useEffect(() => {
    setSelectedTags(productFilters.tags);
  }, []);

  useEffect(() => {
    setSelectedCategoryName('');
  }, [productFilters]);

  // useEffect(() => {
  // }, [filteredTags]);


  useEffect(() => {
    // Function to handle click events
    const handleClick = (event) => {
      const target = event.target;
      const parent = target.parentElement;

      if (!target.classList.contains('dropdown_selector') && !(parent && parent.classList.contains('dropdown_selector'))) {
        setOpenDropdownIndex(null); // Закрываем выпадающий список
      }
    };

    // Add event listener to the document
    document.addEventListener('click', handleClick);

    // Cleanup event listener on component unmount
    return () => {
      document.removeEventListener('click', handleClick);
    };
  }, []);

  // useEffect(() => {
  //   // Извлекаем categoryId из URL
  //   const query = new URLSearchParams(location.search);
  //   const categoryId = query.get('category');

  //   if (categoryId) {
  //     const category = terms.categories.find(cat => cat.termId === parseInt(categoryId));
  //     if (category) {
  //       setSelectedCategoryName(category.name);
  //     }
  //   }
  // }, [location.search, terms.categories]);

  useEffect(() => {
    // Извлекаем categoryId из URL
    const query = new URLSearchParams(location.search);
    const categoryId = query.get('category');

    if (categoryId && terms.categories) {
      const category = terms.categories.find(cat => cat.termId === parseInt(categoryId));
      if (category) {
        setSelectedCategoryName(category.name);
      }
    }
  }, [location.search, terms.categories]);

  function handleStonesInput(input) {
    const inputValue = input.target.value;
    setStonesInputValue(inputValue);
  }

  const handleSortChange = (value) => {
    setProductFilters(prevFilters => ({
      ...prevFilters,
      sortBy: value,
      offset: 0
    }));
  };

  const handleMasterpieceChange = (event) => {
    setProductFilters(prevFilters => ({
      ...prevFilters,
      masterpiece: event.target.checked ? 1 : ''
    }));
  };

  const handleIridescentChange = (event) => {
    setProductFilters(prevFilters => ({
      ...prevFilters,
      iridescent: event.target.checked ? 1 : ''
    }));
  };


  const sortOptions = [
    { value: 'default', label: 'По умолчанию' },
    { value: 'price-asc', label: 'По возрастанию цены' },
    { value: 'price-desc', label: 'По убыванию цены' },
    { value: 'date-desc', label: 'По новизне' },
    { value: 'discount-desc', label: 'По размеру скидки' }
  ];

  return (
    <section className='tag-section'>
      {isBigScreen &&
        <>
          <div className='sorting-area'>
              <div className='sorting-dropdown'>
              {/* <button type='button' className='sorting-dropdown__button pointer' onClick={(event) => toggleDropdown(0, event)}>Сортировка  <img src={sorting} alt="caret" style={{ marginLeft: '5px' }} /></button> */}
              <button type='button' className='sorting__button pointer' onClick={(event) => toggleDropdown(0, event)}><img src={sorting} alt="sort" /></button>

              <div className={`sorting-content overflow-hidden ${openDropdownIndex === 0 ? 'sorting-content_visible' : ''}`}>
                <div className="tags__dropdown sorting_by">
                  {sortOptions.map(option => (
                    <button
                      key={option.value}
                      onClick={() => handleSortChange(option.value)}
                      style={{ fontWeight: productFilters.sortBy === option.value ? 'bold' : 'normal' }}
                    >
                      {option.label}
                    </button>
                  ))}
                </div>
              </div>
            </div>


             <div className='sorting-dropdown'>
              <button type='button' className='sorting-dropdown__button pointer' onClick={(event) => toggleDropdown(9, event)}>{selectedSizes.length > 0 ?`Размеры (${selectedSizes.length}) ` : 'Размеры'} <img src={caret} alt="caret" style={{ marginLeft: '5px' }} /></button>

              <div className={`sorting-content dropdown_selector ${openDropdownIndex === 9 ? 'sorting-content_visible' : ''}`}>
                <div className="tags__dropdown dropdown_selector">
                  {/* <SearchForm /> */}
                  
                  {terms && terms.sizes && terms.sizes.map(size => (
                    <label key={size.termId} className='tag-label'>
                      <input
                        type="checkbox"
                        value={size.termId}
                        className='tag-button_dropdown'
                        checked={selectedSizes && selectedSizes.some(selectedSize => selectedSize === size.termId) ? 'checked' : ''}
                        onChange={(event) => {
                          event.stopPropagation(); // Остановка распространения события клика
                          handleSizeToggle(size.termId);
                        }}
                      />
                      <div className='tag-pseudo-checkbox pointer' onClick={(event) => event.stopPropagation()}>{}<span className='color_ball'></span></div>
                    </label>
                  ))}
                  {/* <button type='button' className='sorting-button'>Готово</button> */}
                </div>
                <button type='button' className='sorting-button' onClick={() => setOpenDropdownIndex(null)}>Готово</button>
              </div>
            </div>



            
            <div className='sorting-dropdown'>
              <button type='button' className='sorting-dropdown__button pointer' onClick={(event) => toggleDropdown(5, event)}>Все камни  <img src={caret} alt="caret" style={{ marginLeft: '5px' }} /></button>

              <div className={`sorting-content dropdown_selector ${openDropdownIndex === 5 ? 'sorting-content_visible' : ''}`}>
                <div className="tags__dropdown dropdown_selector">
                  <div className='dropdown_selector'>
                    <input className='stone-search' type='text' onChange={handleStonesInput} placeholder='начните вводить название'></input>
                  </div>
                  {tags && filteredTags.map(tag => (
                    <label key={tag.termId} className='tag-label'>
                      <input
                        type="checkbox"
                        value={tag.termId}
                        className='tag-button_dropdown'
                        checked={selectedTags && selectedTags.some(selectedTag => selectedTag === tag.termId) ? 'checked' : ''}
                        onChange={(event) => {
                          event.stopPropagation(); // Остановка распространения события клика
                          handleTagToggle(tag.termId);
                        }}
                      />
                      <div className='tag-pseudo-checkbox pointer' onClick={(event) => event.stopPropagation()}>{tag.name}{!productFilters.category && (<span className="tag-count">{tag.count}</span>)} </div>
                    </label>
                  ))}
                  {/* <button type='button' className='sorting-button'>Готово</button> */}
                </div>
                <button type='button' className='sorting-button' onClick={() => setOpenDropdownIndex(null)}>Готово</button>
              </div>
            </div>
            <div className='sorting-dropdown'>
              <button type='button' className='sorting-dropdown__button pointer' onClick={(event) => toggleDropdown(9, event)}>{selectedColors.length > 0 ?`Цвета (${selectedColors.length}) ` : 'Цвета'} <img src={caret} alt="caret" style={{ marginLeft: '5px' }} /></button>

              <div className={`sorting-content dropdown_selector ${openDropdownIndex === 9 ? 'sorting-content_visible' : ''}`}>
                <div className="tags__dropdown dropdown_selector">
                  {/* <SearchForm /> */}
                  
                  {terms && terms.colors && terms.colors.map(color => (
                    <label key={color.termId} className='tag-label'>
                      <input
                        type="checkbox"
                        value={color.termId}
                        className='tag-button_dropdown'
                        checked={selectedColors && selectedColors.some(selectedColor => selectedColor === color.termId) ? 'checked' : ''}
                        onChange={(event) => {
                          event.stopPropagation(); // Остановка распространения события клика
                          handleColorToggle(color.termId);
                        }}
                      />
                      <div className='tag-pseudo-checkbox pointer' onClick={(event) => event.stopPropagation()}>{color.name}<span className='color_ball' style={{ background: color.extra }}></span></div>
                    </label>
                  ))}
                  {/* <button type='button' className='sorting-button'>Готово</button> */}
                </div>
                <button type='button' className='sorting-button' onClick={() => setOpenDropdownIndex(null)}>Готово</button>
              </div>
            </div>
            <label className='tag-label'>
              <input
                className='tag-button'
                type="checkbox"
                checked={productFilters.masterpiece}
                onChange={handleMasterpieceChange}
              />
              <div className='sorting-dropdown__button pointer'>Авторские украшения</div>
            </label>

            <label className='tag-label'>
              <input
                className='tag-button'
                type="checkbox"
                checked={productFilters.iridescent}
                onChange={handleIridescentChange}
              />
              <div className='sorting-dropdown__button pointer'>Оптический эффект</div>
            </label>
          </div>

          <div className='tag-area'>
            {tags && filteredTags.slice(0, 15).map(tag => (
              <label key={tag.termId} className='tag-label'>
                <input
                  type="checkbox"
                  value={tag.termId}
                  className='tag-button'
                  checked={selectedTags && selectedTags.some(selectedTag => selectedTag === tag.termId) ? 'checked' : ''}
                  onChange={() => handleTagToggle(tag.termId)}
                />
                <div className='tag-pseudo-button pointer'>{tag.name}{!productFilters.category && (<span className="tag-count">{tag.count}</span>)} </div>

              </label>
            ))}

          </div>
        </>
      }

      {isSmallScreen &&
        <>
          <div className='sorting-area'>
            <div className='tags_flex'>
            <div className='sorting-dropdown'>
              {/* <button type='button' className='sorting-dropdown__button pointer' onClick={(event) => toggleDropdown(0, event)}>Сортировка  <img src={sorting} alt="caret" style={{ marginLeft: '5px' }} /></button> */}
              <button type='button' className='sorting__button pointer' onClick={(event) => toggleDropdown(0, event)}><img src={sorting} alt="sort" /></button>

              <div className={`sorting-content overflow-hidden ${openDropdownIndex === 0 ? 'sorting-content_visible' : ''}`}>
                <div className="tags__dropdown sorting_by">
                  {sortOptions.map(option => (
                    <button
                      key={option.value}
                      onClick={() => handleSortChange(option.value)}
                      style={{ fontWeight: productFilters.sortBy === option.value ? 'bold' : 'normal' }}
                    >
                      {option.label}
                    </button>
                  ))}
                </div>
              </div>
            </div>
            <div className='sorting-dropdown'>
              <button type='button' className='sorting-dropdown__button pointer' onClick={(event) => toggleDropdown(12, event)}>Категории <img src={caret} alt="caret" style={{ marginLeft: '5px' }} /></button>

              <div className={`sorting-content dropdown_selector ${openDropdownIndex === 12 ? 'sorting-content_visible' : ''}`}>
                <div className="tags__dropdown dropdown_selector">
                  {categories && categories.map(category => (
                    <NavLink to='' className='tag-pseudo-checkbox' onClick={handleCategoryClick} key={category.termId} data-id={category.termId} style={{ marginBottom: '5px' }}>{category.name}</NavLink>
                  ))}
                </div>
              </div>
            </div>
            </div>
            
            <div className='tags_flex'>
            <label className='tag-label'>
              <input
                className='tag-button'
                type="checkbox"
                checked={productFilters.masterpiece}
                onChange={handleMasterpieceChange}
              />
              <div className='sorting-dropdown__button pointer'>Авторское украшение</div>
            </label>

            <label className='tag-label'>
              <input
                className='tag-button'
                type="checkbox"
                checked={productFilters.iridescent}
                onChange={handleIridescentChange}
              />
              <div className='sorting-dropdown__button pointer'>Оптический эффект</div>
            </label>

            </div>
          
          </div>

          <div className='tag-area_mobile'>
            <div className='sorting-dropdown'>
              <button type='button' className='tag-pseudo-button pointer' onClick={(event) => toggleDropdown(9, event)}>
                {selectedColors.length > 0 ?`Цвет (${selectedColors.length}) ` : 'Цвет'}<img src={caret} alt="caret" style={{ marginLeft: '5px' }} /></button>

              <div className={`sorting-content dropdown_selector ${openDropdownIndex === 9 ? 'sorting-content_visible' : ''}`}>
                <div className="tags__dropdown dropdown_selector">
                  {terms && terms.colors && terms.colors.map(color => (
                    <label key={color.termId} className='tag-label'>
                      <input
                        type="checkbox"
                        value={color.termId}
                        className='tag-button_dropdown'
                        checked={selectedColors && selectedColors.some(selectedColor => selectedColor === color.termId) ? 'checked' : ''}
                        onChange={(event) => {
                          event.stopPropagation(); // Остановка распространения события клика
                          handleColorToggle(color.termId);
                        }}
                      />
                      <div className='tag-pseudo-checkbox pointer' onClick={(event) => event.stopPropagation()}>{color.name}<span className='color_ball' style={{ background: color.extra }}></span></div>
                    </label>
                  ))}
                </div>
                <button type='button' className='sorting-button' onClick={() => setOpenDropdownIndex(null)}>Готово</button>
              </div>
            </div>

            {tags && filteredTags.map(tag => (
              <label key={tag.termId} className='tag-label'>
                <input
                  type="checkbox"
                  value={tag.termId}
                  className='tag-button'
                  checked={selectedTags && selectedTags.some(selectedTag => selectedTag === tag.termId) ? 'checked' : ''}
                  onChange={() => handleTagToggle(tag.termId)}
                />
                <div className='tag-pseudo-button pointer'>{tag.name}{!productFilters.category && (<span className="tag-count">{tag.count}</span>)} </div>

              </label>
            ))}

          </div>
        </>
      }





      <div className='chosen-area'>
        {selectedCategoryName && <h1 className='category-header'>&bull; {selectedCategoryName.toUpperCase()} &bull;</h1>}
      </div>

    </section>
  );
};
