import MobileNavBar from "../MobileNavBar/MobileNavBar";
import './Pages.css';
import { useEffect } from "react";
// import sparkle from '../../images/sparkle-success.svg';
// import bullet from '../../images/diamond-fill.svg';
// import heartBullet from '../../images/heart-fill_24.svg';
// import uni from '../../images/uni2.png';
// import logo from '../../images/logo_pic.png';
// import kvartz from '../../images/kvartzDolinaShop.jpg';
// import mount2 from '../../images/urals2.jpg';


export default function DeliveryPage({ onBurgerClick, onBurgerClose, isBurgerOpen, orders, cart, onCatClick, isLoggedIn }) {

  useEffect(() => {
    document.title = 'Доставка и оплата — Долина самоцветов';
  }, []);

  return (
    <>
      <main className="delivery">
        <h1 className="delivery__main-header">Доставка и оплата</h1>
        <section className="delivery__section">
          <h2 className="delivery__header">Как заказать?</h2>
          <p className="delivery__main-text">Для того, чтобы сделать заказ в Долине самоцветов, необходимо добавить понравившиеся товары в корзину и перейти к оформлению.
            На стадии оформления заказа вам будут предложены несколько вариантов доставки и оплаты.</p>
          <p className="delivery__main-text">Если по какой-то причине оформить заказ на сайте не получается, вы можете оформить его по телефону 8 800 200 1705 с 8.00 до 17.00 по Московскому времени.</p>
          <p className="delivery__main-text">Для того, чтобы сделать заказ в Долине самоцветов, необходимо добавить понравившиеся товары в корзину и перейти к оформлению.
            На стадии оформления заказа вам будут предложены несколько вариантов доставки и оплаты.</p>
          <p className="delivery__main-text">При оформлении заказа по телефону вам понадобятся артикулы выбранных товаров: их можно найти в карточке товара или в корзине. </p>
          <p className="delivery__main-text">Мы доставляем заказы по всей России. Упаковываем и отправляем ваш заказ в течение 1-3 рабочих дней. </p>
        </section>
        <section className="delivery__section">
        <h2 className="delivery__header">Доставка</h2>
        <div>таблица</div>
        <h3 className="delivery__subheader">Скидки на доставку</h3>
        <p className="delivery__main-text">Мы всегда стараемся сделать опыт покупок в нашем магазине приятнее, поэтому у нас есть скидки на доставку: при сумме заказа от 3000 руб доставка будет дешевле на 30%, а от 5000 руб доставка за наш счет! Скидки автоматически активируются на этапе заказа. </p>
        </section>
        <section className="delivery__section">
        <h2 className="delivery__header">Оплата</h2>
        <p className="delivery__main-text">Оплата на нашем сайте производится через Т-Банк. Вы можете оплатить заказ банковской картой, через СБП или ваше банковское приложение. </p>
          <p className="delivery__main-text">Также вы можете оплатить товар при получении после осмотра и примерки, эта опция будет доступна при выборе доставки сервисами СДЭК и Boxberry. </p>
          <div className="delivery__notion">
          <p className="delivery__main-text">Важно: при оплате на сайте у нас действует скидка 3% на весь заказ. </p>
          </div>
        </section>


      </main>
      <MobileNavBar
        onBurger={onBurgerClick}
        isBurgerOpen={isBurgerOpen}
        onBurgerClose={onBurgerClose}
        cart={cart}
        orders={orders}
        onCatClick={onCatClick}
        isLoggedIn={isLoggedIn}
      />
    </>
  )
}
