import './Order.css';
import logo from '../../images/варик6.png';
import SearchForm from '../SearchForm/SearchForm';
import { Link, NavLink, useLocation, useParams } from 'react-router-dom';
import { useMediaQuery } from 'react-responsive';
import adminApi from '../../utils/adminApi';
import AdminHeader from './AdminHeader';
import { useState, useEffect } from "react";
import phoneIcon from '../../images/phone_2.svg';
import phoneIconThin from '../../images/phone-thin.svg';
import phoneIconFill from '../../images/phone-fill.svg';
import rubleIcon from '../../images/currency-rub.svg';
import checkmarkIconGreen from '../../images/check-circle_green.svg';
import hourglassIcon from '../../images/hourglass-high-fill.svg';
import hourglassIconRed from '../../images/hourglass-high-fill_red.svg';
import sparkleIcon from '../../images/sparkle-fill.svg';
import checkmarkIcon from '../../images/check-circle.svg';


export default function Order({ terms, onCatClick, activeCategory }) {


    const { orderId } = useParams();

    const [order, setOrder] = useState([]);
    const [tracking, setTracking] = useState(order.tracking);
    const [editingTracking, setEditingTracking] = useState(false);
    
    useEffect(() => {
        adminApi.getOrder(orderId)
            .then((data) => {
                setOrder(data[0]);
                setTracking(data[0].tracking)
                console.log(data[0])
            }).catch((err) => {
                console.log(err);
            })

    }, []);

    function setStatus(orderId, status) {

        adminApi.setOrderStatus(orderId, status)
            .then(response => {
                setOrder(order => ({
                    ...order,
                    orderStatus: status
                }));
            })
    }


    function saveTracking() {

        let orderId = order.orderId;

        adminApi.setOrderTracking(orderId, tracking)
            .then(response => {
                setEditingTracking(false);
                setOrder(order => ({
                    ...order,
                    tracking: tracking
                }));
            })
    }


    const getStatusName = (index) => {
        switch (index) {
            case 0:
                return 'Новый';
            case 1:
                return 'Собираем заказ';
            case 2:
                return 'В пути';
            case 3:
                return 'Получен';
            case -1:
                return 'Отменён';
            default:
                return '';
        }
    };


    const getDeliveryTypeName = (index) => {
        switch (index) {
            case 1:
                return 'Самовывоз СДЭК';
            case 2:
                return 'Курьерская доставка СДЭК';
            case 3:
                return 'Почта';
            default:
                return '';
        }
    };

    function handleTrackingInput(event) {
        setTracking(event.target.value)
        setEditingTracking(true)
    }

    function calculateDiscount(product) {
        let price = product.price;
        let salePrice = product.salePrice;

        if (product.salePrice > 0) {
            salePrice = Math.floor((product.salePrice - (product.salePrice / 100 * order.discount)) / 10) * 10;
        } {
            price = Math.floor((product.price - (product.price / 100 * order.discount)) / 10) * 10;
        }

        return (
            <div className='orderPrice'>
                {salePrice > 0 ?
              <div className='sale-container'>
                <span className='card__price card__price_crossed'>{price} &#8381;</span>
                <span className='card__sale-price'>{salePrice} &#8381;</span>
              </div>
              :
              <span className='card__price'>{price} &#8381;</span>}
              {false && order.discount > 0 && <span>-{price - product.price} &#8381; за предоплату</span>}
            </div>
        );
    }
    
    function cancelOrder() {
        if (window.confirm('Точно отменить?')) {
            adminApi.cancelOrder(order.orderId, order.orderedProducts)
                .then( response => {
                  setOrder(order => ({
                    ...order,
                    orderStatus: -1
                  }))
                }
                );
        }
    }

    return (
        <>
          <div className='admin_page order_page'>
            <AdminHeader />
            <div className="admin_order">
            <a className='back_to' href='/administration/orderlist'>← вернуться к списку заказов</a>
              <div className='grid_order'>
                <div className="grid_item">
                  <span className='payment'>
                    {order.paid ? (<img src={checkmarkIconGreen} />) : (order.paymentType === 1 ? (<img src={hourglassIconRed} />) : (<img src={hourglassIcon} />))}
                    <span className='price_and_payment'>
                      <b style={{ color: order.paid ? 'green' : (order.paymentType === 1 ? 'red' : '') }}>{order.totalPrice} ₽</b>
                      <span>{order.paymentType ? 'Предоплата' : 'Наложенный платёж'}</span>
                    </span>
                  </span>
                </div>
                <div className="grid_item">
                  <span>Заказ: <b>№{order.orderId}</b> | {(() => {
                      const date = new Date(order.createdAt * 1000);
                      return date.toLocaleString('ru', {
                        year: 'numeric',
                        month: 'numeric',
                        day: 'numeric',
                        hour: 'numeric',
                        minute: 'numeric',
                      });
                    })()}</span>
                </div>
                <div className="grid_item">
                  <span>От: <b>{order.fullName}</b></span>
                </div>
                <div className="grid_item">
                  <span>Телефон: <b>{order.phone}</b></span>
                </div>
                <div className="grid_item">
                  <span className='callback'>
                    Звонок: {order.callbackNeeded ? <b>нужен</b> : <b>не нужен</b>}
                  </span>
                </div>
                <div className="grid_item">
                  <span>Адрес доставки: {order.postalCode == 0 ?? <span>{order.postalCode} </span>}<b>{order.city}</b>, {order.deliveryAddress}</span>
                </div>
                <div className="grid_item">
                  <span>Тип доставки: <b>{getDeliveryTypeName(order.deliveryType)}</b></span>
                </div>
                {order.deliveryType == 1 ?? <div className="grid_item">
                  <span>Офис СДЭК: {order.cdekOffice}</span>
                </div>}
                <div className="grid_item">
                  <span>Комментарий: {order.comment ? <b>{order.comment}</b> : <b>-</b>}</span>
                </div>
                {/* <div className="grid_item">
                  <span>Скидка: {order.discount}%</span>
                </div> */}
                <hr />
                <div className="grid_item">
                  <span className='status'>
                    Статус заказа: 
                    {/* <b>
                      {order.orderStatus === 0 && <span className='sparkled'>Новый <img src={sparkleIcon} /></span>}
                      {order.orderStatus === 1 && 'Собираем заказ'}
                      {order.orderStatus === 2 && 'В пути'}
                      {order.orderStatus === 3 && 'Получен'}
                      {order.orderStatus === -1 && 'Отменён'}
                    </b> */}
                    <div className='balls'>
                      {order.orderStatus !== -1 && [...Array(4)].map((_, index) => (
                        <div
                          key={index}
                          className={`ball ${index <= order.orderStatus ? 'ball_filled' : ''}`}
                          style={{ cursor: index === order.orderStatus ? 'default' : 'pointer', backgroundColor: order.orderStatus === 0 && index === 0 && '#570d88' }}
                          onClick={() => index !== order.orderStatus && setStatus(order.orderId, index)}
                          title={getStatusName(index)}
                        >{index == order.orderStatus && <img src={checkmarkIcon} />}{getStatusName(index)}</div>
                      ))}
                      {order.orderStatus === -1 && 'Отменён'}
                    </div>
                  </span>
                </div>
                <div className="grid_item">
                  <span className='status'>
                    Трекинг номер: {!editingTracking &&
                    <span className='tracking_number' onClick={() => setEditingTracking(true)}>{tracking ?? 'Добавить'}</span>
                    }
                    {editingTracking && <>
                    <input type='number' value={tracking} onChange={handleTrackingInput}/>
                    <div className='cancelTracking' onClick={() => setEditingTracking(false)}>
                      Отменить
                    </div>
                    <div className='saveTracking' onClick={saveTracking}>
                      Сохранить
                    </div></>
                    }
                  </span>
                </div>
                {order.orderStatus != -1 && <div className="grid_item">
                  <span className='cancelOrder' onClick={cancelOrder}>
                    Отменить заказ
                  </span>
                </div>
                }
              </div>
              <div className='grid_ordered_products'>
              {order.orderedProducts && order.orderedProducts.map((product) => (
                <div key={product.productId} className='grid_ordered_product'><a href={'/product/' + product.slug + '-' + product.productId} target='_blank'>
                  <img className='order__pic' title="Открыть страницу товара" alt={product.title} src={`https://dolina.shop/photos/${product.path.slice(0, -4) + `_preview.jpg`}`}></img>
                    </a>
                  {product.title}<br />
                  {calculateDiscount(product)}
                </div>
              ))}
              </div>
            </div>
          </div>
        </>
      );
      
}
