import './Card.css';
// import { useEffect, useState } from 'react';

export default function Card({ name, mainPicture, price, onCardClick, id, pictures }) {

  mainPicture = mainPicture.replace(/'/g, ''); // kolkhoz
  pictures = JSON.parse(pictures); // это должно быть в api 

  function handleClick() {
    onCardClick(id)
  }

  function onHover() {

  }

  return (
    <li className='card' onClick={handleClick}>
      <a href='#'>
        <div className='card__image-container'>
          <div className='card__previews'>
            {pictures.map((pic) => (
              <div className='card__preview-container' onMouseOver={() => mainPicture = {pic}}>
                {/* <div className='card__preview-mark'></div> */}
                <img className='card__preview' src={pic}></img>
              
              </div>
            ))}
          </div>
          <button type='button' className='card__save-button'></button>
          <div className='card__image-overlay'></div>
          <img className='card__image' src={mainPicture}/>
        </div>
      </a>
      <div className='card__description-area'>
        <span className='card__price'>{price} &#8381;</span>
        <h2 className='card__header'>{name}</h2>
      </div>
    </li>
  )
}
