import './Form.css';
import { Link, useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';


export default function Form ({ title, name, children, buttonText, onSubmit, submitDisabled, terms, onCatClick, category, search, setSearch }) {

  const location = useLocation();
  const navigate = useNavigate();

  const goBack = () => {
    navigate(-1);
  };

  const signupLink = <span className='form__link-caption'>–£–∂–µ –∑–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω—ã? <Link className='form__link hover_type_normal' to="/signin">–í–æ–π—Ç–∏</Link></span>;
  const signinLink = <span className='form__link-caption'>–ï—â—ë –Ω–µ –∑–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω—ã? <Link className='form__link hover_type_normal' to="/signup">–ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å</Link></span>
  return (
    <>
      <body className='form-page'>
        <div className="form">
          <section className='form__container'>
            <form name={`${name}-form`} onSubmit={onSubmit}>
              <fieldset className="form__fieldset">
                <h1 className="form__title">{title}</h1>
                {location.pathname !== '/signin' ? 
                <div className='form__notion'>
                  <p className='form__notion-text'>–¢–µ–ø–µ—Ä—å —É –Ω–∞—Å –µ—Å—Ç—å —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è! 
                  –û–Ω–∞ –∑–∞–π–º—ë—Ç –≤—Å–µ–≥–æ –Ω–µ—Å–∫–æ–ª—å–∫–æ —Å–µ–∫—É–Ω–¥, –∑–∞—Ç–æ —Ç–∞–∫ –≤–∞—à–∏ –ø–µ—Ä—Å–æ–Ω–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –±—É–¥—É—Ç –≤ –±–æ–ª—å—à–µ–π –±–µ–∑–æ–ø–∞—Å–Ω–æ—Å—Ç–∏, –∞ –µ—â—ë –≤—ã —Å–º–æ–∂–µ—Ç–µ —Å–ª–µ–¥–∏—Ç—å –∑–∞ —Å–≤–æ–∏–º –∑–∞–∫–∞–∑–æ–º –∏ —Å–æ—Ö—Ä–∞–Ω—è—Ç—å —Ç–æ–≤–∞—Ä—ã –≤ –∏–∑–±—Ä–∞–Ω–Ω–æ–º!</p><p style={{textAlign: 'center', margin: '5px 0'}}>üòç</p>
                </div>
                 : null}
                {children}
                <button type="submit" 
                name="submit" 
                className="form__submit-button hover_type_normal" 
                disabled={submitDisabled}
                >{buttonText || '–í–æ–π—Ç–∏'}</button>
              </fieldset>
            </form>
          </section>
        <div className='form__link-toggle'>
          {location.pathname !== '/signin' ? 
            signupLink : 
            signinLink}
        </div>
        <div className='form__go-back' onClick={goBack}>–ù–∞–∑–∞–¥</div>
      </div>

    </body>
  </>
  )
}

