import Header from "../Header/Header";
import MobileNavBar from "../MobileNavBar/MobileNavBar";
import './ContactPage.css';
import { useEffect, useState, useContext } from "react";
import Reviews from "../Reviews/Reviews";
import { CurrentUserContext } from "../../contexts/CurrentUserContext";

export default function ContactPage({ reviews, terms, onCatClick, category, onBurgerClick, onBurgerClose, isBurgerOpen, search, setSearch, orders, cart }) {
  const currentUser = useContext(CurrentUserContext);
  console.log(currentUser.name)
  const [title, setTitle] = useState('');
  const [text, setText] = useState('');
  const [author, setAuthor] = useState();

  function handleTitleChange(e) {
    setTitle(e.target.value)
  }

  useEffect(() => {
    document.title = 'Отзывы - Долина самоцветов';
  }, []);

  return (
    <>
      <main className="reviews">
        <h1 className="wishlist-header">Отзывы</h1>
        <div className="reviews-container">
          <button type="button" className="review__button">Оставить отзыв</button>
          <div className="reviews__form-container reviews__form-container_active">
            <form className="reviews__form">
              <label htmlFor="title" className='form__input-label'>Заголовок</label>
              <input
                className="form__input"
                // placeholder=""
                type="text"
                name="title"
                required
                minLength={2}
                onChange={handleTitleChange}
                value={title || ''}
              ></input>

            </form>
          </div>
          <Reviews
            reviews={reviews}
          />
        </div>
      </main>
      <MobileNavBar
        onBurger={onBurgerClick}
        isBurgerOpen={isBurgerOpen}
        onBurgerClose={onBurgerClose}
        cart={cart}
        onCatClick={onCatClick}
        orders={orders}
      />
    </>
  )
}
