class AdminApi {
    constructor(options) {
      this._url = options.url;
      this._headers = options.headers;
    }
  
    _getResponseData(res) {
      if (!res.ok) {
        return Promise.reject(`Error: ${res.status}`);
      }
      return res.json();
    }
  
    uploadImage(file) {
      const formData = new FormData();
      formData.append('image', file);
  
      return fetch(`${this._url}/admin/upload/image`, {
        method: 'POST',
        headers: this._headers, // Note: Headers should not include 'Content-Type' for FormData
        body: formData
      })
      .then(this._getResponseData);
    }
  
    uploadVideo(file) {
      const formData = new FormData();
      formData.append('video', file);
  
      return fetch(`${this._url}/admin/upload/video`, {
        method: 'POST',
        headers: this._headers, // Note: Headers should not include 'Content-Type' for FormData
        body: formData
      })
      .then(this._getResponseData);
    }
  
    productSearch(text) {  
      return fetch(`${this._url}/products/search?text=${text}`, {
        method: 'GET',
        headers: this._headers
      })
      .then(this._getResponseData);
    }
  
    addProduct(productData) {  
      return fetch(`${this._url}/admin/products`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(productData)
      })
      .then(this._getResponseData);
    }

  }
  

        
const adminApi = new AdminApi({
    url: ' https://backend.dolina.shop',
    headers: {
    //   'Content-Type': 'application/json',
    }
  });
  
export default adminApi;
