class ProductsApi {
  constructor(options) {
    this._url = options.url
    this._headers = options.headers
  }

  _getResponseData(res) {
    if (!res.ok) {
      return Promise.reject(`Ошибка: ${res.status}`);
    }
    return res.json();
  }

  getAllProducts() {
    return fetch(this._url + '/products/all', {
      method: 'GET',
      headers: this._headers
    })
      .then(this._getResponseData);
  }

  getProduct(id) {
    return fetch(this._url + `/products/by_id?productId=${id}`, {
      method: 'GET',
      headers: this._headers
    })
      .then(this._getResponseData);
  }

  getProductsById(products) {
    let savedPoducts = products.join(',');
    return fetch(this._url + `/products/by_id?productId=${savedPoducts}`, {
      method: 'GET',
      headers: this._headers
    })
      .then(this._getResponseData);
  }

  filterProducts(category, tags) {
    tags = tags.join(',');
    return fetch(this._url + `/products/filter?category=${category}&tag=${tags}`, {
      method: 'GET',
      headers: this._headers
    })
    .then(this._getResponseData);
  }

}

        
const productsApi = new ProductsApi({
  url: ' https://backend.dolina.shop',
  headers: {
    'Content-Type': 'application/json',
  }
});

export default productsApi;
