import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import './FileUploader.css'

const FileUploader = () => {
  const [files, setFiles] = useState([]);

  const onDrop = (acceptedFiles) => {
    setFiles([...files, ...acceptedFiles]);
  };

  const onDeleteFile = (index) => {
    const updatedFiles = [...files];
    updatedFiles.splice(index, 1);
    setFiles(updatedFiles);
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;

    const updatedFiles = Array.from(files);
    const [reorderedItem] = updatedFiles.splice(result.source.index, 1);
    updatedFiles.splice(result.destination.index, 0, reorderedItem);

    setFiles(updatedFiles);
  };

  const { getRootProps, getInputProps } = useDropzone({ onDrop });

  return (
    <div className="file-uploader">
      <div {...getRootProps()} className="dropzone">
        <input {...getInputProps()} />
        <p>Drag & drop some files here, or click to select files</p>
      </div>
      <div className="thumbnail-list">
        {files.map((file, index) => (
          <div key={index} className="thumbnail" draggable onDragEnd={onDragEnd}>
            <img src={URL.createObjectURL(file)} alt="thumbnail" />
            <button onClick={() => onDeleteFile(index)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FileUploader;
