import { useState, useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

export default function MobileGallery({ pictures, videos, color, onImageClick }) {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Эффект для сброса текущего слайда при изменении картинок
  useEffect(() => {
    setCurrentSlide(0);
  }, [pictures]);

  const settings = {
    arrows: false,
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    swipeToSlide: true,
    beforeChange: (oldIndex, newIndex) => {
      setCurrentSlide(newIndex);
    },
    afterChange: (current) => {
      setCurrentSlide(current);
    }
  };

  return (
    <div>
      <Slider {...settings} slickGoTo={currentSlide}>
        {pictures.map((picture, index) => (
          <div key={index}>
            <img
              src={`https://dolina.shop/photos/${picture.path}`}
              alt={`Photo ${index}`}
              onClick={onImageClick}
            />
          </div>
        ))}
      </Slider>
      <div style={{ textAlign: 'center', marginTop: '10px' }}>
        <span>
          {currentSlide + 1} / {pictures.length}
        </span>
      </div>
    </div>
  );
}
