sourcesContent:
import eyeIcon from '../../images/eye.svg';
import React, { useState, useEffect } from 'react';
// import Header from '../Header/Header';
import AdminHeader from './AdminHeader';
import './ProductConstructor.css';
import FileUploader from './FileUploader';
import adminApi from '../../utils/adminApi';
import { DEFAULT_COLOR_LIST, FEATURES_LIST, HARDCODED_CATEGORIES } from '../../utils/constants';

import checkSquare from '../../images/check-square.svg';
import fileDashed from '../../images/file-dashed.svg';
import copySimple from '../../images/copy-simple.svg';

function ProductConstructor({ terms }) {

  // file uploader variables
  const [images, setImages] = useState([]);
  const [videos, setVideos] = useState([]);
  const [buttonColor, setButtonColor] = useState('');

  const [productName, setProductName] = useState('');
  const [productDescription, setProductDescription] = useState('');
  const [colorsToSelectFrom, setColorsToSelectFrom] = useState(DEFAULT_COLOR_LIST);
  const [colors, setColors] = useState([]);
  const [length, setLength] = useState('');
  const [weight, setWeight] = useState('');
  const [stoneOrigin, setStoneOrigin] = useState('');
  const [metalType, setMetalType] = useState('');
  const [stoneSize, setStoneSize] = useState('');
  const [production, setProduction] = useState('');
  const [price, setPrice] = useState('');
  const [salePrice, setSalePrice] = useState('');
  const [discount, setDiscount] = useState(0);
  const [articleNumber, setArticleNumber] = useState('');
  const [inStock, setInStock] = useState(1);
  const [status, setStatus] = useState(99);


  const [categoriesSelected, setCategoriesSelected] = useState([]);

  const [stonesSearchResults, setStonesSearchResults] = useState([]);
  const [isStonesSearchResultsShowing, setIsStonesSearchResultsShowing] = useState(false);
  const [stonesSelected, setStonesSelected] = useState([]);
  const [stonesInputValue, setStonesInputValue] = useState('');

  const [sizesSearchResults, setSizesSearchResults] = useState([]);
  const [isSizesSearchResultsShowing, setIsSizesSearchResultsShowing] = useState(false);
  const [sizesSelected, setSizesSelected] = useState([]);
  const [sizeInputValue, setSizeInputValue] = useState('');

  // const [additionalProperties, setAdditionalProperties] = useState([]);
  // const [favorite, setFavorite] = useState(false);
  // const [status, setStatus] = useState('draft');
  // const [radioValue, setRadioValue] = useState('');

  // const [isUnique, setIsUnique] = useState(0);
  // const [isIrid, setIsIrid] = useState(0);
  // const [isMinimalism, setIsMinimalism] = useState(0);
  // const [isMasterpiece, setIsMasterpiece] = useState(0);

  const [features, setFeatures] = useState({
    unique: 0,
    masterpiece: 0,
    iridescent: 0,
    minimalism: 0,
  });

  function handleFeatureChange(featureSlug) {
    let newValue = features[featureSlug] == 1 ? 0 : 1;
    setFeatures({
      ...features,
      [featureSlug]: newValue
    })
  }

  const [upsellsSearchResults, setUpsellsSearchResults] = useState([]);
  const [isUpsellsSearchResultsShowing, setIsUpsellsSearchResultsShowing] = useState(false);
  const [upsellsSelected, setUpsellsSelected] = useState([]);

  // console.log(stoneTags);
  // console.log(attachedFiles)
  // console.log(terms)

  const handleFormSubmit = (e) => {
    e.preventDefault();

    const productData = {};

    productData.title = productName;
    productData.text = productDescription;
    productData.price = price;
    productData.discount = discount;
    productData.salePrice = salePrice;
    productData.length = length;
    productData.weight = weight;
    productData.stoneOrigin = stoneOrigin;
    productData.origin = production;
    productData.stoneSize = stoneSize;
    productData.metalType = metalType;
    productData.iridescent = features.iridescent;
    productData.unique = features.unique;
    productData.masterpiece = features.masterpiece;
    productData.minimalism = features.minimalism;
    productData.SKU = articleNumber;
    productData.published = status;
    productData.inStock = inStock;
    productData.color = buttonColor;

    productData.categories = categoriesSelected;

    productData.tags = [];
    stonesSelected.forEach(stone => {
      productData.tags.push(stone.termId);
    });

    productData.images = [];
    images.forEach(image => {
      productData.images.push(image.file);
    });

    productData.videos = [];
    videos.forEach(video => {
      productData.videos.push(video.file);
    });

    const upsellProductIds = [];
    upsellsSelected.forEach(upsell => {
      upsellProductIds.push(upsell.productId);
    });

    productData.upsells = upsellProductIds.join(',')

    // const response = adminApi.addProduct(productData);
    // console.log(response);
    console.log(productData);

  };


  // CATEGORIES

  function handleCategoryChange(e) {
    const termId = parseInt(e.target.value);
    if (!categoriesSelected.includes(termId)) {
      setCategoriesSelected([...categoriesSelected, termId]);
    } else {
      setCategoriesSelected(categoriesSelected.filter(catId => catId !== termId));
    }
  }


  // STONES

  function handleStonesInput(input) {
    const inputValue = input.target.value;
    setStonesInputValue(inputValue);
    if (inputValue.length > 0) {
      const filteredStones = terms.tags.filter(tag => tag.name.toLowerCase().includes(inputValue.toLowerCase()));
      setStonesSearchResults(filteredStones.slice(0, 24));
      setIsStonesSearchResultsShowing(true);
    } else {
      setStonesSearchResults([]);
      setIsStonesSearchResultsShowing(false);
    }
  }

  function handleStoneSelect(stone) {
    setStonesSelected(prevStones => [...prevStones, stone]);
    setIsStonesSearchResultsShowing(false);
    document.getElementById('stonesInput').value = '';
  }

  function handleStoneRemoval(termId) {
    setStonesSelected(prevStones => prevStones.filter(stone => stone.termId !== termId));
  }

  function closeStoneSearchResults() {
    setIsStonesSearchResultsShowing(false);
    document.getElementById('stonesInput').value = '';
  }



  // SIZES

  function handleSizesInput(input) {
    const inputValue = input.target.value;
    setSizeInputValue(inputValue);
    if (inputValue.length > 0) {
      const filteredSizes = terms.ringSizes.filter(ringSize => ringSize.name.replace(',', '.').includes(inputValue.replace(',', '.')));
      setSizesSearchResults(filteredSizes);
      setIsSizesSearchResultsShowing(true);
    } else {
      setSizesSearchResults([]);
      setIsSizesSearchResultsShowing(false);
    }
  }

  function handleSizeSelect(size) {
    setSizesSelected(prevSizes => [...prevSizes, size]);
    setIsSizesSearchResultsShowing(false);
    document.getElementById('sizesInput').value = '';
  }

  function handleSizeRemoval(termId) {
    setSizesSelected(prevSizes => prevSizes.filter(size => size.termId !== termId));
  }

  function closeSizeSearchResults() {
    setIsSizesSearchResultsShowing(false);
    document.getElementById('sizesInput').value = '';
  }




  function handleColorSelect(color) {
    if (!colors.includes(color)) {
      setColors(prevColors => [...prevColors, color]);
    } else {
      setColors(prevColors => prevColors.filter(item => item.name !== color.name));
    }
  }

  function handleColorUnSelect(color) {
    setColors(prevColors => prevColors.filter(item => item.name !== color.name));
  }

  const colorsToSelect = colorsToSelectFrom.map((object) => {
    const color = object.color;
    const isSpecialColor = color === 'black' || color === 'burgundy' || color === 'brown';
    let labelClass = `cat-checkbox__label ${isSpecialColor ? 'white-font' : ''}`;

    if (colors.includes(object)) labelClass += ' opacity-low';

    return (
      <div
        className={labelClass + ' color-button unselectable color-' + color}
        data-color={color}
        key={object.name}
        onClick={() => handleColorSelect(object)}
      >
        {object.name}
      </div>
    )
  });

  const colorsSelected = colors.map((object) => {
    const color = object.color;
    const isSpecialColor = color === 'black' || color === 'burgundy' || color === 'brown';
    const labelClass = `cat-checkbox__label ${isSpecialColor ? ' white-font' : ''}`;
    return (
      <div
        className={labelClass + ' color-button unselectable color-' + color}
        data-color={color}
        onClick={() => handleColorUnSelect(object)}
      >
        {object.name}
      </div>
    )
  });





  // useEffect(() => {
  //   if (price > 0 && discount > 0) {
  //     setSalePrice(Math.floor((price - (price / 100 * discount)) / 10) * 10);
  //   } else {
  //     setSalePrice('');
  //   }
  // }, [colors]);


  // UPSELLS

  async function handleUpsellsInput(input) {
    const inputValue = input.target.value;
    if (inputValue.length > 0) {
      const searchResults = await adminApi.productSearch(input.target.value);
      setUpsellsSearchResults(searchResults.slice(0, 10));
      setIsUpsellsSearchResultsShowing(true);
    } else {
      setUpsellsSearchResults([]);
      setIsUpsellsSearchResultsShowing(false);
    }
  }

  function handleUpsellSelect(product) {
    setUpsellsSelected(prevUpsells => [...prevUpsells, product]);
  }

  function handleUpsellRemoval(productId) {
    setUpsellsSelected(prevUpsells => prevUpsells.filter(product => product.productId !== productId));
  }

  function closeUpsellsSearchResults() {
    setIsUpsellsSearchResultsShowing(false);
    document.getElementById('upsellsInput').value = '';
  }


  // sale
  useEffect(() => {
    if (price > 0 && discount > 0) {
      setSalePrice(Math.floor((price - (price / 100 * discount)) / 10) * 10);
    } else {
      setSalePrice('');
    }
  }, [discount, price]);


  // useEffect(() => {
  //   const handleBeforeUnload = (event) => {
  //     event.preventDefault();
  //     event.returnValue = ''; // Modern browsers ignore this, but it's needed for some older ones
  //   };

  //   window.addEventListener('beforeunload', handleBeforeUnload);

  //   return () => {
  //     window.removeEventListener('beforeunload', handleBeforeUnload);
  //   };
  // }, []);


  return (
    <>
      <AdminHeader
      />
      <main className='product-constructor'>
        <form className='add-form' onSubmit={handleFormSubmit}>
          {/* <h2>   </h2> */}
          <div className='form-container'>


            <div className="left-block">
              <div className="block">
                <input className='input_size_long input-title' type="text" id="productName" value={productName} placeholder=' ' onChange={(e) => setProductName(e.target.value)} />
                <textarea className='input_size_long input_size_big' id="productDescription" value={productDescription} placeholder=' ' onChange={(e) => setProductDescription(e.target.value)} />
              </div>

              {/*     */}

              <FileUploader
                images={images}
                setImages={setImages}
                videos={videos}
                setVideos={setVideos}
                buttonColor={buttonColor}
                setButtonColor={setButtonColor}
              />

              {/*    */}
              <div className="block">
                <h3> </h3>
                <div className='cats-div'>
                  {HARDCODED_CATEGORIES.map((term) => (
                    <label htmlFor={'cat-' + term.slug} className='cat-checkbox__label' key={term.termId}>
                      <input className='cat-checkbox' type='checkbox' name='category' value={term.termId} id={'cat-' + term.slug} onChange={handleCategoryChange} />
                      <div className='pseudo-button pointer unselectable'>{term.name}</div>
                    </label>
                  ))}
                </div>
              </div>


              <div className='stones block'>
                <h3></h3>
                <input
                  type="text"
                  id='stonesInput'
                  onChange={handleStonesInput}
                  className='input_size_medium stones'
                  placeholder="  "
                />

                {isStonesSearchResultsShowing && (
                  <div className='stoneSearchResults'>
                    <div className='stoneSearchResults-top'>
                      <div> </div>
                      <div
                        onClick={closeStoneSearchResults}
                        className='close_upsells_search_results'
                      >[x] </div>
                      {/* on click outside closure is needed */}
                    </div>
                    {stonesSearchResults.length == 0 && (<div className='noStoneResults'>  <div> : <b>{stonesInputValue}</b></div></div>)}
                    {stonesSearchResults.map((stone) => {
                      if (!stonesSelected.includes(stone)) {
                        return (
                          <div
                            className='stoneSearchResult'
                            key={stone.termId}
                            onClick={() => handleStoneSelect(stone)}
                          >
                            <span>{stone.name}</span>

                          </div>
                        )
                      }
                    })}
                  </div>
                )}

                {stonesSelected.length > 0 && (
                  <div className='stonesSelected'>
                    {stonesSelected.map((stone) => (
                      (
                        <div
                          className='stoneSelected'
                          key={stone.termId}
                        >

                          <span>{stone.name}</span>

                          <button className='thumbnail-button' onClick={() => handleStoneRemoval(stone.termId)}></button>

                        </div>
                      )
                    ))}
                  </div>
                )}
              </div>





              <div className='block'>
                <h3> </h3>
                <div className='colors-container'>

                  {colorsToSelect}
                </div>
                {colors.length > 0 && (
                  <div className='colors-selected'>
                    <h3> </h3>
                    {colorsSelected}
                  </div>
                )}

              </div>



            </div>


            <div className="right-block">
              <div className='price_and_status'>
                {/*   ,  */}
                <div className='price_and_instock'>
                  {/* <h3>, </h3> */}

                  <div className='instock block'>
                    <div className='price__item'>
                      <label htmlFor='input-stock' className='instock_text'></label>
                      <div className='instock_buttons instock_minus unselectable' onClick={() => inStock > 0 && setInStock(parseInt(inStock) - 1)}></div>
                      <input className='instock_input' type="number" id='input-stock' value={inStock} onChange={(e) => setInStock(e.target.value)} />
                      <div className='instock_buttons instock_plus unselectable' onClick={() => setInStock(parseInt(inStock) + 1)}>+</div>
                    </div>
                  </div>

                  <div className='price_block block'>
                    <label htmlFor='input-price' className={`full_price ${discount == 0 ? 'font-weight-600' : ''}`}> </label>
                    <input className={`price_input ${discount > 0 ? 'line-through opacity-medium' : ''}`} type="number" placeholder='' id='input-price' value={price} onChange={(e) => setPrice(e.target.value)} />
                    <div className={`price_ruble_icon ${discount > 0 ? 'opacity-medium' : ''}`}></div>

                    <label htmlFor='input-sale-price' className={`sale_price ${discount > 0 ? 'font-weight-600' : 'opacity-medium'}`}>  </label>
                    <input className={`sale_price_display ${discount == 0 || price < 1 ? 'opacity-low' : ''}`} type="number" id='input-sale-price' value={salePrice} onChange={(e) => setSalePrice(e.target.value)} disabled /><div className={`sale_price_ruble ${discount == 0 ? 'opacity-low' : ''}`}></div>

                    <label htmlFor='input-discount' className={`discount_text ${price < 1 ? 'opacity-low' : ''}`}></label>
                    <input className='discount_slider' type="range" min="0" max="50" placeholder="0" id='input-discount' value={discount} onChange={(e) => setDiscount(e.target.value)} disabled={price < 1} />
                    <div className={`discount_percentage ${price < 1 ? 'opacity-low' : ''}`}>{discount}%</div>
                  </div>

                </div>
                <div className='status_and_copy'>

                  <div className='block'>
                    {/* <h3> </h3> */}
                    <div
                      className={`status-button published ${status == 1 ? 'active' : ''}`}
                      onClick={() => setStatus(1)}
                    ><img src={checkSquare} className='status_icon' />
                      
                    </div>

                    <div className={`status-button draft ${status == 0 ? 'active' : ''}`}
                      onClick={() => setStatus(0)}
                    ><img src={fileDashed} className='status_icon' />
                      
                    </div>
                    <hr />
                    <div className='view_and_submit'>
                      <button className='view-product' type="submit"><img src={eyeIcon} /></button>
                      <button className='submit-adding' type="submit"></button>
                    </div>
                  </div>

                  <div className='block' style={{ textAlign: 'center' }}>
                    {/* button should be inactive if the product is not saved yet */}
                    <a href='' className='copy_product'><img src={copySimple} className='status_icon' />  </a>
                  </div>
                </div>
              </div>

              {/*        */}
              <div className='chars__container block'>
                <h3></h3>

                <div className='chars__item'>
                  <label htmlFor='input-sku' className=''></label>
                  <input className='chars_input' id='input-sku' type="number" placeholder="12345" value={articleNumber || ''} onChange={(e) => setArticleNumber(e.target.value)} />
                </div>

                <div className='chars__item'>
                  <label htmlFor='input-length' className=''>  :</label>
                  <input className='chars_input' id='input-length' type="number" placeholder="22,5" value={length || ''} onChange={(e) => setLength(e.target.value)} />
                </div>

                <div className='chars__item'>
                  <label htmlFor='input-weight' className=''>  :</label>
                  <input className='chars_input' id='input-weight' type="number" placeholder="25" value={weight || ''} onChange={(e) => setWeight(e.target.value)} />
                </div>

                <div className='chars__item'>
                  <label htmlFor='input-origin' className=''> :</label>
                  <input className='chars_input' id='input-origin' type="text" placeholder="" value={stoneOrigin || ''} onChange={(e) => setStoneOrigin(e.target.value)} />
                </div>

                <div className='chars__item'>
                  <label htmlFor='input-metal' className=''>:</label>
                  <input className='chars_input' id='input-metal' type="text" placeholder="" value={metalType || ''} onChange={(e) => setMetalType(e.target.value)} />
                </div>

                <div className='chars__item'>
                  <label htmlFor='input-stonesize' className=''>   </label>
                  <input className='chars_input' id='input-stonesize' type="text" placeholder="1,2  0,3" value={stoneSize || ''} onChange={(e) => setStoneSize(e.target.value)} />
                </div>

                <div className='chars__item'>
                  <label htmlFor='input-production' className=''>:</label>
                  <input className='chars_input' id='input-production' type="text" placeholder=", " value={production || ''} onChange={(e) => setProduction(e.target.value)} />
                </div>

              </div>

              <div className='block'>
                <h3></h3>
                <input
                  type="text"
                  id='sizesInput'
                  onChange={handleSizesInput}
                  className='input_size_medium sizes'
                  placeholder="  "
                />


                {isSizesSearchResultsShowing && (
                  <div className='sizeSearchResults'>
                    <div className='sizeSearchResults-top'>
                      <div> </div>
                      <div
                        onClick={closeSizeSearchResults}
                        className='close_upsells_search_results'
                      >[x] </div>
                      {/* on click outside closure is needed */}
                    </div>
                    {sizesSearchResults.length == 0 && (<div className='noSizeResults'>  <div> : <b>{sizeInputValue}</b></div></div>)}
                    {sizesSearchResults.map((size) => {
                      if (!sizesSelected.includes(size)) {
                        return (
                          <div
                            className='sizeSearchResult'
                            key={size.termId}
                            onClick={() => handleSizeSelect(size)}
                          >
                            <span>{size.name}</span>

                          </div>
                        )
                      }
                    })}
                  </div>
                )}

                {sizesSelected.length > 0 && (
                  <div className='sizesSelected'>
                    {sizesSelected.map((size) => (
                      (
                        <div
                          className='sizeSelected'
                          key={size.termId}
                        >

                          <span>{size.name}</span>

                          <button className='thumbnail-button' onClick={() => handleSizeRemoval(size.termId)}></button>

                        </div>
                      )
                    ))}
                  </div>
                )}


              </div>



              <div className='block'>

                <h3> </h3>

                <div className='features'>
                  {FEATURES_LIST.map((feature) => (
                    <label htmlFor={'checkbox-' + feature.slug} key={feature.slug}>
                      <input className='cat-checkbox' type="checkbox" checked={features[feature.slug] == 1} id={'checkbox-' + feature.slug} name="special-tags" value={feature.slug} onChange={() => handleFeatureChange(feature.slug)}></input>
                      <div className='features-pseudo-button pointer unselectable'>{feature.name}</div>
                    </label>
                  ))}

                  {/* <input className='checkbox_type_normal' type="checkbox" id='checkbox-unique' name="special-tags" checked={isUnique === '1'} onChange={() => setIsUnique(isUnique === '1' ? '0' : '1')}></input> */}
                </div>
              </div>

              <div className='upsells block'>
                <h3> </h3>
                <input
                  type="text"
                  id='upsellsInput'
                  onChange={handleUpsellsInput}
                  className='input_size_medium upsell'
                  placeholder="  "
                />
                {isUpsellsSearchResultsShowing && (
                  <div className='upsellSearchResults'>
                    <div className='upsellSearchResults-top'>
                      <div> </div>
                      <div
                        onClick={closeUpsellsSearchResults}
                        className='close_upsells_search_results'
                      >[x] </div>
                      {/* on click outside closure is needed */}
                    </div>
                    {upsellsSearchResults.length == 0 && (<div className='noUpsellResults'>  </div>)}
                    {upsellsSearchResults.map((product) => {
                      if (!upsellsSelected.includes(product)) {
                        return (
                          <div
                            className='upsellSearchResult'
                            key={product.productId}
                            onClick={() => handleUpsellSelect(product)}
                          >
                            <img src={"https://dolina.shop/photos/" + product.pictures[0].path.slice(0, -4) + `_preview.jpg`} alt={product.title} />
                            <span>{product.title}</span>
                          </div>
                        )
                      }
                    })}
                  </div>
                )}

                {upsellsSelected.length > 0 && (
                  <div className='upsellsSelected'>
                    {upsellsSelected.map((product) => (
                      (
                        <div
                          className='upsellSelected'
                          key={product.productId}
                        >

                          <a href={"http://localhost:3000/product/" + product.slug + '-' + product.productId} target='_blank'>
                            <img src={"https://dolina.shop/photos/" + product.pictures[0].path.slice(0, -4) + `_preview.jpg`} alt={product.title} />
                            <span>{product.title}</span>
                          </a>

                          <button className='thumbnail-button' onClick={() => handleUpsellRemoval(product.productId)}></button>

                        </div>
                      )
                    ))}
                  </div>
                )}
              </div>
            </div>


          </div>


        </form>
      </main>
    </>);
};

export default ProductConstructor;




