                  <div className='city_select'>
                    {cities.map(city => (
                      <span className='city_suggested' key={city.code} onClick={() => handleCitySelect(city)}>
                        <b>{city.city}</b> ({city.sub_region}, {city.region})
                      </span>
                    ))}
                  </div>
                )}
