import starIcon from '../../images/star.svg';
import starActiveIcon from '../../images/star-fill.svg';
import phoneIcon from '../../images/phone_2.svg';
import phoneIconThin from '../../images/phone-thin.svg';
import phoneIconFill from '../../images/phone-fill.svg';
import rubleIcon from '../../images/currency-rub.svg';
import checkmarkIconGreen from '../../images/check-circle_green.svg';
import hourglassIcon from '../../images/hourglass-high-fill.svg';
import hourglassIconRed from '../../images/hourglass-high-fill_red.svg';
import sparkleIcon from '../../images/sparkle-fill.svg';
import React, { useState, useEffect } from 'react';
// import Header from '../Header/Header';
import AdminHeader from './AdminHeader';
import './OrderList.css';
import FileUploader from './FileUploader';
import adminApi from '../../utils/adminApi';
import productsApi from '../../utils/Api';
import { DEFAULT_COLOR_LIST, FEATURES_LIST, HARDCODED_CATEGORIES } from '../../utils/constants';

import imageIcon from '../../images/image.svg';


function OrderList({ terms }) {

    const [orders, setOrders] = useState([]);


    const [currentorderId, setCurrentorderId] = useState([]); // for price editing etc.
    const [price, setPrice] = useState(0);
    const [salePrice, setSalePrice] = useState(0);
    const [discount, setDiscount] = useState(0);
    const [priceBlockStyle, setPriceBlockStyle] = useState({ display: "none" });

    useEffect(() => {
        adminApi.getOrders()
            .then((data) => {
                setOrders(data);
                console.log(data)
            }).catch((err) => {
                console.log(err);
            })
    }, []);

    function deleteProduct(orderId) {
        // if (window.confirm('Точно удалить?')) {
        //     adminApi.deleteProduct(orderId)
        //         .then(setProducts(products.filter(product => order.orderId !== orderId)));
        // }
    }

    function setStatus(orderId, status) {
        
        adminApi.setOrderStatus(orderId, status)
            .then(response => {
                setOrders(prevOrders =>
                    orders.map(o =>
                        o.orderId === orderId
                            ? { ...o, orderStatus: status }
                            : o
                    )
                )
            }
            );
    }

    function handlePriceClick(event, orderId, price, salePrice, discount) {

        // const target = event.target;
        // const rect = target.getBoundingClientRect();

        // setCurrentorderId(orderId)
        // setPrice(price);
        // setSalePrice(salePrice);
        // setDiscount(discount ?? '0');

        // const top = rect.top + window.scrollY - 110 + (rect.height / 2);

        // setPriceBlockStyle({
        //     display: 'block',
        //     top: `${top}px`
        // });
    }

    useEffect(() => {
        // if (price > 0 && discount > 0) {
        //     setSalePrice(Math.floor((price - (price / 100 * discount)) / 10) * 10);
        // } else {
        //     setSalePrice('');
        // }
    }, [discount, price]);

    function closePricePopup() {
        // setPriceBlockStyle({ display: 'none' });
    }

    function savePricePopup() {
        // setPriceBlockStyle({ display: 'none' });
        // const priceElement = document.querySelector('.table_price.p_' + currentorderId);
        // priceElement.style.opacity = 0.25;

        // adminApi.updatePrice(currentorderId, price, salePrice, discount)
        //     .then(response => {
        //         priceElement.style.opacity = 1;
        //         setProducts(prevProducts =>
        //             prevProducts.map(p =>
        //                 p.orderId === currentorderId
        //                     ? { ...p, price: price, salePrice: salePrice, discount: discount }
        //                     : p
        //             )
        //         )
        //     }
        //     );
    }

    function handleInStockChange(orderId, newInStock) {
        // const inStockElement = document.querySelector('.table_instock.p_' + orderId);
        // inStockElement.style.opacity = 0.25;

        // adminApi.setInStock(orderId, newInStock)
        // .then(response => {
        //     inStockElement.style.opacity = 1;
        //     setProducts(prevProducts =>
        //         prevProducts.map(p =>
        //             p.orderId === orderId
        //                 ? { ...p, inStock: newInStock }
        //                 : p
        //         )
        //     )

        //     // starButton.style.opacity = 1;
        // }
        // );
    }

    const getStatusName = (index) => {
        switch (index) {
            case 0:
                return 'Новый';
            case 1:
                return 'Собираем заказ';
            case 2:
                return 'В пути';
            case 3:
                return 'Получен';
            case -1:
                return 'Отменён';
            default:
                return '';
        }
    };

    function cancelTracking(orderId, tracking) {
        const value = tracking ?? 'Добавить';
        document.querySelector('o_' + orderId).insertAdjacentHTML(value);
    }

    function addTracking(event, order) {
        event.target.innerHTML = `<input type="number" class="o_${order.orderId}" value="${order.tracking}">
        <span class="cancelTracking" onclick="cancelTracking(${order.orderId, order.tracking})">Отмена</span>
        <span class="saveTracking" onclick=saveTracking(${order.orderId})>Сохранить</span>`;
    }

    return (
        <>
            <div className='admin_page product_list_page'>
                <AdminHeader
                />
                <div className="order_list">
                    <div className="table_header">
                        <span>Заказ</span>
                        <span><img src={phoneIcon} /></span>
                        <span style={{ padding: '0 20px' }}>Адрес</span>
                        <span>Дата</span>
                        <span style={{ textAlign: 'center' }}><img src={rubleIcon} /></span>
                        <span>Статус</span>
                        <span className='justify_center'>Трек номер</span>
                    </div>
                    {orders.map(order => (
                        <div className='table_order' key={order.orderId}>
                            <a href={'/administration/order/' + order.orderId} className='order_from' title="Перейти в заказ">№<b>{order.orderId}</b> <span>{order.fullName}</span></a>
                            <span className='callback'>{order.callbackNeeded ? <img src={phoneIconFill} /> : <img src={phoneIconThin} />}</span>
                            <span className='delivery_address'><b>{order.city}</b>, {order.deliveryAddress}</span>
                            <span className='date'>
                                {(() => {
                                    const date = new Date(order.createdAt * 1000);
                                    return date.toLocaleString('ru', {
                                        year: 'numeric',
                                        month: 'numeric',
                                        day: 'numeric',
                                    });
                                })()}
                            </span>
                            <span className='payment'>
                                {order.paid ? ( <img src={checkmarkIconGreen} /> ) : ( order.paymentType === 1 ? ( <img src={hourglassIconRed} /> ) : ( <img src={hourglassIcon} />))}
                                <span className='price_and_payment'>
                                    <b style={{color: order.paid ? ('green') : (order.paymentType === 1 ? ('red') : (''))}}>{order.totalPrice} ₽</b>
                                    <span>{order.paymentType ? 'Предоплата' : 'Наложенный платёж'}</span>
                                </span>
                            </span>
                            <span className='status'>
                                <b>
                                    {order.orderStatus === 0 && <span className='sparkled'>Новый <img src={sparkleIcon} /></span>}
                                    {order.orderStatus === 1 && 'Собираем заказ'}
                                    {order.orderStatus === 2 && 'В пути'}
                                    {order.orderStatus === 3 && 'Получен'}
                                    {order.orderStatus === -1 && 'Отменён'}</b>
                                <div className='balls'>
                                {order.orderStatus !== -1 && [...Array(4)].map((_, index) => (
                                <div
                                key={index}
                                className={`ball ${index <= order.orderStatus ? 'ball_filled' : ''}`}
                                // style={{cursor: index === order.orderStatus && 'default'}}
                                style={{cursor: index === order.orderStatus && 'default', backgroundColor: order.orderStatus === 0 && index === 0 && '#570d88'}}
                                onClick={() => index !== order.orderStatus && setStatus(order.orderId, index)}
                                title={getStatusName(index)}
                                ></div>
                                ))}
                            </div>
                            </span>
                            <span><span>{order.tracking ?? '-'}</span></span>
                        </div>
                    ))}

                </div>

            </div>
        </>
    )
};

export default OrderList;
