import './BurgerMenu.css';
import { NavLink } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
// import closeIcon from '../../../images/x-bold.svg'

export default function BurgerMenu({ isOpen, onCloseterms, terms, onCatClick, activeCategory }) {

  const navigate = useNavigate();

  // function handleNavLinkClick(url) {
  //   onClose(); // Закрываем бургер-меню перед переходом по ссылке
  //   navigate(url); // Перенаправляем пользователя на указанный URL
  // }

  function handleClick(event) {
    event.preventDefault();
    onCatClick(event.target.dataset.id);
  }  
  
  function resetCategory() {
    onCatClick('');
  }

  return (
   <div className={(isOpen ? 'burger__popup burger__visible' : 'burger__popup')}>
    <div className='burger__container'>
      <nav className='burger__navigation'>
        <button type="button" className="burger__close-button" onClick={onClose}></button>
        {terms.categories && terms.categories.map((term) => (
              <NavLink to='' className='burger__link' onClick={handleClick} key={term.termId} data-id={term.termId}>{term.name}</NavLink>
            ))}
            {/* <div onClick={() => handleNavLinkClick('/catalog/?category=6')} className='burger__link'>Бусы</div>
            <div onClick={() => handleNavLinkClick('/catalog/?category=44')} className='burger__link'>Серьги</div>
            <div onClick={() => handleNavLinkClick('/catalog/?category=40')} className='burger__link'>Кольца</div>
            <div onClick={() => handleNavLinkClick('/catalog/?category=102')} className='burger__link'>Подвески</div>
            <div onClick={() => handleNavLinkClick('/catalog/?category=167')} className='burger__link'>Комплекты</div>
            <div onClick={() => handleNavLinkClick('/catalog/?category=107')} className='burger__link'>Броши</div> */}

            <div onClick={() => handleNavLinkClick('/catalog/new')} className='burger__link'>Новинки</div>
            <div onClick={() => handleNavLinkClick('/catalog/sale')} className='burger__link'>Скидки</div>
      </nav>
    </div>
   </div>
  )
}

