import Header from "../Header/Header";
import MobileNavBar from "../MobileNavBar/MobileNavBar";
import './ContactPage.css';
import { useEffect, useState, useContext } from "react";
import Reviews from "../Reviews/Reviews";
import { CurrentUserContext } from "../../contexts/CurrentUserContext";

export default function ContactPage({ reviews, terms, onCatClick, category, onBurgerClick, onBurgerClose, isBurgerOpen, search, setSearch, orders, cart }) {
  const currentUser = useContext(CurrentUserContext);
  console.log(currentUser.name)
  const [title, setTitle] = useState('');
  const [text, setText] = useState('');
  const [author, setAuthor] = useState();

  function handleTitleChange(e) {
    setTitle(e.target.value)
  }

  function handleTextChange(e) {
    setText(e.target.value);
  }

  function handleAuthorChange(e) {
    setAuthor(e.target.value);
  }

  useEffect(() => {
    document.title = 'Отзывы - Долина самоцветов';
  }, []);

  return (
    <>
      <main className="reviews">
        <h1 className="wishlist-header">Отзывы</h1>
        <div className="reviews-container">
          <button type="button" className="review__button">Оставить отзыв</button>
          <div className="reviews__form-container reviews__form-container_active">
            <form className="reviews__form">
              <div className="flex_type_row">
                <div className="flex_type_column" style={{width:'50%'}}>
                  <label htmlFor="title" className='reviews__input-label'>Заголовок отзыва*</label>
                  <input
                    className="reviews__form-input"
                    // placeholder=""
                    type="text"
                    name="title"
                    required
                    minLength={2}
                    onChange={handleTitleChange}
                    value={title || ''}
                  ></input>
                </div>
                <div className="flex_type_column" style={{width:'50%'}}>
                <label htmlFor="author" className='reviews__input-label'>Подпись*</label>
                <input
                  className="reviews__form-input"
                  // placeholder=""
                  type="text"
                  name="author"
                  required
                  minLength={2}
                  onChange={handleAuthorChange}
                  value={author || ''}
                ></input>
                </div>
                
              </div>
              <div className="flex_type_column">
                <label htmlFor="text" className='reviews__input-label'>Текст отзыва*</label>
                <textarea
                  className="reviews__form-input_l"
                  // style={{ minHeight: '100px' }}
                  // placeholder=""
                  type="text"
                  name="text"
                  required
                  minLength={5}
                  onChange={handleTextChange}
                  value={title || ''}
                ></textarea>
              </div>

              <button type="submit" className="review__submit-button">Опубликовать</button>
            </form>
          </div>
          <Reviews
            reviews={reviews}
          />
        </div>
      </main>
      <MobileNavBar
        onBurger={onBurgerClick}
        isBurgerOpen={isBurgerOpen}
        onBurgerClose={onBurgerClose}
        cart={cart}
        onCatClick={onCatClick}
        orders={orders}
      />
    </>
  )
}
