import { useEffect, useState } from 'react';
import './App.css';
import MainPage from './pages/MainPage';
import CatalogPage from './pages/CatalogPage';
import ProductPage from './pages/ProductPage';
import RegisterForm from './Form/RegisterForm';
import LoginForm from './Form/LoginForm';
import ShoppingCart from './ShoppingCart/ShoppingCart';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import Wishlist from './Profile/Wishlist';
import ProtectedRoute from './ProtectedRoute';
import productsApi from '../utils/Api';
import usersApi from '../utils/UsersApi';
import NotFoundPage from './NotFoundPage/NotFoundPage';
import Profile from './Profile/Profile';
import Orders from './Profile/Orders';

import { Route, Routes, useNavigate, NavLink } from 'react-router-dom';


function App() {

  const navigate = useNavigate();
  const [productData, setProductData] = useState([]);
  const [products, setProducts] = useState([]);
  const [inputError, setInputError] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState((localStorage.getItem('jwt') ? true : false));
  const [currentUser, setCurrentUser] = useState({});
  const [savedProducts, setSavedproducts] = useState([]);
  const [orders,setOrders] = useState([]);
  const [cart, setCart] = useState([]);
  // const [isSaved, setIssaved] = useState(false);


  function handleLogin(email, password) {
    setInputError('');
    usersApi.login(email, password)
      .then(() => {
        setIsLoggedIn(true);
        navigate('/', { replace: true })
      })
      .catch((err) => {
        if (err.status === 401) {
          setInputError('Неправильный логин или пароль')
        } else {
          setInputError('При авторизации произошла ошибка')
        }
      })
  }

  function handleRegistration(name, email, password) {
    setInputError('');
    usersApi.register(name, email, password)
      .then(() => {
        handleLogin(email, password);
      })
      .catch((err) => {
        if (err.status === 409) {
          setInputError('Пользователь с таким email уже существует')
        } else {
          setInputError('При регистрации пользователя произошла ошибка')
        }
      })
  }

  function handleSignOut() {
    setIsLoggedIn(false);
    localStorage.removeItem('jwt');
    navigate('/signin', { replace: true });
  }

  // КОРЗИНА
  useEffect(() => {
    const savedСart = localStorage.getItem('cart');
    if (savedСart) {
      setCart(JSON.parse(savedСart));
    }
  }, []);

  function handleAddToCart(productId, checked, size) {
    const newCart = {...cart};
    newCart[productId] = {checked, size}; // это типа как эррэй
    setCart(newCart);
    if (isLoggedIn) {
      // console.log(newCart)
      // патчим в корзину юзера
      usersApi.updateCart(JSON.stringify(newCart))
    } else {
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
  }

  function handleRemoveFromCart(productId) {
    const newCart = {...cart};
    delete newCart[productId];
    setCart(newCart);
    if (isLoggedIn) {
      usersApi.updateCart(JSON.stringify(newCart))
    } else {
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
  }

  // function update(productId, checked, size) {
  //   const newCart = {...cart};
  //   newCart[productId] = {checked, size}; // это типа как эррэй
  //   setCart(newCart);
  //   if (isLoggedIn) {
  //     console.log(newCart)
  //     // патчим в корзину юзера
  //     usersApi.updateCart(JSON.stringify(newCart))
  //   } else {
  //     localStorage.setItem('cart', JSON.stringify(newCart));
  //   }
  // }

  // переход в товар
  function handleCardClick(newSlug) {
    navigate(`/product/${newSlug}`, { replace: true });
  }

  // лайк
  function handleLikeClick(id) {
    if (isLoggedIn) {
      if (savedProducts.includes(id)) {      
        usersApi.removeProduct(id)
        .then(() => {
          setSavedproducts(savedProducts.filter(item => item !== id));
        })
        .catch(err => console.log(err));
      } else {
        usersApi.saveProduct(id)
        .then(() => {
          setSavedproducts([id, ...savedProducts]);
        })
        .catch(err => console.log(err));
      }
    }
    else {
      navigate('/signin', { replace: true });
    }
  }

  // Аутентификация, ставит current-юзера
  // useEffect(() => {
  //   const token = localStorage.getItem('jwt');
  //   const savedСart = localStorage.getItem('cart');
  //   // console.log(token)
  //   if (token) {
  //     usersApi.checkToken(token)
  //       .then((res) => {
  //         usersApi.getSavedProducts()
  //         .then((wishlist) => {
  //           res.wishlist = wishlist;
  //         });
  //       })

  //       .then((res) => {
  //         setCurrentUser(res);
  //           setCart(JSON.parse(res.cart));
  //           setIsLoggedIn(true);
  //       })
  //       .catch((err) => {
  //         console.log(err)
  //       })
  //   }
  // }, [isLoggedIn]);

    // Аутентификация, ставит current-юзера, корзину
  useEffect(() => {
    const token = localStorage.getItem('jwt');
    const savedCart = localStorage.getItem('cart');

    if (token) {
      usersApi.checkToken(token)
        .then((res) => {
          return Promise.all([
            res,
            usersApi.getSavedProducts(),
            savedCart ? usersApi.updateCart(savedCart) : res.cart
          ]);
        })
        .then(([userRes, wishlist, updatedCart]) => {
          // userRes.wishlist = wishlist;
          setCurrentUser(userRes);
          console.log(currentUser);
          setCart(JSON.parse(updatedCart));
          setIsLoggedIn(true);
        })
        .catch((err) => console.error('Error while updating cart:', err));
    }
  }, []);

  // ставит избранное и заказы
  useEffect(() => {
    if (isLoggedIn) {
      usersApi.getUsersProfile()
        .then((data) => {
          setSavedproducts(data[0])
          setOrders(data[1])
        // console.log(data)
        })
        .catch((err) => {
          console.log(err)
        })
    }
  }, [isLoggedIn])


// Получаем продукты и рендерим товары
  useEffect(() => {
    productsApi.getAllProducts()
      .then((data) => {
        // console.log(data);
        setProducts(data);
      }).catch((err) => {
        console.log(err);
      })
  }, []) // когда в скобках products, запросы идут бесконечно. Почему?

// console.log(currentUser)

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
              <div className='header_block_1'>
          <div className='header__container'>
            <NavLink to='/' className='navlink_size_s color_white' >О доставке</NavLink>
            <NavLink to='/' className='navlink_size_s color_white' >О нас</NavLink>

          </div>
        </div>
        <div className='content'>
          <Routes>
          <Route path="/" element={<MainPage
                onCardClick={handleCardClick}
                onLikeClick={handleLikeClick}
                products={products}
                savedProducts={savedProducts}
                isLoggedIn={isLoggedIn}
              />} />
              <Route path="/c" element={<CatalogPage
                onCardClick={handleCardClick}
                onLikeClick={handleLikeClick}
                products={products}
                savedProducts={savedProducts}
                isLoggedIn={isLoggedIn}
              />} />
              <Route path="/product/:newSlug" element={<ProductPage
                productData={productData}
                onLikeClick={handleLikeClick}
                savedProducts={savedProducts}
                isLoggedIn={isLoggedIn}
                onAddClick={handleAddToCart}
                cart={cart}
              />} />
              <Route path="/signup" element={<RegisterForm
                onRegistration={handleRegistration}
                inputError={inputError}
              />} />
              <Route path="/signin" element={<LoginForm
                onLogin={handleLogin}
                inputError={inputError}
              />} />
              <Route path="/me" element={<ProtectedRoute
                isLoggedIn={isLoggedIn}
                element={Profile}
                onSignout={handleSignOut}

                savedProducts={savedProducts}
                orders={orders}
              />} />
              <Route path="/me/orders" element={<ProtectedRoute
                isLoggedIn={isLoggedIn}
                orders={orders}
                element={Orders}
              />} />
              <Route path="/me/wishlist" element={<ProtectedRoute
                isLoggedIn={isLoggedIn}
                element={Wishlist}
                onCardClick={handleCardClick}
                onLikeClick={handleLikeClick}
                savedProducts={savedProducts}
              />} />

              <Route path="/cart" element={<ShoppingCart
                cart={cart}
                onCardClick={handleCardClick}
                isLoggedIn={isLoggedIn}
                onRemoveClick={handleRemoveFromCart}
                savedProducts={savedProducts}
                onLikeClick={handleLikeClick}
              />} />
              <Route path="*" element={<NotFoundPage />} />
          </Routes>
        </div>
      </div>
    </CurrentUserContext.Provider>
  )
}

export default App;

// Страницы: 
// - главная
// - каталог 
// - страница товара 
// - личный кабинет
// - корзина 
// - оформление заказа
// - избранное
// - покупки (заказы)
// - регистрация 
// - авторизация
