import './AdminHeader.css';
import logo from '../../images/варик6.png';
import SearchForm from '../SearchForm/SearchForm';
import { Link, NavLink, useLocation } from 'react-router-dom';
import { useMediaQuery } from 'react-responsive';
import adminApi from '../../utils/adminApi';
import { useState, useEffect } from "react";
import sparkleIcon from '../../images/sparkle-fill.svg';


export default function AdminHeader({ terms, onCatClick, activeCategory }) {

  const [newOrders, setNewOrders] = useState([]);

    useEffect(() => {
      adminApi.getNewOrders()
          .then((data) => {
              setNewOrders(data);
          }).catch((err) => {
              console.log(err);
          })

  }, []);

  return (
    <>
      {true && (<header className="header">
        <link rel="preconnect" href="https://fonts.googleapis.com" />
        <link href="https://fonts.googleapis.com/css2?family=Noto+Sans:ital,wght@0,100..900;1,100..900&display=swap" rel="stylesheet" />
        <div className='header_block_2'>
          <Link to="/administration/productlist">
            <img src={logo} className="logo_theme_light" alt="Логотип" />
          </Link>
        <nav className='admin_header_block_3'>
          <div className='admin_header__menu'>
            <NavLink to='/administration/orderlist' className='navlink_size_m'>Заказы {newOrders.length > 0 ? <span className='admin_header_new_orders'><img src={sparkleIcon} />{newOrders.length}</span> : ''}</NavLink>
            <NavLink to='/administration/productlist' className='navlink_size_m'>Товары</NavLink>
            <a href='/administration/product' className='navlink_size_m'>Добавить товар</a>
          </div>
      </nav>
      
      </div>
      </header>)}
      {false && (
        <header className='header_type_mobile'>
          <SearchForm />
        </header>
      )}
    </>
  )
}
