import './Form.css';
import { Link, useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useEffect } from "react";


export default function Form({ title, name, children, buttonText, onSubmit, submitDisabled, isLoggedIn }) {

  const location = useLocation();
  const navigate = useNavigate();

  const goBack = () => {
    navigate(-1);
  };

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://yastatic.net/s3/passport-sdk/autofill/v1/sdk-suggest-with-polyfills-latest.js';
    script.async = true;
    document.head.appendChild(script);

    script.onload = () => {
      window.YaAuthSuggest.init(
        {
          client_id: "93da601e53fa40f381846ea4590ab50d",
          response_type: "token",
          redirect_uri: "https://dolina.shop/yandexOauth",
        },
        "https://dolina.shop",
        {
          view: "button",
          parentId: "buttonContainerId",
          buttonSize: 'm',
          buttonView: 'main',
          buttonTheme: 'light',
          buttonBorderRadius: "0",
          buttonIcon: 'ya',
        }
      )
        .then(({ handler }) => handler())
        .then(data => console.log('–°–æ–æ–±—â–µ–Ω–∏–µ —Å —Ç–æ–∫–µ–Ω–æ–º', data))
        .catch(error => console.log('–û–±—Ä–∞–±–æ—Ç–∫–∞ –æ—à–∏–±–∫–∏', error));
    };

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  function openOAuthPopup() {
    const width = 600;
    const height = 600;
    const left = (window.screen.width / 2) - (width / 2);
    const top = (window.screen.height / 2) - (height / 2);
    window.open(
      'https://oauth.yandex.com/authorize?response_type=code&client_id=93da601e53fa40f381846ea4590ab50d&redirect_uri=https://dolina.shop/yandexOauth',
      'YandexOAuth',
      `width=${width},height=${height},top=${top},left=${left}`
    );
  }

  
  useEffect(() => {
    if (isLoggedIn) window.location.href = 'https://dolina.shop/';
  }, [isLoggedIn]);


  useEffect(() => {
    const handleStorageChange = (event) => {
      if (event.key === 'jwt') {
        window.location.href = 'https://dolina.shop/';
      }
    };

    window.addEventListener('storage', handleStorageChange);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);


  const signupLink = <span className='form__link-caption'>–£–∂–µ –∑–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω—ã? <Link className='form__link hover_type_normal' to="/signin">–í–æ–π—Ç–∏</Link></span>;
  const signinLink = <span className='form__link-caption'>–ï—â—ë –Ω–µ –∑–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω—ã? <Link className='form__link hover_type_normal' to="/signup">–†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è</Link></span>
  return (
    <>
      <body className='form-page'>
        <div className="form">
          <section className='form__container'>
            <form name={`${name}-form`} onSubmit={onSubmit}>
              <fieldset className="form__fieldset">
                <h1 className="form__title">{title}</h1>
                {location.pathname !== '/signin' ?
                  <div className='form__notion'>
                    <p className='form__notion-text'>–¢–µ–ø–µ—Ä—å —É –Ω–∞—Å –µ—Å—Ç—å —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è!
                      –û–Ω–∞ –∑–∞–π–º—ë—Ç –≤—Å–µ–≥–æ –Ω–µ—Å–∫–æ–ª—å–∫–æ —Å–µ–∫—É–Ω–¥, –∑–∞—Ç–æ —Ç–∞–∫ –≤–∞—à–∏ –ø–µ—Ä—Å–æ–Ω–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –±—É–¥—É—Ç –≤ –±–æ–ª—å—à–µ–π –±–µ–∑–æ–ø–∞—Å–Ω–æ—Å—Ç–∏, –∞ –µ—â—ë –≤—ã —Å–º–æ–∂–µ—Ç–µ —Å–ª–µ–¥–∏—Ç—å –∑–∞ —Å–≤–æ–∏–º –∑–∞–∫–∞–∑–æ–º –∏ —Å–æ—Ö—Ä–∞–Ω—è—Ç—å —Ç–æ–≤–∞—Ä—ã –≤ –∏–∑–±—Ä–∞–Ω–Ω–æ–º!</p><p style={{ textAlign: 'center', margin: '5px 0' }}>üòç</p>
                  </div>
                  : null}
                –ê–≤—Ç–æ—Ä–∏–∑—É–π—Ç–µ—Å—å –≤ –æ–¥–∏–Ω –∫–ª–∏–∫ —á–µ—Ä–µ–∑ –Ø–Ω–¥–µ–∫—Å:
                <div id="buttonContainerId" onClick={openOAuthPopup}></div>
                –ò–ª–∏ —Å –ø–æ–º–æ—â—å—é —ç–ª–µ–∫—Ç—Ä–æ–Ω–Ω–æ–π –ø–æ—á—Ç—ã –∏ –ø–∞—Ä–æ–ª—è:<br />
                {children}
                <button type="submit"
                  name="submit"
                  className="form__submit-button hover_type_normal"
                  disabled={submitDisabled}
                >{buttonText || '–í–æ–π—Ç–∏'}</button>
              </fieldset>
            </form>
          </section>
          <div className='form__link-toggle'>
            {location.pathname !== '/signin' ?
              signupLink :
              signinLink}
          </div>
          <div id="buttonContainerId"></div>
          <div className='form__go-back' onClick={goBack}>&#8656; –ù–∞–∑–∞–¥</div>
        </div>

      </body>
    </>
  )
}

// —Å–¥–µ–ª–∞—Ç—å:
// - —á—Ç–æ–±—ã –∏–Ω–ø—É—Ç –±—ã–ª –ø—É—Å—Ç–æ–π –ø–æ –∫–ª–∏–∫—É
// - —á—Ç–æ–±—ã –ª—ç–π–±–ª—ã –ø–æ—è–≤–ª—è–ª–∏—Å—å –ø–æ—Å–ª–µ —ç—Ç–æ–≥–æ
