//   .then((data) => setData(data))
//  }

//  getData()
//  console.log(data)

return (
  <section className='product'>
    <h1 className='product__title'>{`productData.0.title`}</h1>
    <div className='product__container'>
      <div className='product_block_1'></div>
      <div className='product_block_2'></div>
      <div className='product_block_3'></div>
    </div>

  </section>
)
}

