import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

export default function MobileGallery({ pictures, onImageClick }) {

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    swipeToSlide: true
  }

  return (
    <div>
    <Slider {...settings}>
      {pictures.map((picture, index) => (
        <div key={index}>
          <img src={picture} alt={`Photo ${index}`} onClick={onImageClick} />
        </div>
      ))}
    </Slider>
  </div>
  )
}
