import { useEffect, useState } from 'react';
import './App.css';
import MainPage from './pages/MainPage';
import CatalogPage from './pages/CatalogPage';
import ProductPage from './pages/ProductPage';
import ContactPage from './pages/ContactPage';
import RegisterForm from './Form/RegisterForm';
import LoginForm from './Form/LoginForm';
import ShoppingCart from './ShoppingCart/ShoppingCart';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import Wishlist from './Profile/Wishlist';
import ProtectedRoute from './ProtectedRoute';
import productsApi from '../utils/Api';
import usersApi from '../utils/UsersApi';
import NotFoundPage from './NotFoundPage/NotFoundPage';
import Profile from './Profile/Profile';
import Orders from './Profile/Orders';
import ProductConstructor from './AdminPanel/ProductConstructor';
import ProductList from './AdminPanel/ProductList';
import OrderList from './AdminPanel/OrderList';
import Order from './AdminPanel/Order';
import { Route, Routes, useNavigate, NavLink, useLocation, useSearchParams } from 'react-router-dom';
import BurgerMenu from './Header/BurgerMenu/BurgerMenu';
import MobileNavBar from './MobileNavBar/MobileNavBar';
import OrderingPage from './ShoppingCart/OrderingPage/OrderingPage';
import Header from './Header/Header';
import Footer from './Footer/Footer';
import LicensePage from './pages/LicensePage';
import AboutPage from './pages/AboutPage';
import SuccessPage from './ShoppingCart/SuccessPage/SuccessPage';


function App() {

  const navigate = useNavigate();
  const location = useLocation();

  const [productData, setProductData] = useState([]);
  const [products, setProducts] = useState([]);
  const [inputError, setInputError] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState((localStorage.getItem('jwt') ? true : false));
  const [currentUser, setCurrentUser] = useState({});
  const [savedProducts, setSavedproducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [cart, setCart] = useState([]);
  const [isProfilePopupOpen, setIsProfilePopupOpen] = useState(false);
  const [terms, setTerms] = useState([]);
  // const [inStock, isInStock] = useState(true);
  // const [isSaved, setIssaved] = useState(false);
  // получаем данные при первой загрузке страницы, один раз
  const searchParams = new URLSearchParams(location.search)
  const categoryQuery = searchParams.get('category');
  const tagsQuery = searchParams.get('tags');
  const searchQuery = searchParams.get('search');
  const [category, setCategory] = useState(categoryQuery);
  const [tags, setTags] = useState(tagsQuery ? tagsQuery.split(',').map(id => parseInt(id)) : '');
  const [search, setSearch] = useState(searchQuery);
  const [isBurgerOpen, setIsBurgerOpen] = useState(false);
  // console.log(isBurgerOpen)

  const [offset, setOffset] = useState(0);
  const pageSize = 24;
  const [sortBy, setSortBy] = useState('default');
  const [sortOrder, setSortOrder] = useState('asc');
  const [lastProductsRequest, setLastProductsRequest] = useState(0);
  const [loading, setLoading] = useState(false);


  function handleBurgerOpening() {
    setIsBurgerOpen(true);
    // console.log('fucking burger')
  }

  function handleBurgerClosing() {
    setIsBurgerOpen(false);
    // console.log('fucking burger!')
  }

  function handleLogin(email, password) {
    setInputError('');
    usersApi.login(email, password)
      .then(() => {
        setIsLoggedIn(true);
        navigate('/', { replace: true }) // replace is messing up the history
      })
      .catch((err) => {
        if (err.status === 401) {
          setInputError('Неправильный логин или пароль')
        } else {
          setInputError('При авторизации произошла ошибка')
        }
      })
  }

  // console.log(products)

  function handleRegistration(name, email, password) {
    setInputError('');
    usersApi.register(name, email, password)
      .then(() => {
        handleLogin(email, password);
      })
      .catch((err) => {
        if (err.status === 409) {
          setInputError('Пользователь с таким email уже существует')
        } else {
          setInputError('При регистрации пользователя произошла ошибка')
        }
      })
  }

  function handleSignOut() {
    setIsLoggedIn(false);
    localStorage.removeItem('jwt');
    navigate('/signin', { replace: true }); // replace is messing up the history
  }

  function handleUpdateUser(e) {
    usersApi.updateUserData(e)
      .then((res) => setCurrentUser(res));
    setIsProfilePopupOpen(false);
  }

  function handleChangeClick() {
    // console.log(isProfilePopupOpen)
    setIsProfilePopupOpen(true);
  }

  function handleClosePopupClick() {
    // console.log(isProfilePopupOpen)
    setIsProfilePopupOpen(false)
  }


  // КОРЗИНА
  useEffect(() => {
    const savedСart = localStorage.getItem('cart');
    if (savedСart) {
      setCart(JSON.parse(savedСart));
    }
  }, []);

  function handleAddToCart(productId, checked, size) {
    const newCart = { ...cart };
    newCart[productId] = { checked, size }; // это типа как эррэй
    setCart(newCart);
    if (isLoggedIn) {
      // console.log(newCart)
      // патчим в корзину юзера
      usersApi.updateCart(JSON.stringify(newCart))
    } else {
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
  }

  function handleRemoveFromCart(productId) {
    const newCart = { ...cart };
    delete newCart[productId];
    setCart(newCart);
    if (isLoggedIn) {
      usersApi.updateCart(JSON.stringify(newCart))
    } else {
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
  }

  // function update(productId, checked, size) {
  //   const newCart = {...cart};
  //   newCart[productId] = {checked, size}; // это типа как эррэй
  //   setCart(newCart);
  //   if (isLoggedIn) {
  //     console.log(newCart)
  //     // патчим в корзину юзера
  //     usersApi.updateCart(JSON.stringify(newCart))
  //   } else {
  //     localStorage.setItem('cart', JSON.stringify(newCart));
  //   }
  // }

  // переход в товар
  function handleCardClick(newSlug) {
    console.log('хэндлер сработал, хотя не должен был')
  }

  // лайк
  function handleLikeClick(id) {
    if (isLoggedIn) {
      if (savedProducts.includes(id)) {
        usersApi.removeProduct(id)
          .then(() => {
            setSavedproducts(savedProducts.filter(item => item !== id));
          })
          .catch(err => console.log(err));
      } else {
        usersApi.saveProduct(id)
          .then(() => {
            setSavedproducts([id, ...savedProducts]);
          })
          .catch(err => console.log(err));
      }
    }
    else {
      navigate('/signin', { replace: true }); // replace is messing up the history
    }
  }


  // Аутентификация, ставит current-юзера
  // useEffect(() => {
  //   const token = localStorage.getItem('jwt');
  //   const savedСart = localStorage.getItem('cart');
  //   // console.log(token)
  //   if (token) {
  //     usersApi.checkToken(token)
  //       .then((res) => {
  //         usersApi.getSavedProducts()
  //         .then((wishlist) => {
  //           res.wishlist = wishlist;
  //         });
  //       })

  //       .then((res) => {
  //         setCurrentUser(res);
  //           setCart(JSON.parse(res.cart));
  //           setIsLoggedIn(true);
  //       })
  //       .catch((err) => {
  //         console.log(err)
  //       })
  //   }
  // }, [isLoggedIn]);

  // Аутентификация, ставит current-юзера, корзину
  useEffect(() => {
    const token = localStorage.getItem('jwt');
    const savedCart = localStorage.getItem('cart');

    if (token) {
      usersApi.checkToken(token)
        .then((res) => {
          return Promise.all([
            res,
            usersApi.getSavedProducts(),
            savedCart ? usersApi.updateCart(savedCart) : res.cart
          ]);
        })
        .then(([userRes, wishlist, updatedCart]) => {
          // userRes.wishlist = wishlist;
          setCurrentUser(userRes);
          // console.log(currentUser);
          setCart(JSON.parse(updatedCart));
          setIsLoggedIn(true);
        })
        .catch((err) => console.error('Error while updating cart:', err));
    }
  }, []);

  // ставит избранное и заказы
  useEffect(() => {
    if (isLoggedIn) {
      usersApi.getUsersProfile()
        .then((data) => {
          setSavedproducts(data[0])
          setOrders(data[1])
          // console.log(data)
        })
        .catch((err) => {
          console.log(err)
        })
    }
  }, [isLoggedIn])


  // Получаем продукты и рендерим товары
  //   useEffect(() => {
  //     productsApi.getAllProducts()
  //       .then((data) => {
  //         console.log(data);
  //         setProducts(data);
  //       }).catch((err) => {
  //         console.log(err);
  //       })
  //   }, []) // когда в скобках products, запросы идут бесконечно. Почему?

  // console.log(currentUser)



  // РУБРИКИ

  useEffect(() => {
    productsApi.getTerms()
      .then((data) => {
        setTerms(data);
      }).catch((err) => {
        console.log(err);
      })
  }, [])



  useEffect(() => {

    setCategory('');

  }, [search]);

  function fetchProducts() {

      //     setLoading(true);
  //     try {
  //         const data = await productsApi.filterProducts('', '', '', offset, pageSize);
  //         setProducts(prevProducts => [...prevProducts, ...data]);
  //         setOffset(prevOffset => prevOffset + pageSize);
  //     } catch (error) {
  //         console.error('Error fetching products', error);
  //     }
  //     setLoading(false);

    let sortByClean = sortBy.includes('-') ? sortBy.split('-')[0] : sortBy;

    if (lastProductsRequest != offset + pageSize || lastProductsRequest == 0) {

      productsApi.filterProducts(category ?? '', tags ?? '', search ?? '', offset, pageSize, sortByClean, sortOrder)
        .then((data) => {
          setProducts(prevProducts => [...prevProducts, ...data]);
          setOffset(prevOffset => prevOffset + pageSize);
        }).catch((err) => {
          console.log(err);
        })

    }

    setLastProductsRequest(offset + pageSize);

  };


  useEffect(() => {

    // обновляем url просто
    if (window.location.pathname == "/catalog/") { // kolkhoz

      // if (category) {
      //   searchParams.set('category', category);
      //   // setProducts([]);
      // } else {
      //   searchParams.delete('category');
      // }

      if (tags && tags.length > 0) {
        searchParams.set('tags', tags);
        // setProducts([]);
      } else {
        searchParams.delete('tags');
      }

      if (search) {
        searchParams.set('search', search);
        // setProducts([]);
      } else {
        searchParams.delete('search');
      }

      navigate('/catalog/?' + searchParams.toString());
    }

    // fetchProducts();

    
    let sortByClean = sortBy.includes('-') ? sortBy.split('-')[0] : sortBy

    productsApi.filterProducts(category ?? '', tags ?? '', search ?? '', 0, 24, sortByClean, sortOrder)
      .then((data) => {
        setProducts(data);
      }).catch((err) => {
        console.log(err);
      })

  }, [category, tags, search, sortBy, sortOrder]);


  function handleCategorySelection(categoryId) {
    setCategory(categoryId);
    if (categoryId) {
      navigate('/catalog/?category=' + categoryId);
    } else {
      navigate('/catalog/');
    }
    setTags([]);
  }

  function handleTagSelection(activeTags) {
    setTags(activeTags);
  }

  const showHeader = location.pathname !== '/ordering' && !location.pathname.includes('/administration');

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        {!window.location.pathname.includes("administration") &&
          <div className='header_block_1'>
            <div className='header__container'>
              <p className='.navlink_size_s color_white'>
                <NavLink to='/about' className='navlink_size_s color_white' >О нас</NavLink>
                <NavLink to='/contacts' className='navlink_size_s color_white' >Контакты</NavLink>  
                8 800 200 17 05</p>
            </div>
          </div>
        }
        <div className='content'>
          {showHeader && <Header
            terms={terms}
            onCatClick={handleCategorySelection}
            activeCategory={category}
            isBurgerOpen={isBurgerOpen}
            onBurgerClick={handleBurgerOpening}
            onBurgerClose={handleBurgerClosing}
            search={search}
            setSearch={setSearch}
            cart={cart}
            orders={orders}
          />}
          <Routes>
            <Route path="/" element={<MainPage
              onCardClick={handleCardClick}
              onLikeClick={handleLikeClick}
              products={products}
              setProducts={setProducts}
              savedProducts={savedProducts}
              isLoggedIn={isLoggedIn}
              terms={terms}
              onCatClick={handleCategorySelection}
              category={category}
              cart={cart}
              orders={orders}
              fetchProducts={fetchProducts}
              onBurgerClick={handleBurgerOpening}
            />} />
            <Route path="/contacts" element={<ContactPage
              terms={terms}
              onCatClick={handleCategorySelection}
              category={category}
              cart={cart}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
            />} />
            <Route path="/license" element={<LicensePage
              terms={terms}
              onCatClick={handleCategorySelection}
              category={category}
              cart={cart}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
            />} />
            <Route path="/about" element={<AboutPage
              terms={terms}
              onCatClick={handleCategorySelection}
              category={category}
              cart={cart}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
            />} />
            <Route path="/success" element={<SuccessPage
              terms={terms}
              onCatClick={handleCategorySelection}
              category={category}
              cart={cart}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
            />} />
            <Route path="/catalog/" element={<CatalogPage
              onCardClick={handleCardClick}
              onLikeClick={handleLikeClick}
              products={products}
              setProducts={setProducts}
              savedProducts={savedProducts}
              isLoggedIn={isLoggedIn}
              terms={terms}
              category={category}
              tags={tags}
              onCatClick={handleCategorySelection}
              onTagClick={handleTagSelection}
              cart={cart}
              orders={orders}
              sortBy={sortBy}
              setSortBy={setSortBy}
              sortOrder={sortOrder}
              setSortOrder={setSortOrder}
              fetchProducts={fetchProducts}
              onBurgerClick={handleBurgerOpening}
            />} />
            <Route path="/product/:newSlug" element={<ProductPage
              productData={productData}
              onLikeClick={handleLikeClick}
              savedProducts={savedProducts}
              isLoggedIn={isLoggedIn}
              onAddClick={handleAddToCart}
              cart={cart}
              orders={orders}
              onCardClick={handleCardClick}
              terms={terms}
              onCatClick={handleCategorySelection}
              onBurgerClick={handleBurgerOpening}
            />} />
            <Route path="/signup" element={<RegisterForm
              onRegistration={handleRegistration}
              inputError={inputError}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
            />} />
            <Route path="/signin" element={<LoginForm
              onLogin={handleLogin}
              inputError={inputError}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
            />} />
            <Route path="/me" element={<ProtectedRoute
              isLoggedIn={isLoggedIn}
              element={Profile}
              onUpdateUser={handleUpdateUser}
              onSignout={handleSignOut}
              onChangeClick={handleChangeClick}
              onCloseClick={handleClosePopupClick}
              savedProducts={savedProducts}
              orders={orders}
              cart={cart}
              isPopupOpen={isProfilePopupOpen}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
              onBurgerClick={handleBurgerOpening}
            />} />
            <Route path="/me/orders" element={<ProtectedRoute
              isLoggedIn={isLoggedIn}
              orders={orders}
              element={Orders}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
              cart={cart}
              onBurgerClick={handleBurgerOpening}
            />} />
            <Route path="/me/wishlist" element={<ProtectedRoute
              isLoggedIn={isLoggedIn}
              element={Wishlist}
              onCardClick={handleCardClick}
              onLikeClick={handleLikeClick}
              savedProducts={savedProducts}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
              cart={cart}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
            />} />

            <Route path="/cart" element={<ShoppingCart
              cart={cart}
              onCardClick={handleCardClick}
              isLoggedIn={isLoggedIn}
              onRemoveClick={handleRemoveFromCart}
              savedProducts={savedProducts}
              onLikeClick={handleLikeClick}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
            />} />
            <Route path="/ordering" element={<OrderingPage
              cart={cart}
            // onCardClick={handleCardClick}
            isLoggedIn={isLoggedIn}
            // onRemoveClick={handleRemoveFromCart}
            // savedProducts={savedProducts}
            // onLikeClick={handleLikeClick}
            // terms={terms}
            // category={category}
            // onCatClick={handleCategorySelection}
            // isBurgerOpen={isBurgerOpen}
            // onBurgerClick={handleBurgerOpening}
            // onBurgerClose={handleBurgerClosing}
            />} />
            <Route path="*" element={<NotFoundPage />} />
            <Route path="/administration/product/:productId?/:copy?" element={<ProductConstructor
              terms={terms}
            />} />

            <Route path="/administration/productlist" element={<ProductList
            
            />} />

            <Route path="/administration/orderList" element={<OrderList
              terms={terms}
            />} />

            <Route path="/administration/order/:orderId" element={<Order
              terms={terms}
            />} />


          </Routes>
          {/* <MobileNavBar/>
          <BurgerMenu isOpen={isBurgerOpen} onClose={handleBurgerClosing}/> */}
        </div>
        <Footer />
      </div>
    </CurrentUserContext.Provider>
  )
}

export default App;

// Страницы:
// - главная
// - каталог
// - страница товара
// - личный кабинет
// - корзина
// - оформление заказа
// - избранное
// - покупки (заказы)
// - регистрация
// - авторизация
