import './Catalog.css';
import Card from '../Card/Card';
import { useParams } from 'react-router-dom';
// import {useState, useEffect, useContext } from 'react';
// import { CurrentUserContext } from '../../contexts/CurrentUserContext'; 
import productsApi from '../../utils/Api';
// import Product from '../Product/Product';
import { useState, useEffect } from 'react';

export default function Catalog({ onCardClick, products, setProducts, onLikeClick, savedProducts, search, setSearch, fetchProducts, productFilters, setProductFilters = false, isLoadingMore, showLoadMore = false, catalogLoading = false}) {
  // console.log(products[0])

  
  // const [offset, setOffset] = useState(24);
  // const pageSize = 24;

  // const fetchProducts = async () => {
  //     setLoading(true);
  //     try {
  //         const data = await productsApi.filterProducts('', '', '', offset, pageSize);
  //         setProducts(prevProducts => [...prevProducts, ...data]);
  //         setOffset(prevOffset => prevOffset + pageSize);
  //     } catch (error) {
  //         console.error('Error fetching products', error);
  //     }
  //     setLoading(false);
  // };

  return (
    
    <section className={`catalog ${catalogLoading ? 'loading' : ''}`}>
      <ul className='catalog-list'>
        {products.map((product) => 
        (
          <Card
          // product={product}
            id={product.productId}
            slug={product.slug}
            name={product.title}
            // mainPicture={product.pictures[0]}
            price={product.price}
            key={product.productId}
            onCardClick={onCardClick}
            onLikeClick={onLikeClick}
            pictures={product.pictures}
            savedProducts={savedProducts}
            inStock={product.inStock}
            search={search}
            setSearch={setSearch}
            createdAt={product.createdAt}
            discount={product.discount}
            salePrice={product.salePrice}
            // isLoggedIn={isLoggedIn}
          ></Card>
        ))}
        {products.length == 0 && productFilters && productFilters.search && <>Ничего не найдено</>}
      </ul>
        {showLoadMore &&
        <div className='load-more_button-conainer'>
          <button className={'load-more_button' + (isLoadingMore ? ' loading' : '')} onClick={() => setProductFilters(prevFilters => ({
            ...prevFilters,
            offset: productFilters.offset + productFilters.pageSize
          }))} disabled={isLoadingMore}>
            {isLoadingMore ? 'загружаем...' : 'загрузить ещё'}
          </button></div>
      }

    </section>
  )
}
