class AdminApi {
    constructor(options) {
      this._url = options.url;
      this._headers = options.headers;
    }
  
    _getResponseData(res) {
      if (!res.ok) {
        return Promise.reject(`Error: ${res.status}`);
      }
      return res.json();
    }
  
    uploadImage(file) {
      const formData = new FormData();
      formData.append('image', file);
  
      return fetch(`${this._url}/admin/upload/image`, {
        method: 'POST',
        headers: this._headers, // Note: Headers should not include 'Content-Type' for FormData
        body: formData
      })
      .then(this._getResponseData);
    }
  
    uploadVideo(file) {
      const formData = new FormData();
      formData.append('video', file);
  
      return fetch(`${this._url}/admin/upload/video`, {
        method: 'POST',
        headers: this._headers, // Note: Headers should not include 'Content-Type' for FormData
        body: formData
      })
      .then(this._getResponseData);
    }
  
    productSearch(text) {  
      return fetch(`${this._url}/products/search?text=${text}`, {
        method: 'GET',
        headers: this._headers
      })
      .then(this._getResponseData);
    }
  
    addProduct(productData) {  
      return fetch(`${this._url}/admin/products`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(productData)
      })
      .then(this._getResponseData);
    }
    
  
    editProduct(productData) {  
      return fetch(`${this._url}/admin/products`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(productData)
      })
      .then(this._getResponseData);
    }

    deleteProduct(productId) {  
      return fetch(`${this._url}/admin/products?productId=${productId}`, {
        method: 'DELETE',
        headers: this._headers
      })
      .then(this._getResponseData);
    }

    setStar(productId, star) {  
      return fetch(`${this._url}/admin/products/star`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({productId, star})
      })
      .then(this._getResponseData);
    }

    updatePrice(productId, price, salePrice, discount) {  
      return fetch(`${this._url}/admin/products/price`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({productId, price, salePrice, discount})
      })
      .then(this._getResponseData);
    }

    setInStock(productId, inStock) {  
      return fetch(`${this._url}/admin/products/instock`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({productId, inStock})
      })
      .then(this._getResponseData);
    }


    // ORDERS
    

    getOrders() {
      return fetch(this._url + '/admin/orders', {
        method: 'GET',
      })
      .then(this._getResponseData);
    }


    getOrder(orderId) {  
      return fetch(`${this._url}/admin/order?orderId=${orderId}`, {
        method: 'GET',
        headers: this._headers
      })
      .then(this._getResponseData);
    }

    getNewOrders() {
      return fetch(this._url + '/admin/orders/new', {
        method: 'GET',
      })
      .then(this._getResponseData);
    }


    setOrderStatus(orderId, status) {  
      return fetch(`${this._url}/admin/orders/status`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({orderId, status})
      })
      .then(this._getResponseData);
    }


    setOrderTracking(orderId, tracking) {  
      return fetch(`${this._url}/admin/orders/tracking`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({orderId, tracking})
      })
      .then(this._getResponseData);
    }


    cancelOrder(orderId, products) {  
      return fetch(`${this._url}/admin/orders/cancel`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({orderId, products})
      })
      .then(this._getResponseData);
    }


  }
  

        
const adminApi = new AdminApi({
    url: ' https://backend.dolina.shop',
    headers: {
    //   'Content-Type': 'application/json',
    }
  });
  
export default adminApi;
