import './ShoppingCart.css';
import Header from '../Header/Header';
import MobileNavBar from '../MobileNavBar/MobileNavBar';
import CartProduct from './CartProduct/CartProduct';
import productsApi from "../../utils/Api";
import { useEffect, useState } from 'react';
import { useMediaQuery } from 'react-responsive';
import {useNavigate} from 'react-router-dom';

export default function ShoppingCart({ cart, onCardClick, onRemoveClick, savedProducts, onLikeClick, terms, onCatClick, category, onBurgerClick, onBurgerClose, isBurgerOpen, search, setSearch  }) {

  const navigate = useNavigate();

  const [cartProducts, setCartProducts] = useState([]);
  const [total, setTotal] = useState(0);
  const [totalWithDiscount, setTotalWithDiscount] = useState(0);
  const [shopDiscount, setShopDiscount] = useState(0);

  useEffect(() => {
    const productIds = Object.keys(cart);
    productsApi.getProductsById([productIds])
      .then((res) => {
        setCartProducts(res)
        // console.log(res)
      })
      .catch((err) => console.log(err))
  }, [cart]);



  useEffect(() => {
    let sum = 0;
    let withDiscount = 0;
  
    cartProducts.forEach(product => {
      sum += product.price;
      withDiscount += product.salePrice ?? product.price;
    });
    
    setTotal(sum);
    setTotalWithDiscount(withDiscount);
    setShopDiscount(sum - withDiscount);

  }, [cartProducts]); // Re-run the effect only when cartProducts change)

  

  // const [isChecked, setIsChecked] = useState(true);
  const isBigScreen = useMediaQuery({ minWidth: 768 }); // надо бы перенести в константы
  const isSmallScreen = useMediaQuery({ maxWidth: 767 });

  // function handleCheckboxClick(id) { // не работает
  //   setIsChecked(false)
  //   console.log(isChecked)
  // }

  function proceedToOrderingPage(event) {
    event.preventDefault();
    navigate('/ordering')
  }

  return (
    <>
    <main className='cart flex_type_column'>
    <h1 className='cart__header'>Корзина</h1>
    <div className='cart-container_1'>
      <section className='cart__products'>
        <ul className='cart__product-list'>
          {
            cartProducts.length < 1 ? (
              <div>
                <p style={{margin: '0'}}>Пока в корзину ничего не добавлено</p>
              </div>
            ) : (
              cartProducts.map((product) => (
                <CartProduct
                name={product.title} 
                price={product.price}
                slug={product.slug}
                salePrice={product.salePrice ?? 0}
                onCardClick={onCardClick}
                id={product.productId}
                key={product.productId}
                pictures={product.pictures}
                cartDetails={cart[product.productId]}
                onRemoveClick={onRemoveClick}
                savedProducts={savedProducts}
                onLikeClick={onLikeClick}
                inStock={product.inStock}
                // index={index}
                // onCheckboxClick={handleCheckboxClick}
                />
              ))
            )
          }
          
        </ul>
      </section>
      {isBigScreen && (<div className='cart__summary'>
        <form className='cart__form'>
          <h2 className='cart__subheader'>Сумма заказа</h2>
          <div className='cart-container_2'>
            <div className='cart__sum-text'>Товары ({cartProducts.length})</div>
            <div className='cart__sum-text'>{total} &#8381;</div>
          </div>
          <div className='cart-container_2'>
            <div className='cart__sum-text'>Скидка магазина</div>
            <div className='cart__sum-text'>- {shopDiscount} &#8381;</div>
          </div>
          <div className='cart-container_2'>
            <p className='cart__sum-header'>Итого:</p>
            <div className='cart__sum'>{totalWithDiscount} &#8381;</div>
          </div>
          <p className='cart__sum-caption'>Без учета стоимости доставки</p>
          <button type='submit' onClick={proceedToOrderingPage} name='cart-submit' className='cart__submit-button hover_type_normal pointer'>Перейти к оформлению</button>
        </form>
      </div>
      )}
      {isSmallScreen && (
        <div className='cart__summary'>
        <form className='cart__form'>
          <h2 className='cart__subheader'>Сумма заказа</h2>
          <div className='cart-container_2'>
            <div className='cart__sum-text'>Товары ({cartProducts.length})</div>
            <div className='cart__sum-text'>{total} &#8381;</div>
          </div>
          <div className='cart-container_2'>
            <div className='cart__sum-text'>Скидка магазина</div>
            <div className='cart__sum-text'>- 1 000 &#8381;</div>
          </div>
          <div className='cart-container_2'>
            <p className='cart__sum-header'>Итого:</p>
            <div className='cart__sum'>{total} &#8381;</div>
          </div>
          <p className='cart__sum-caption'>Без учета стоимости доставки</p>
          <button type='submit' name='cart-submit' className='cart__submit-button hover_type_normal pointer'>Перейти к оформлению</button>
        </form>
        {/* РЕАЛИЗОВАТЬ ПЛАВАЮЩИЙ-ПРЯЧУЩИЙСЯ КОМПОНЕНТ ПОЗЖЕ */}
        {/* <div className='cart__mobile-area'>
          <div className='cart__sum_mobile'>{total} &#8381;</div>
          <button type='submit' name='cart-submit' className='cart__submit-button_mobile hover_type_normal pointer'>К оформлению</button>
        </div> */}
      </div>
      
      )}
      </div>
      </main>
     <MobileNavBar
     onBurger={onBurgerClick} 
     isBurgerOpen={isBurgerOpen} 
     onBurgerClose={onBurgerClose}
     cart={cart}/>
    </>
  )
}

// Не реализовано: 
//  - работа чекбоксов
//  - скидка 
//  - глав-чекбокс 
//  - кнопки в избранное и удалить
