import Crumbs from "../Crumbs/Crumbs";
import Header from "../Header/Header";
import Catalog from "../Catalog/Catalog";
import MobileNavBar from "../MobileNavBar/MobileNavBar";
import Tags from "../Tags/Tags";
import { useEffect, useState } from 'react';


export default function CatalogPage({ onCardClick, onLikeClick, products, setProducts, savedProducts, isLoggedIn, terms, onCatClick, category, tags, onTagClick, onBurgerClick, onBurgerClose, isBurgerOpen, search, setSearch, orders, cart, sortBy, setSortBy, sortOrder, setSortOrder, fetchProducts, productFilters, setProductFilters, isLoadingMore, showLoadMore, catalogLoading, onColorClick, stones }) {

  useEffect(() => {
    document.title = "Каталог - Долина самоцветов"
  }, []);

  // const location = useLocation();
  // const queryParams = new URLSearchParams(location.search);

  // access query parameters
  // const paramValue = queryParams.get("paramName");

  return (
    <>
    {/* <Crumbs/> */}
    {/* <div className='catalog_title'>Каталог</div> */}
    {productFilters.search && <div className='search_results_title'>Результаты поиска по запросу "{productFilters.search}"</div>}
    <Tags 
      terms={terms} 
      tags={terms.tags} 
      categories={terms.categories} 
      onTagClick={onTagClick}
      activeCategory={category}
      activeTags={tags}
      sortBy={sortBy}
      setSortBy={setSortBy}
      sortOrder={sortOrder}
      setSortOrder={setSortOrder}
      productFilters={productFilters}
      setProductFilters={setProductFilters}
      onColorClick={onColorClick}
    />
    <Catalog
      onCardClick={onCardClick}
      onLikeClick={onLikeClick}
      products={products}
      stones={stones}
      setProducts={setProducts}
      savedProducts={savedProducts}
      isLoggedIn={isLoggedIn}
      fetchProducts={fetchProducts}
      productFilters={productFilters}
      setProductFilters={setProductFilters}
      isLoadingMore={isLoadingMore}
      showLoadMore={showLoadMore}
      catalogLoading={catalogLoading}
      onCatClick={onCatClick}
    />
    <MobileNavBar 
    onBurger={onBurgerClick} 
    isBurgerOpen={isBurgerOpen} 
    onBurgerClose={onBurgerClose}
    cart={cart}
    onCatClick={onCatClick}
    orders={orders}/>
    </>
  )
}
