// import React, { useState } from 'react';
// import { useDropzone } from 'react-dropzone';
import './FileUploader.css';
import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';

const FileUploader = () => {
  const [files, setFiles] = useState([]);

  const onDrop = useCallback(acceptedFiles => {
    setFiles(prevFiles => [
      ...prevFiles,
      ...acceptedFiles.map(file =>
        Object.assign(file, {
          preview: URL.createObjectURL(file)
        })
      )
    ]);
  }, []);

  const onDragOver = e => {
    e.preventDefault();
  };

  const onDropFile = (e, index) => {
    const sourceIndex = e.dataTransfer.getData('index');
    const destinationIndex = index;
    const newFiles = [...files];
    const movedFile = newFiles.splice(sourceIndex, 1)[0];
    newFiles.splice(destinationIndex, 0, movedFile);
    setFiles(newFiles);
  };

  const { getRootProps, getInputProps } = useDropzone({ onDrop });

  return (
    <div>
      <div {...getRootProps()} style={dropzoneStyle}>
        <input {...getInputProps()} />
        <p>Перетащите файлы сюда или кликните для выбора</p>
      </div>
      <div style={fileListStyle}>
        {files.map((file, index) => (
          <div
            key={file.name}
            onDragOver={onDragOver}
            onDrop={e => onDropFile(e, index)}
            draggable
            onDragStart={e => {
              e.dataTransfer.setData('index', index);
            }}
            style={fileItemStyle}
          >
            <img src={file.preview} alt="Preview" style={{ width: '100px' }} />
            <p>{file.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

const dropzoneStyle = {
  border: '2px dashed #cccccc',
  borderRadius: '4px',
  padding: '20px',
  textAlign: 'center',
  cursor: 'pointer',
};

const fileListStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '10px',
};

const fileItemStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  padding: '10px',
  border: '1px solid #cccccc',
  borderRadius: '4px',
};

export default FileUploader;
