import loadingIcon from '../../images/loading.svg';
import videoIcon from '../../images/play-circle.svg';
import checkmarkIcon from '../../images/check-circle.svg';
import './FileUploader.css';
import React, { useState, useCallback, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';
import adminApi from '../../utils/adminApi';
import { DEFAULT_COLOR_PALETTE } from '../../utils/constants';
import { type } from '@testing-library/user-event/dist/type';

function FileUploader({ images, setImages, videos, setVideos, buttonColor, setButtonColor, editing }) {

  const [suggestedColors, setSuggestedColors] = useState(DEFAULT_COLOR_PALETTE);
  const [filesLoading, setFilesLoading] = useState(false);
  const [parentBg, setParentBg] = useState('#ebe9e9');

  const onDrop = useCallback(async acceptedFiles => {
    let numberOfFilesLoading = acceptedFiles.length;
    setFilesLoading(numberOfFilesLoading);

    for (let i = 0; i < acceptedFiles.length; i++) {
      const file = acceptedFiles[i];
      if (file.type.slice(0, 5) == 'video') {
        const response = await adminApi.uploadVideo(file)
        setVideos(prevVideos => [...prevVideos, { 
          attachmentId: (Math.floor(Math.random() * (10000000001 - 1000001 + 1)) + 1000001),
          productId: 0,
          path: "temp/" + response.file
        }]);
      } else {
        const response = await adminApi.uploadImage(file)
        setImages(prevImages => [...prevImages, {
          attachmentId: (Math.floor(Math.random() * (10000000001 - 1000001 + 1)) + 1000001),
          productId: 0,
          path: "temp/" + response.file,
          colors: response.colors
        }]);
      }
      numberOfFilesLoading -= 1;
      setFilesLoading(numberOfFilesLoading);
    }

  }, []);


  const { getRootProps, getInputProps } = useDropzone({ onDrop, multiple: true });

  // updating colors
  useEffect(() => {
    if (images[0] && images[0].colors) {
      setSuggestedColors(images[0].colors);
    } else {
      setSuggestedColors(DEFAULT_COLOR_PALETTE);
    }
  }, [images]);

  const colors = suggestedColors.map((color, index) => (
    <div
      onMouseEnter={() => setParentBg(color)}
      onClick={() => images[0] && setButtonColor(color)}
      onMouseLeave={() => setParentBg('#ebe9e9')}
      style={{ backgroundColor: color, width: '100%', height: '35px', display: 'flex', justifyContent: 'center' }} data-id={color} key={index}>
    </div>
  ));

  // handling image order change
  const handleDragStart = (e, index) => {
    e.dataTransfer.setData('index', index);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e, newIndex) => {
    e.preventDefault();
    const oldIndex = e.dataTransfer.getData('index');
    const newImages = [...images];
    const movedItem = newImages.splice(oldIndex, 1)[0];
    newImages.splice(newIndex, 0, movedItem);
    setImages(newImages);
  };

  const imgThumbs = images.map((image, index) => (
    <div
      className='thumbnail'
      key={image.path}
      draggable
      onDragStart={(e) => handleDragStart(e, index)}
      onDragOver={handleDragOver}
      onDrop={(e) => handleDrop(e, index)}
    >
      <img src={"https://dolina.shop/photos/" + image.path} alt={image.path} className='thumbnail-img' />
      <button className='thumbnail-button' onClick={() => removeImage(image.path)}></button>
    </div>
  ));


  const videoThumbs = videos.map((video) => (
    <div
      className='thumbnail'
      key={video.path}
    >
      <video src={"https://dolina.shop/videos/" + video.path} />
      <button className='thumbnail-button' onClick={() => removeVideo(video.path)}></button>
      <img src={videoIcon} className='video-icon' />
    </div>

  ));


  const removeImage = (path) => {
    setImages(prevImages => prevImages.filter(image => image.path !== path));
  };

  const removeVideo = (path) => {
    setVideos(prevImages => prevImages.filter(video => video.path !== path));
  };


  const parentStyle = images[0] ? { backgroundColor: parentBg } : {};


  return (
    <>
      <div className='block'>
        <div {...getRootProps({ className: 'dropzone' })}>
          <input {...getInputProps()} />
          <p>Сюда можно перетащить фото и видео <br />(или кликнуть и выбрать)</p>
        </div>
        <aside className='thumbnail-div'>
          {imgThumbs}
          {videoThumbs}
          {filesLoading > 0 && Array(filesLoading).fill().map((_, index) => (
            <div className='loading' key={index}>
              <img src={loadingIcon} className='loading_icon' alt='Loading Icon' />
              <div className='loading_trash_substitute' />
            </div>
          ))}
        </aside>
      </div>
      <div className='block suggested_colors_container' style={parentStyle}>
        <h3 className={`transition ${parentBg != "#ebe9e9" && images[0] ? 'white-font' : ''}`}>Цвет кнопки</h3>
        {buttonColor && (<div className='buttonColor' style={{ backgroundColor: buttonColor }}><img src={checkmarkIcon} /></div>)}
        <div className='suggestedColors'>
          {colors}
        </div>
      </div>
    </>
  );
};

export default FileUploader;
