import './Product.css';
import Header from '../Header/Header';
import Crumbs from '../Crumbs/Crumbs';
import SizeCard from '../SizeCard/SizeCard';
import productsApi from '../../utils/Api';
import { useState, useEffect } from 'react';

export default function Product({ productData }) {

  let temporaryPic = productData.mainPicture;
  temporaryPic = temporaryPic.replace(/'/g, ''); // kolkhoz, исправить на бэке
  let temporarySizes = [
    15, 15.5, 16, 16.5, 17, 17.5, 18, 18.5 // времянка до получения массива с размерами колец
  ]
 


return (
  <>
  <Header/>
  <Crumbs/>

  <main className='product'>
    <h1 className='product__title border-red'>{productData.title}</h1>
    <div className='product__container'>
      <div className='product_block_1 border-red'>
        <img className='mainPic' src={temporaryPic}></img>
      </div>
      <div className='product_block_2 border-red'>

        <section className='product__sizes-container'>
        <h2 className='product__subtitle'>Выберите размер:</h2>
        <ul className='product__sizes'>
          {
            temporarySizes.map((size) => (
              <SizeCard size={size}/>
            ))
          }
        </ul>
        </section>

        <section className='product__chars-container'>
          <h2 className='product__subtitle'>Характеристики</h2>

          <table className='product__table'>
            <tbody className='product_font_main'>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Камень</th>
                <div></div>
                <td className='product__table-right'>Норм камень</td>
              </tr>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Металл</th>
                <div></div>
                <td className='product__table-right'>{productData.metalType || 'Мельхиор'}</td>
              </tr>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Вес</th>
                <div></div>
                <td className='product__table-right'>{productData.weight} гр</td>
              </tr>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Длина изделия</th>
                <div></div>
                <td className='product__table-right'>120 см</td>
              </tr>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Происхождение камня</th>
                <div></div>
                <td className='product__table-right'>{productData.stoneOrigin || 'Урал'}</td>
              </tr>

              <tr className='product__table-unit'>
                <th className='product__table-left'>Страна производства</th>
                <div></div>
                <td className='product__table-right'>Россия</td>
              </tr>

            </tbody>
          </table>

        </section>
        <section className='product__description-container'>
          <h2 className='product__subtitle'>Описание</h2>
          <p className='product__description product_font_main'>{productData.text}</p>
        </section>
      </div>
      <div className='product_block_3'>
        <div className=''>
          <form className='product__form'>
            <h1 className='product__title'>{productData.title}</h1>
            <span className='product__price'>{productData.price} &#8381;</span>
            <span className=''>В наличии {productData.unique} шт.</span>
            <div className='product__buttons'>
              <button type='submit' className='product__submit-button'>Добавить в корзину</button>
              {/* <button type='button' className='product__like-button'></button> */}
            </div>
          </form>
        </div>
      </div>
    </div>
  </main>

  </>
)
}

// Что в товаре: 
// - тайтл +
// - цена +
// - артикул
// - кол-во в наличии +
// - "просматривают"
// - кнопка в корзину +
// - кнопка в избранное +
// - "все товары категории" !
// - "все товары из того-то" !
// - камень +
// - кнопка поделиться !
// - кнопка сообщить об ошибке wb !
// - гарантия возврата 
// - приблизительная дата доставки?

// - если кольцо, размеры +
// - страна происхождения камня +
// - металл + 
// - страна изготовления +
// - вес + 
// - размер кабошона
// - длина изделия +
// - натуральный камень !
// - в единственном экземпляре !
