import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './MainBanner.css';
import { useMediaQuery } from 'react-responsive';
import ban1 from '../../images/ban1.jpg';
import ban1m from '../../images/ban1_m.jpg';
import ban2 from '../../images/ban2.jpg';
import ban2m from '../../images/ban2_m.jpg';
import ban3 from '../../images/ban3.jpg';
import ban3m from '../../images/ban3_m.jpg';
import ban4 from '../../images/ban4.jpg';
import ban4m from '../../images/ban4_m.jpg';

export default function MainBanner() {
  const settings = {
    dots: true, // Показать точки навигации
    infinite: true, // Бесконечный цикл
    speed: 500, // Скорость анимации
    slidesToShow: 1, // Показывать по одному слайду
    slidesToScroll: 1, // Скроллить по одному слайду
    autoplay: true, // Автоматическое пролистывание
    autoplaySpeed: 5000, // Скорость автоматического пролистывания
    arrows: true, // Показать стрелки навигации
  };

  const isBigScreen = useMediaQuery({ minWidth: 768 }); // надо бы перенести в константы
  const isSmallScreen = useMediaQuery({ maxWidth: 767 });

  return (
  <>
  {isBigScreen && 
  <div className='main-banner'>
      <h2> Banner </h2>
      <Slider {...settings}>
        <div>
          <img src={ban1} alt="Slide 1" />
        </div>
        <div>
          <img src={ban2} alt="Slide 2" />
        </div>
        <div>
          <img src={ban3} alt="Slide 3" />
        </div>
        <div>
          <img src={ban4} alt="Slide 4" />
        </div>
        {/* Добавьте столько слайдов, сколько необходимо */}
      </Slider>
    </div>
}

  {isSmallScreen && 
  <div className='main-banner'>
      {/* <h2> Banner </h2> */}
      <Slider {...settings}>
      <div>
          <img src={ban1m} alt="Slide 1" />
        </div>
        <div>
          <img src={ban2m} alt="Slide 2" />
        </div>
        <div>
          <img src={ban3m} alt="Slide 3" />
        </div>
        <div>
          <img src={ban4m} alt="Slide 4" />
        </div>
        {/* Добавьте столько слайдов, сколько необходимо */}
      </Slider>
    </div>
}
  </>
    
  );
}
