import './Profile.css';
import React, { useState } from 'react';
import Header from '../Header/Header';
import productsApi from '../../utils/Api';
import MobileNavBar from "../MobileNavBar/MobileNavBar";
import Skeleton from 'react-loading-skeleton';
import { useMediaQuery } from 'react-responsive';
import caretIcon from '../../images/caret-down.svg';

export default function Orders({ orders, terms, onCatClick, onBurgerClick, onBurgerClose, isBurgerOpen, cart }) {
  const isBigScreen = useMediaQuery({ minWidth: 771 }); // надо бы перенести в константы
  const isSmallScreen = useMediaQuery({ maxWidth: 770 });
  // console.log(orders)
  if (!orders) {
    return <div>Loading...</div>
  }

  function getDate(timestamp) {
    return new Date(timestamp * 1000).toLocaleDateString('ru-RU');
  }

  const [expandedOrders, setExpandedOrders] = useState({});

  const getStatusName = (index) => {
    switch (index) {
        case 0:
            return 'Новый';
        case 1:
            return 'Собираем заказ';
        case 2:
            return 'В пути';
        case 3:
            return 'Доставлен';
        case -1:
            return 'Отменён';
        default:
            return '';
    }
};

const getStatusClass = (index) => {
  switch (index) {
      case 0:
          return 'status-new';
      case 1:
          return 'status-collecting';
      case 2:
          return 'status-on-the-way';
      case 3:
          return 'status-delivered';
      case -1:
          return 'status-cancelled';
      default:
          return '';
  }
};



  const toggleOrderExpand = (orderId) => {
    setExpandedOrders((prev) => ({
      ...prev,
      [orderId]: !prev[orderId],
    }));
  };

  return (
    <>
  {isBigScreen && (
    <main className='orders'>
    <ul className='orders-list'>
      <h1 className='orders__title'>Заказы</h1>
      {
      !orders ? (
        
          <li className='order'>
            <Skeleton height={245} width="100%"/>
          </li>
        
      ) : (
        orders.length === 0 ? (
          <div>
              <p style={{margin: '0 15px'}}>Пока заказов нет</p>
            </div>
        ) : (
          orders.map((order) => (
            <li className='order' key={order.orderId}>
              <div className='flex_type_row order__head'>
                <h3 className='order__header'>Заказ №{order.orderId} от {getDate(order.createdAt)}</h3>
                {/* <p className='order__status'>{getStatusName(order.orderStatus)}</p> */}
                <p className={`order__status ${getStatusClass(order.orderStatus)}`}>{getStatusName(order.orderStatus)}</p>
                
              </div>
              <div className='flex_type_row order__body'>
                <div className='' style={{marginLeft:'15px'}}>
                  <div style={{display: 'flex', alignItems:'center'}}>
                  <p className='order__price'>{order.totalPrice} &#8381;</p>
                  {order.paid === 1 ? (
                    <p className='order_paid'>Оплачен</p>
                  ) : (
                    <p className='order_not-paid'>Не оплачен</p>
                  )}
                  
                  </div>
                  {order.tracking > 0 ? (
                    <p className='order__main-text'>Трек-номер: <span className='order_tracking-number'>{order.tracking}</span></p>
                  ) : (
                    null
                  )}
                
                  {/* <p className='order__number'>№{order.orderId}</p> */}
                  <p className='order__main-text'>Доставка: {`${order.city + ', ' + order.deliveryAddress}` || order.cdekOffice}</p>
                  
                   <p className='order__main-text'>Получатель: {order.fullName}</p>
                   <p className='order__main-text'>Комментарий к заказу: {order.comment}</p>
                  
                </div>
                <div className='order__products'>
                {order.orderedProducts.map((product) => (
                  <a key={product.id} href={`${'/product/' + product.slug + '-' + product.productId}`} target="_blank">
                    <img className='order__pic' alt={product.title} src={`https://dolina.shop/photos/${product.path.slice(0, -4) + `_preview.jpg`}`}></img>
                  </a>
                ))}
              </div>
              </div>
            </li>
          ))
        )
        

      )
      
      }
    </ul>
  </main>
  )}

  {isSmallScreen && (
    <main className='orders'>
    <ul className='orders-list'>
      <h1 className='orders__title'>Заказы</h1>
      {
      !orders ? (
        
          <li className='order'>
            <Skeleton height={245} width="100%"/>
          </li>
        
      ) : (
        orders.length === 0 ? (
          <div>
              <p style={{margin: '0 15px'}}>Пока заказов нет</p>
            </div>
        ) : (
          orders.map((order) => (
            <li className='order' key={order.orderId}>
              <div className='flex_type_row order__head'>
                <h3 className='order__header'>Заказ №{order.orderId} от {getDate(order.createdAt)}</h3>
                {/* <p className='order__status'>{getStatusName(order.orderStatus)}</p> */}
                <button onClick={() => toggleOrderExpand(order.orderId)}>
                      {expandedOrders[order.orderId] ? 'Свернуть' : 'Развернуть'}
                    </button>
                <p className={`order__status ${getStatusClass(order.orderStatus)}`}>{getStatusName(order.orderStatus)}</p>
                
              </div>
              <div className='flex_type_row order__body'>
                <div className='' style={{marginLeft:'15px'}}>
                  <div style={{display: 'flex', alignItems:'center'}}>
                  <p className='order__price'>{order.totalPrice} &#8381;</p>
                  {order.paid === 1 ? (
                    <p className='order_paid'>Оплачен</p>
                  ) : (
                    <p className='order_not-paid'>Не оплачен</p>
                  )}
                  
                  </div>
                  {order.tracking > 0 ? (
                    <p className='order__main-text'>Трек-номер: <span className='order_tracking-number'>{order.tracking}</span></p>
                  ) : (
                    null
                  )}
                
                  {/* <p className='order__number'>№{order.orderId}</p> */}
                  <p className='order__main-text'>Доставка: {`${order.city + ', ' + order.deliveryAddress}` || order.cdekOffice}</p>
                  
                   <p className='order__main-text'>Получатель: {order.fullName}</p>
                   <p className='order__main-text'>Комментарий к заказу: {order.comment}</p>
                  
                </div>
                {/* <div className='order__products order__products_visible'> */}
                {/* {order.orderedProducts.map((product) => (
                  <a key={product.id} href={`${'/product/' + product.slug + '-' + product.productId}`} target="_blank">
                    <img className='order__pic' alt={product.title} src={`https://dolina.shop/photos/${product.path.slice(0, -4) + `_preview.jpg`}`}></img>
                  </a>
                ))} */}
                {expandedOrders[order.orderId] && (
                      <div className='order__products order__products_visible'>
                        {order.orderedProducts.map((product) => (
                          <a key={product.id} href={`/product/${product.slug}-${product.productId}`} target="_blank" rel="noopener noreferrer">
                            <img
                              className='order__pic'
                              alt={product.title}
                              src={`https://dolina.shop/photos/${product.path.slice(0, -4) + `_preview.jpg`}`}
                            />
                          </a>
                        ))}
                      </div>
                    )}
              </div>
              {/* </div> */}
            </li>
          ))
        )
        

      )
      
      }
    </ul>
  </main>
  )}
    
    <MobileNavBar
      onBurger={onBurgerClick} 
      isBurgerOpen={isBurgerOpen} 
      onBurgerClose={onBurgerClose}
            cart={cart}
      orders={orders}
    />
    </>
  )
}

