import loadingIcon from '../../images/loading.svg';
import videoIcon from '../../images/play-circle.svg';
import './FileUploader.css';
import React, { useState, useCallback, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';
import adminApi from '../../utils/adminApi';

function FileUploader({ onAttach, onColorChoose }) {

  const [images, setImages] = useState([]);
  const [videos, setVideos] = useState([]);
  const [suggestedColors, setSuggestedColors] = useState(["#c6c6c6", "#c6c6c6", "#c6c6c6", "#c6c6c6", "#c6c6c6"]);
  const [imagesLoading, setImagesLoading] = useState(false);

  const onDrop = useCallback(async acceptedFiles => {
      setImagesLoading(true);
      const uploadedImages = [];
      const uploadedVideos = [];
      
      for (const file of acceptedFiles) {
        if (file.type.slice(0, 5) == 'video') {
    
          const response = await adminApi.uploadVideo(file)
          uploadedVideos.push({ file: response.file });

        } else {
    
          const response = await adminApi.uploadImage(file)
          uploadedImages.push({ file: response.file, colors: response.colors });
        }
      }
  
      setImagesLoading(false);

      setImages(prevImages => [...prevImages, ...uploadedImages]);
      setVideos(prevVideos => [...prevVideos, ...uploadedVideos]);
    
  }, []);


  const { getRootProps, getInputProps } = useDropzone({ onDrop, multiple: true });


  // updating colors
  useEffect(() => {
    if (images[0]) {
      setSuggestedColors(images[0].colors);
    } else {
      setSuggestedColors(["#c6c6c6", "#c6c6c6", "#c6c6c6", "#c6c6c6", "#c6c6c6"]);
    }
  }, [images]);

  const colors = suggestedColors.map((color, index) => (
    <label style={{ backgroundColor: color, width: '100%', height: '35px', display: 'flex', justifyContent: 'center' }} data-id={color} key={index}>
      <input type='radio' name='color' value={color} onChange={handleColorChoosing}></input>
    </label>
  ));



  function handleColorChoosing(e) {
    onColorChoose(e.target.value);
  }





  // handling image order change
  const handleDragStart = (e, index) => {
    e.dataTransfer.setData('index', index);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e, newIndex) => {
    e.preventDefault();
    const oldIndex = e.dataTransfer.getData('index');
    const newImages = [...images];
    const movedItem = newImages.splice(oldIndex, 1)[0];
    newImages.splice(newIndex, 0, movedItem);
    setImages(newImages);
  };


  const imgThumbs = images.map((image, index) => (
    <div
      className='thumbnail'
      key={image.file}
      draggable
      onDragStart={(e) => handleDragStart(e, index)}
      onDragOver={handleDragOver}
      onDrop={(e) => handleDrop(e, index)}
    >
      <img src={"https://dolina.shop/photos/temp/" + image.file} alt={image.file} className='thumbnail-img' />

      <button className='thumbnail-button' onClick={() => removeImage(image.file)}></button>
    </div>
  ));


  const videoThumbs = videos.map((video) => (
    <div
      className='thumbnail'
      key={video.file}
    >

    <video src={"https://dolina.shop/videos/temp/" + video.file} />
      <button className='thumbnail-button' onClick={() => removeVideo(video.file)}></button>
    <img src={videoIcon} className='video-icon' />
    </div>

  ));
  

  const removeImage = (file) => {
    setImages(prevImages => prevImages.filter(image => image.file !== file));
  };

  const removeVideo = (file) => {
    setVideos(prevImages => prevImages.filter(video => video.file !== file));
  };

  return (
    <div>
      <div {...getRootProps({ className: 'dropzone' })}>
        <input {...getInputProps()} />
        <p>Сюда можно перетащить фото и видео (или кликнуть и выбрать)</p>
      </div>
        <aside className='thumbnail-div' style={{ marginTop: '20px' }}>
        {imagesLoading && (
          <div className='loading'>
            <img src={loadingIcon} /> грузим, грузим
          </div>
        )}
        {imgThumbs}
        {videoThumbs}
      </aside>
      <h3>Цвет кнопки:</h3>
    <div className='suggestedColors'>
      {colors}
    </div>
    </div>
  );
};

export default FileUploader;
