import './Form.css';
import { Link, useLocation, useParams, useNavigate } from 'react-router-dom';
// import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from "react";
import { EMAIL_REGEXP } from '../../utils/constants';


export default function ChangePasswordForm({ onSubmit }) {
  const [password, setPassword] = useState('');
  const [passwordValidationError, setPasswordValidationError] = useState('');
  const [submitDisabled, setSubmitDisabled] = useState(true);

  const { token } = useParams();
  const navigate = useNavigate();

  if (!token) navigate('/'); 

  useEffect(() => {
    if (password.length > 6) {
      setSubmitDisabled(false);
    } else {
      setSubmitDisabled(true);
    }
  }, [password]);

  function handlePasswordChange(e) {
    setPassword(e.target.value);

    if (e.target.value.length > 6) {
      setPasswordValidationError('');
    } else {
      setPasswordValidationError('Пароль должен быть длиннее 6 символов');
    }
  }

  function handleSubmit(e) {
    e.preventDefault();
    onSubmit(token, password)
    // if (EMAIL_REGEXP.test(email)) {
    //   setEmailValidationError('');
    //   // onRegistration(name, email, password);
    // } else {
    //   setEmailValidationError('Введён некорректный email');
    // }
  }


  return (
    <>
      <body className='form-page'>
        <div className="form">
          <section className='form__container'>
            <form name={`forgot-form`} onSubmit={handleSubmit}>
              <fieldset className="form__fieldset">
                <h1 className="form__title">Изменение пароля</h1>


                <label htmlFor="password" className='form__input-label'>Новый пароль</label>
                <input
                  className="form__input"
                  placeholder="Введите пароль"
                  type="password"
                  name="password"
                  required
                  minLength={6}
                  maxLength={30}
                  onChange={handlePasswordChange}
                  value={password || ''}
                ></input>

                <button type="submit"
                  name="submit"
                  className="form__submit-button hover_type_normal"
                disabled={submitDisabled}
                >Продолжить</button>
              </fieldset>
            </form>
          </section>
          <div id="buttonContainerId"></div>
        </div>

      </body>
    </>
  )
}
