import { useEffect, useState } from 'react';
import './App.css';
import MainPage from './pages/MainPage';
import CatalogPage from './pages/CatalogPage';
// import NewPage from './pages/NewPage';
// import SalePage from './pages/SalePage';
import ProductPage from './pages/ProductPage';
import ContactPage from './pages/ContactPage';
import RegisterForm from './Form/RegisterForm';
import LoginForm from './Form/LoginForm';
import ShoppingCart from './ShoppingCart/ShoppingCart';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import Wishlist from './Profile/Wishlist';
import ProtectedRoute from './ProtectedRoute';
import productsApi from '../utils/Api';
import usersApi from '../utils/UsersApi';
import NotFoundPage from './NotFoundPage/NotFoundPage';
import Profile from './Profile/Profile';
import Orders from './Profile/Orders';
import { Route, Routes, useNavigate, NavLink, useLocation, useSearchParams } from 'react-router-dom';
import BurgerMenu from './Header/BurgerMenu/BurgerMenu';
import MobileNavBar from './MobileNavBar/MobileNavBar';
import OrderingPage from './ShoppingCart/OrderingPage/OrderingPage';
import Header from './Header/Header';
import Footer from './Footer/Footer';
import LicensePage from './pages/LicensePage';
import AboutPage from './pages/AboutPage';
import SuccessPage from './ShoppingCart/SuccessPage/SuccessPage';
import ScrollToTop from './ScrollToTop';


function App() {

  const navigate = useNavigate();
  const location = useLocation();

  const [pageTitle, setPageTitle] = useState('Долина Самоцветов');
  const [productData, setProductData] = useState([]);
  const [products, setProducts] = useState([]);
  const [inputError, setInputError] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState((localStorage.getItem('jwt') ? true : false));
  const [currentUser, setCurrentUser] = useState({});
  const [savedProducts, setSavedproducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [cart, setCart] = useState([]);
  const [isProfilePopupOpen, setIsProfilePopupOpen] = useState(false);
  const [terms, setTerms] = useState([]);
  const searchParams = new URLSearchParams(location.search)
  const [category, setCategory] = useState('');
  const [tags, setTags] = useState('');
  const [search, setSearch] = useState('');
  const [isBurgerOpen, setIsBurgerOpen] = useState(false);
  // console.log(isBurgerOpen)
  
  const categoryQuery = searchParams.get('category');
  const tagsQuery = searchParams.get('tags');
  const searchQuery = searchParams.get('search');

  const [productFilters, setProductFilters] = useState({
    category: categoryQuery ?? '',
    tags: tagsQuery ? tagsQuery.split(',').map(id => parseInt(id)) : [],
    search: searchQuery ?? '',
    sortBy: 'default',
    offset: 0,
    pageSize: 24,
    seed: Math.floor(Math.random() * 99999)
  });

  
  useEffect(() => {
    document.title = pageTitle;
  }, [pageTitle]);

  const [offset, setOffset] = useState(0);
  const pageSize = 24;
  const [sortBy, setSortBy] = useState('');
  const [sortOrder, setSortOrder] = useState('');
  const [lastProductsRequest, setLastProductsRequest] = useState(0);
  const [isLoadingMore, setIsLoadingMore] = useState(false);
  const [showLoadMore, setShowLoadMore] = useState(false);
  const [catalogLoading, setCatalogLoading] = useState(false);
  
  // console.log(productFilters)

  function handleBurgerOpening() {
    setIsBurgerOpen(true);
  }

  function handleBurgerClosing() {
    setIsBurgerOpen(false);
  }

  function handleLogin(email, password) {
    setInputError('');
    usersApi.login(email, password)
      .then(() => {
        setIsLoggedIn(true);
        navigate('/');
      })
      .catch((err) => {
        if (err.status === 401) {
          setInputError('Неправильный логин или пароль')
        } else {
          setInputError('При авторизации произошла ошибка')
        }
      })
  }

  // console.log(products)

  function handleRegistration(name, email, password) {
    setInputError('');
    usersApi.register(name, email, password)
      .then(() => {
        handleLogin(email, password);
      })
      .catch((err) => {
        if (err.status === 409) {
          setInputError('Пользователь с таким email уже существует')
        } else {
          setInputError('При регистрации пользователя произошла ошибка')
        }
      })
  }

  function handleSignOut() {
    setIsProfilePopupOpen(false);
    setIsLoggedIn(false);
    localStorage.removeItem('jwt');
    navigate('/signin');
  }

  function handleUpdateUser(e) {
    usersApi.updateUserData(e)
      .then((res) => setCurrentUser(res));
    setIsProfilePopupOpen(false);
  }

  function handleChangeClick() {
    // console.log(isProfilePopupOpen)
    setIsProfilePopupOpen(true);
  }

  function handleClosePopupClick() {
    // console.log(isProfilePopupOpen)
    setIsProfilePopupOpen(false)
  }


  // КОРЗИНА
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      setCart(JSON.parse(savedCart));
    }
    // console.log('saved cart:', savedCart);
  }, []);

  function handleAddToCart(productId, checked, size) {
    const newCart = { ...cart };
    newCart[productId] = { checked, size }; // это типа как эррэй
    setCart(newCart);
    if (isLoggedIn) {
      // console.log(newCart)
      // патчим в корзину юзера
      usersApi.updateCart(JSON.stringify(newCart))
    } else {
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
    // console.log('new cart:', newCart);
  }

  function handleRemoveFromCart(productId) {
    const newCart = { ...cart };
    delete newCart[productId];
    setCart(newCart);
    if (isLoggedIn) {
      usersApi.updateCart(JSON.stringify(newCart))
    } else {
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
  }

  // function update(productId, checked, size) {
  //   const newCart = {...cart};
  //   newCart[productId] = {checked, size}; // это типа как эррэй
  //   setCart(newCart);
  //   if (isLoggedIn) {
  //     console.log(newCart)
  //     // патчим в корзину юзера
  //     usersApi.updateCart(JSON.stringify(newCart))
  //   } else {
  //     localStorage.setItem('cart', JSON.stringify(newCart));
  //   }
  // }

  // переход в товар
  function handleCardClick(newSlug) {
    console.log('хэндлер сработал, хотя не должен был')
  }

  // лайк
  function handleLikeClick(id) {
    if (isLoggedIn) {
      if (savedProducts.includes(id)) {
        usersApi.removeProduct(id)
          .then(() => {
            setSavedproducts(savedProducts.filter(item => item !== id));
          })
          .catch(err => console.log(err));
      } else {
        usersApi.saveProduct(id)
          .then(() => {
            setSavedproducts([id, ...savedProducts]);
          })
          .catch(err => console.log(err));
      }
    }
    else {
      navigate('/signin');
    }
  }


  // Аутентификация, ставит current-юзера, корзину
  useEffect(() => {
    const token = localStorage.getItem('jwt');
    const savedCart = localStorage.getItem('cart');

    if (token) {
      usersApi.checkToken(token)
        .then((res) => {
          return Promise.all([
            res,
            usersApi.getSavedProducts(),
            savedCart ? usersApi.updateCart(savedCart) : res.cart
          ]);
        })
        .then(([userRes, wishlist, updatedCart]) => {
          // userRes.wishlist = wishlist;
          setCurrentUser(userRes);
          // console.log(currentUser);
          setCart(JSON.parse(updatedCart));
          setIsLoggedIn(true);
        })
        .catch((err) => console.error('Error while updating cart:', err));
    }
  }, []);

  // ставит избранное и заказы
  useEffect(() => {
    if (isLoggedIn) {
      usersApi.getUsersProfile()
        .then((data) => {
          setSavedproducts(data[0]);
          setOrders(data[1]);
          setCurrentUser(data[2])
        
          // console.log(data)
        })
        .catch((err) => {
          console.log(err)
        })
    }
  }, [isLoggedIn])


  // Получаем продукты и рендерим товары
  //   useEffect(() => {
  //     productsApi.getAllProducts()
  //       .then((data) => {
  //         console.log(data);
  //         setProducts(data);
  //       }).catch((err) => {
  //         console.log(err);
  //       })
  //   }, []) // когда в скобках products, запросы идут бесконечно. Почему?

  // console.log(currentUser)



  // РУБРИКИ

  useEffect(() => {
    productsApi.getTerms()
      .then((data) => {
        setTerms(data);
      }).catch((err) => {
        console.log(err);
      })
  }, [])

  // useEffect(() => {
  //   console.log(products)
  // }, [products])



  // useEffect(() => {
  //   if (search.length > 0) {
  //     setProductFilters(prevFilters => ({
  //       ...prevFilters,
  //       category: '',
  //       tags: [],
  //       search: search
  //     }));
  //   }

  // }, [search]);

  function fetchProducts() {

      if (productFilters.offset > 0) {
        setIsLoadingMore(true);
      } else {
        setCatalogLoading(true);
      }

      productsApi.filterProducts(
        productFilters.category, 
        productFilters.tags, 
        productFilters.search, 
        productFilters.offset, 
        productFilters.pageSize, 
        productFilters.sortBy, 
        productFilters.seed
      )
        .then((data) => {
          if (data.length > productFilters.pageSize) {
            setShowLoadMore(true);
          } else {
            setShowLoadMore(false)
          }

          if (productFilters.offset > 0) {
            setProducts(prevProducts => [...prevProducts, ...data.slice(0, productFilters.pageSize)]);
            setIsLoadingMore(false);
          } else {
            setProducts(data.slice(0, productFilters.pageSize));
            setCatalogLoading(false);
          }
        }).catch((err) => {
          console.log(err);
        })


  };


  
  useEffect(() => {

    // обновляем url просто
    if (window.location.pathname.includes("catalog")) { // kolkhoz

      if (productFilters.category) {
        // setProducts([]);
        searchParams.set('category', productFilters.category);
      } else {
        searchParams.delete('category');
      }

      if (productFilters.tags.length > 0) {
        // setProducts([]);
        searchParams.set('tags', productFilters.tags);
      } else {
        searchParams.delete('tags');
      }

      if (productFilters.search) {
        // setProducts([]);
        searchParams.set('search', productFilters.search);
      } else {
        searchParams.delete('search');
      }

      let newPath = '/catalog/?' + searchParams.toString();
      if (newPath != window.location.pathname + window.location.search) navigate(newPath);

      fetchProducts();

    }

  }, [productFilters]);



  function handleCategorySelection(categoryId) {
    // setProducts([]);
    setProductFilters(prevFilters => ({
      ...prevFilters,
      category: categoryId,
      tags: [],
      search: '',
      offset: 0
    }));
    if (categoryId) {
      navigate('/catalog/?category=' + categoryId);
    } else {
      navigate('/catalog/');
    }
  }

  function handleTagSelection(activeTags) {
    // setProducts([]);

    setProductFilters(prevFilters => ({
      ...prevFilters,
      tags: activeTags,
      search: '',
      offset: 0
    }));
  }

  const showHeader = location.pathname !== '/ordering' && !location.pathname.includes('/administration');

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        {!window.location.pathname.includes("administration") &&
          <div className='header_block_1'>
            <div className='header__container'>
              <div>
                <NavLink to='/about' className='navlink_size_s color_white' >О нас</NavLink>
                <NavLink to='/contacts' className='navlink_size_s color_white' >Контакты</NavLink>
              </div>
              <p className='.navlink_size_s color_white'>
                8 800 200 17 05</p>
            </div>
          </div>
        }
        <div className='content'>
          {showHeader && <Header
            terms={terms}
            onCatClick={handleCategorySelection}
            activeCategory={category}
            isBurgerOpen={isBurgerOpen}
            onBurgerClick={handleBurgerOpening}
            onBurgerClose={handleBurgerClosing}
            search={search}
            setSearch={setSearch}
            cart={cart}
            orders={orders}
            productFilters={productFilters}
            setProductFilters={setProductFilters}
          />}
          <ScrollToTop/>
          <Routes>
            <Route path="/" element={<MainPage
              onCardClick={handleCardClick}
              onLikeClick={handleLikeClick}
              products={products}
              setProducts={setProducts}
              savedProducts={savedProducts}
              isLoggedIn={isLoggedIn}
              terms={terms}
              onCatClick={handleCategorySelection}
              category={category}
              cart={cart}
              orders={orders}
              fetchProducts={fetchProducts}
              onBurgerClick={handleBurgerOpening}
              onBurgerClose={handleBurgerClosing}
            />} />
            <Route path="/contacts" element={<ContactPage
              terms={terms}
              onCatClick={handleCategorySelection}
              category={category}
              cart={cart}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
              onBurgerClose={handleBurgerClosing}
            />} />
            <Route path="/license" element={<LicensePage
              terms={terms}
              onCatClick={handleCategorySelection}
              category={category}
              cart={cart}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
              onBurgerClose={handleBurgerClosing}
            />} />
            <Route path="/about" element={<AboutPage
              terms={terms}
              onCatClick={handleCategorySelection}
              category={category}
              cart={cart}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
              onBurgerClose={handleBurgerClosing}
            />} />
            <Route path="/me/orders/success/:orderId?" element={<SuccessPage
              terms={terms}
              onCatClick={handleCategorySelection}
              category={category}
              cart={cart}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
              onBurgerClose={handleBurgerClosing}
            />} />
            <Route path="/catalog/" element={<CatalogPage
              onCardClick={handleCardClick}
              onLikeClick={handleLikeClick}
              products={products}
              setProducts={setProducts}
              savedProducts={savedProducts}
              isLoggedIn={isLoggedIn}
              terms={terms}
              category={category}
              tags={tags}
              onCatClick={handleCategorySelection}
              onTagClick={handleTagSelection}
              cart={cart}
              orders={orders}
              sortBy={sortBy}
              setSortBy={setSortBy}
              sortOrder={sortOrder}
              setSortOrder={setSortOrder}
              fetchProducts={fetchProducts}
              onBurgerClick={handleBurgerOpening}
              productFilters={productFilters}
              setProductFilters={setProductFilters}
              onBurgerClose={handleBurgerClosing}
              isLoadingMore={isLoadingMore}
              showLoadMore={showLoadMore}
              catalogLoading={catalogLoading}
            />} />
            <Route path="/product/:newSlug" element={<ProductPage
              productData={productData}
              onLikeClick={handleLikeClick}
              savedProducts={savedProducts}
              isLoggedIn={isLoggedIn}
              onAddClick={handleAddToCart}
              cart={cart}
              orders={orders}
              onCardClick={handleCardClick}
              terms={terms}
              onCatClick={handleCategorySelection}
              onBurgerClick={handleBurgerOpening}
              onBurgerClose={handleBurgerClosing}
            />} />
            <Route path="/signup" element={<RegisterForm
              onRegistration={handleRegistration}
              inputError={inputError}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
            />} />
            <Route path="/signin" element={<LoginForm
              onLogin={handleLogin}
              inputError={inputError}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
            />} />
            <Route path="/me" element={<ProtectedRoute
              isLoggedIn={isLoggedIn}
              element={Profile}
              onUpdateUser={handleUpdateUser}
              onSignout={handleSignOut}
              onChangeClick={handleChangeClick}
              onCloseClick={handleClosePopupClick}
              savedProducts={savedProducts}
              orders={orders}
              cart={cart}
              isPopupOpen={isProfilePopupOpen}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
              onBurgerClick={handleBurgerOpening}
              onBurgerClose={handleBurgerClosing}
            />} />
            <Route path="/me/orders" element={<ProtectedRoute
              isLoggedIn={isLoggedIn}
              orders={orders}
              element={Orders}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
              cart={cart}
              onBurgerClick={handleBurgerOpening}
              onBurgerClose={handleBurgerClosing}
              isBurgerOpen={isBurgerOpen}
            />} />
            <Route path="/me/wishlist" element={<ProtectedRoute
              isLoggedIn={isLoggedIn}
              element={Wishlist}
              onCardClick={handleCardClick}
              onLikeClick={handleLikeClick}
              savedProducts={savedProducts}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
              cart={cart}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
              onBurgerClose={handleBurgerClosing}
            />} />

            <Route path="/cart" element={<ShoppingCart
              cart={cart}
              onCardClick={handleCardClick}
              isLoggedIn={isLoggedIn}
              onRemoveClick={handleRemoveFromCart}
              savedProducts={savedProducts}
              onLikeClick={handleLikeClick}
              terms={terms}
              category={category}
              onCatClick={handleCategorySelection}
              orders={orders}
              onBurgerClick={handleBurgerOpening}
              onBurgerClose={handleBurgerClosing}
            />} />
            <Route path="/ordering" element={<OrderingPage
              cart={cart}
              onCatClick={handleCategorySelection}
            // onCardClick={handleCardClick}
            isLoggedIn={isLoggedIn}
            />} />
            <Route path="*" element={<NotFoundPage />} />


          </Routes>
          {/* <MobileNavBar/>
          <BurgerMenu isOpen={isBurgerOpen} onClose={handleBurgerClosing}/> */}
        </div>
        <Footer />
      </div>
    </CurrentUserContext.Provider>
  )
}

export default App;

// Страницы:
// - главная
// - каталог
// - страница товара
// - личный кабинет
// - корзина
// - оформление заказа
// - избранное
// - покупки (заказы)
// - регистрация
// - авторизация
