import './Profile.css';
import Header from '../Header/Header';
import productsApi from '../../utils/Api';
import MobileNavBar from "../MobileNavBar/MobileNavBar";
import Skeleton from 'react-loading-skeleton';
import { useMediaQuery } from 'react-responsive';
import { useEffect } from "react";
import caretIcon from '../../images/caret-down.svg';
import usersApi from '../../utils/UsersApi';

export default function Orders({ orders, terms, onCatClick, onBurgerClick, onBurgerClose, isBurgerOpen, cart, isLoggedIn }) {

  useEffect(() => {
    document.title = 'Мои заказы — Долина самоцветов';
  }, []);

  const isBigScreen = useMediaQuery({ minWidth: 771 }); // надо бы перенести в константы
  const isSmallScreen = useMediaQuery({ maxWidth: 770 });
  console.log(orders)
  if (!orders) {
    return <div>Loading...</div>
  }

  function getDate(timestamp) {
    return new Date(timestamp * 1000).toLocaleDateString('ru-RU');
  }

  const getStatusName = (index) => {
    switch (index) {
      case 0:
        return 'Новый';
      case 1:
        return 'Собираем заказ';
      case 2:
        return 'В пути';
      case 3:
        return 'Готов к выдаче';
      case 4:
        return 'Получен';
      case -1:
        return 'Отменён';
      default:
        return '';
    }
  };

  const getStatusClass = (index) => {
    switch (index) {
      case 0:
        return 'status-new';
      case 1:
        return 'status-collecting';
      case 2:
        return 'status-on-the-way';
      case 3:
        return 'status-delivered';
      case 4:
        return 'status-done';
      case -1:
        return 'status-cancelled';
      default:
        return '';
    }
  };

  const getDeliveryTypeName = (index) => {
    switch (index) {
        case 1:
            return 'Самовывоз СДЭК';
        case 2:
            return 'Курьерская доставка СДЭК';
        case 3:
            return 'Почта';
        case 4:
            return 'Самовывоз Boxberry';
        case 5:
            return 'Курьерская доставка Boxberry';
        case 6:
            return '5post';
        default:
            return '';
    }
};

  function handlePayClick(orderId) {

    usersApi.orderPay(orderId)
      .then((res) => {
        window.location.href = res.paymentUrl;
      })
  }

  return (
    <>
      {isBigScreen && (
        <main className='orders'>
          <ul className='orders-list'>
            <h1 className='orders__title'>Заказы</h1>
            {
              !orders ? (

                <li className='order'>
                  <Skeleton height={245} width="100%" />
                </li>

              ) : (
                orders.length === 0 ? (
                  <div>
                    <p style={{ margin: '0 15px' }}>Пока заказов нет</p>
                  </div>
                ) : (
                  orders.map((order) => (
                    <li className='order' key={order.orderId}>
                      <div className='flex_type_row order__head'>
                        <h3 className='order__header'>Заказ от {getDate(order.createdAt)}</h3>
                        <p className='order__total-price'>{order.totalPrice + order.deliveryPrice} &#8381;</p>
                        

                      </div>
                      <div className='order__body'>
                        <div className='order__main-info' style={{ marginLeft: '20px' }}>
                            {/* <p className={`order__status ${getStatusClass(order.orderStatus)}`}>{getStatusName(order.orderStatus)}</p> */}
                            {order.tracking > 0 ? (
                            <p className='order__main-text'>Трек-номер: <span className='order_tracking-number'>{order.tracking}</span></p>
                          ) : (
                            null
                          )}
                          <p className='order__main-text'>Номер заказа: {order.orderId}</p>
                          <p className='order__main-text'>{getDeliveryTypeName(order.deliveryType)} {order.deliveryOffice ? order.deliveryOffice : `${order.city + ', ' + order.deliveryAddress}`}</p>
                          {/* <p className='order__main-text'>Получатель: {order.fullName}</p> */}
                          {!order.comment ? (null) : (
                            <p className='order__main-text'>Комментарий к заказу: {order.comment}</p>
                          )}

                          <p className='order__main-text'>Стоимость доставки: {order.deliveryPrice} &#8381;</p>
                        

                            {order.orderStatus === 4
                              ? (null)
                              : (
                                order.paid === 1 ? (
                                  <p className='order_paid'>Оплачен</p>
                                ) : (
                                  (order.paymentType !== 3 ? <><p className='order_not-paid'>Не оплачен</p><span onClick={() => handlePayClick(order.orderId)} className='orders_pay'>Оплатить</span></> : <p className='order_not-paid'>Не оплачен</p>)
                                )
                              )}


                          {/* </div> */}
                          <p className={`order__status ${getStatusClass(order.orderStatus)}`}>{getStatusName(order.orderStatus)}</p>

                        </div>


                        <div className='order__products'>
                          {order.orderedProducts.map((product) => (
                            <div className='order__product-container'>
                              
                              <div className='order_flex_row'>
                              <a key={product.id} href={`${'/product/' + product.slug + '-' + product.productId}`} target="_blank" without rel="noreferrer">
                                <img className='order__pic' alt={product.title} src={`https://dolina.shop/photos/${product.path.slice(0, -4) + `_preview.jpg`}`}></img>
                              </a>
                              <div className=''>
                              <a className='cart-product__title' href={`${'/product/' + product.slug + '-' + product.productId}`} target="_blank" without rel="noreferrer"><h3 className='order__product-title'>{product.title}</h3></a>
                                {/* {cartDetails && <p className='cart-product__size'>Размер: {cartDetails.size || 1}</p>} */}
                               {(product.size > 0) ? <p className='cart-product__size'>Размер: {product.size}</p> : null}
                              </div>


                    
                              </div>
                              <p className='order__product-title'>{product.price} &#8381;</p>
                            </div>


                          ))}
                        </div>
                      </div>
                    </li>
                  ))
                )


              )

            }
          </ul>
        </main>
      )}

      {isSmallScreen && (
        <main className='orders'>
          <ul className='orders-list'>
            <h1 className='orders__title'>Заказы</h1>
            {
              !orders ? (

                <li className='order'>
                  <Skeleton height={245} width="100%" />
                </li>

              ) : (
                orders.length === 0 ? (
                  <div>
                    <p style={{ margin: '0 15px' }}>Пока заказов нет</p>
                  </div>
                ) : (
                  orders.map((order) => (
                    <li className='order' key={order.orderId}>
                      <div className='flex_type_row order__head'>
                        <h3 className='order__header'>Заказ №{order.orderId} от {getDate(order.createdAt)}</h3>
                        {/* <p className='order__status'>{getStatusName(order.orderStatus)}</p> */}
                        <p className={`order__status ${getStatusClass(order.orderStatus)}`}>{getStatusName(order.orderStatus)}</p>

                      </div>
                      <div className='flex_type_row order__body'>
                        <div className='' style={{ marginLeft: '15px' }}>
                          <div style={{ display: 'flex', alignItems: 'center' }}>
                            <p className='order__price'>{order.totalPrice} &#8381;</p>
                            {order.orderStatus === 4
                              ? (null)
                              : (order.paid === 1 ? (
                                <p className='order_paid'>Оплачен</p>
                              ) : (
                                (order.paymentType !== 3 ? <><p className='order_not-paid'>Не оплачен</p><span onClick={() => handlePayClick(order.orderId)} className='orders_pay'>Оплатить</span></> : <p className='order_not-paid'>Не оплачен</p>)
                              ))}

                          </div>
                          {order.tracking > 0 ? (
                            <p className='order__main-text'>Трек-номер: <span className='order_tracking-number'>{order.tracking}</span></p>
                          ) : (
                            null
                          )}

                          {/* <p className='order__number'>№{order.orderId}</p> */}
                          <p className='order__main-text'>Доставка: {`${order.city + ', ' + order.deliveryAddress}` || order.cdekOffice}</p>

                          <p className='order__main-text'>Получатель: {order.fullName}</p>
                          <p className='order__main-text'>Комментарий к заказу: {order.comment}</p>

                        </div>
                        <div className='order__products order__products_visible'>
                          {order.orderedProducts.map((product) => (
                            <a key={product.id} href={`${'/product/' + product.slug + '-' + product.productId}`} target="_blank" without rel="noreferrer">
                              <img className='order__pic' alt={product.title} src={`https://dolina.shop/photos/${product.path.slice(0, -4) + `_preview.jpg`}`}></img>
                            </a>
                          ))}
                        </div>
                      </div>
                    </li>
                  ))
                )


              )

            }
          </ul>
        </main>
      )}

      <MobileNavBar
        onBurger={onBurgerClick}
        isBurgerOpen={isBurgerOpen}
        onBurgerClose={onBurgerClose}
        cart={cart}
        orders={orders}
        onCatClick={onCatClick}
        isLoggedIn={isLoggedIn}
      />
    </>
  )
}

