import './Profile.css';
import Header from '../Header/Header';
import MobileNavBar from "../MobileNavBar/MobileNavBar";
import productsApi from '../../utils/Api';
import ProfilePopup from './ProfilePopup';
import { useNavigate, NavLink } from 'react-router-dom';
import { useMediaQuery } from 'react-responsive';
import { useState, useEffect, useContext } from "react";
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

export default function Profile({ savedProducts, orders, onUpdateUser, onSignout, onChangeClick, onCloseClick, isPopupOpen, terms, onCatClick, onBurgerClick, onBurgerClose, isBurgerOpen  }) {
  // const isBigScreen = useMediaQuery({ minWidth: 768 }); // надо бы перенести в константы
  // const isSmallScreen = useMediaQuery({ maxWidth: 767 });

  const currentUser = useContext(CurrentUserContext);
  // const [isProfilePopupOpen, setIsProfilePopupOpen] = useState(false);
  const [wishlist, setWishlist] = useState([]);
  let wishlistPreviews = wishlist.slice(-4);
  let ordersPreviews = orders.slice(-1);
  function getDate(timestamp) {
    return new Date(timestamp * 1000).toLocaleDateString('ru-RU');
  }

  useEffect(() => {
    productsApi.getProductsById([savedProducts])
      .then((res) => {
        setWishlist(res)
        // console.log(res)
      })
      .catch((err) => console.log(err))
  }, [savedProducts])

  
  useEffect(() => {
    console.log(isPopupOpen);
  }, [isPopupOpen]);


  
  // function onChangeClick2() {
  //   onChangeClick();
  // }


  return (
    <>
    <Header 
    terms={terms}
    onCatClick={onCatClick}
    isBurgerOpen={isBurgerOpen}
    onBurgerClose={onBurgerClose}
    />

    <main className='profile'>
      <h1 className='wishlist-header'>Личный кабинет</h1>
      <section className='user-info'>
        <div className='user-info__avatar'>
          <p className='user-info__avatar-caption'>M</p>
        </div>
        <div className='user-info__container'>
          <p className='user-info__name'>{currentUser.name}</p>
          <p className='user-info__email'>{currentUser.email}</p>
          <NavLink to='' onClick={onChangeClick} className='navlink_size_s '>Редактировать </NavLink>
        </div>
        {/* <NavLink to='/' className='navlink_size_s '>Редактировать </NavLink> */}
      </section>
      <div className='profile_flex'>
        <section className='profile__orders'>
          <NavLink to='/me/orders' className='profile__link'>

          
          <h2 className='profile__subheader'>Заказы &#8594;</h2>
          <ul className='profile__orders_ul'>
            {ordersPreviews.map((order) => (
            <li className='order'>
              <div className='flex_type_row order__head'>
                <h3 className='order__header'>Заказ от {getDate(order.createdAt)}</h3>
                <p className='order__price'>{order.totalPrice} &#8381;</p>
              </div>
              <div className='flex_type_row order__body'>
                <div className=''>
                  <p className='order__number'>№{order.orderId}</p>
                  <p className='order__status'>{order.orderStatus === 0 ? "Создан" : "нет"}</p>
                </div>
                <div className='order__products'>
                {order.orderedProducts.map((product) => (
                  <a href={`https://dolina.shop/product/${product.slug}`}>
                    <img className='order__pic' alt={product.title} src={`https://dolina.shop/photos/${product.path.slice(0, -4) + `_preview.jpg`}`}></img>
                  </a>
                ))}
                
              </div>
              </div>
            </li>
          ))}
        </ul>
        </NavLink>
        </section>
        <section className='profile__wishlist'>
          <NavLink to='/me/wishlist' className='profile__link'>
          <h2 className='profile__subheader'>Избранное &#8594;</h2>
            <ul className='profile__wishlist_ul'>
              {wishlistPreviews.map((product) => (
                <li className='profile__wishlist_li'>
                  <img className='profile__wishlist-pic' src={`https://dolina.shop/photos/${product.pictures[0].path.slice(0, -4) + `_preview.jpg`}`}></img>
                </li>
              ))}
              {(wishlist.length > 4) && 
                <li className='profile__wishlist_li'>{`+${savedProducts.length - 4}`}</li>
              } 
              {/* <li className='profile__wishlist_li'>{`+${savedProducts.length - 4}`}</li> */}
            </ul>
          </NavLink>
        </section>
      </div>
      <ProfilePopup isOpen={isPopupOpen} onClose={onCloseClick} onUpdateUser={onUpdateUser} onSignout={onSignout}/>
      <MobileNavBar/>
    </main>

    </>
  )
}

