import './Card.css';
import { useEffect, useState } from 'react';

export default function Card({ name, mainPicture, price, onCardClick, onLikeClick, id, pictures, isUnique, savedProducts }) {

  mainPicture = mainPicture.replace(/'/g, ''); // kolkhoz
  pictures = JSON.parse(pictures); // это должно быть в api 
  const [previewPicture, setPreviewPicture] = useState(mainPicture); //поднять стейт?
  const [isSaved, setIsSaved] = useState(false); //поднять стейт?
  // const isLiked = card.likes.some(i => i._id === currentUser._id);

  useEffect(() => {
    if (savedProducts.includes(id)) {
      setIsSaved(true);
    } else {
      setIsSaved(false);
    }
  }, [savedProducts])

  function handleClick() {
    onCardClick(id)
  }

  function handleLike() {
    onLikeClick(id)
  }

  // function onHover() {

  // }

  return (
    <li className='card pointer' key={id}>
      <div className='card__container'>
        <a href='#'>
          <div className='card__image-container' onMouseLeave={() => { setPreviewPicture(mainPicture) }}>
            <div className='card__previews'>
              {pictures.map((pic) => (
                <div className='card__preview-container' key={pictures.indexOf(pic)}> 
                  <div className='card__preview-mark'></div> 
                  <img className='card__preview' 
                  src={pic} 
                  onClick={handleClick}
                  onMouseOver={() => {
                    setPreviewPicture(pic)
                  }}
                ></img>
                </div>
              ))}
            </div>
            {/* <button type='button' className='card__save-button' onClick={handleLike}></button> */}
            {/* {console.log(isSaved)} */}
            <button type='button' className={({ isSaved }) => `${isSaved ? "card__save-button card__save-button_active" : "card__save-button"}`} onClick={handleLike}></button>
            {/* className={({ isSaved }) => `${isSaved ? "card__save-button card__save-button_active" : "card__save-button"}`} */}
            <div className='card__image-overlay'></div>
            <img className='card__image' src={previewPicture}/>
          </div>
        </a>
        <div className='card__description-area' onClick={handleClick}>
          <span className='card__price'>{price} &#8381;</span>
          <h2 className='card__header'>{name}</h2>
        </div>
      </div>
    </li>
  )
}
