  // получаем данные при первой загрузке страницы, один раз
  const searchParams = new URLSearchParams(location.search)
  const categoryQuery = searchParams.get('category');
  const tagsQuery = searchParams.get('tags');
  const [category, setCategory] = useState(categoryQuery);
  const [tags, setTags] = useState(tagsQuery ? tagsQuery.split(',').map(id => parseInt(id)) : '');
  const [isBurgerOpen, setIsBurgerOpen] = useState(false);
  // console.log(isBurgerOpen)

  function handleBurgerOpening() {
    setIsBurgerOpen(true);
    // console.log('fucking burger')
  }

  function handleBurgerClosing() {
    setIsBurgerOpen(false);
    console.log('fucking burger!')
  }

  function handleLogin(email, password) {
    setInputError('');
    usersApi.login(email, password)
      .then(() => {
        setIsLoggedIn(true);
        navigate('/', { replace: true }) // replace is messing up the history
      })
      .catch((err) => {
        if (err.status === 401) {
          setInputError('Неправильный логин или пароль')
        } else {
          setInputError('При авторизации произошла ошибка')
        }
      })
  }

  // console.log(products)

  function handleRegistration(name, email, password) {
    setInputError('');
    usersApi.register(name, email, password)
      .then(() => {
        handleLogin(email, password);
      })
      .catch((err) => {
        if (err.status === 409) {
          setInputError('Пользователь с таким email уже существует')
        } else {
          setInputError('При регистрации пользователя произошла ошибка')
        }
      })
  }

  function handleSignOut() {
    setIsLoggedIn(false);
    localStorage.removeItem('jwt');
    navigate('/signin', { replace: true }); // replace is messing up the history
  }

  function handleUpdateUser(e) {
    usersApi.updateUserData(e)
      .then((res) => setCurrentUser(res));
    setIsProfilePopupOpen(false);
  }

  function handleChangeClick() {
    // console.log(isProfilePopupOpen)
    setIsProfilePopupOpen(true);
  }

  function handleClosePopupClick() {
    // console.log(isProfilePopupOpen)
    setIsProfilePopupOpen(false)
  }


  // КОРЗИНА
  useEffect(() => {
    const savedСart = localStorage.getItem('cart');
    if (savedСart) {
      setCart(JSON.parse(savedСart));
    }
  }, []);

  function handleAddToCart(productId, checked, size) {
    const newCart = { ...cart };
    newCart[productId] = { checked, size }; // это типа как эррэй
    setCart(newCart);
    if (isLoggedIn) {
      // console.log(newCart)
      // патчим в корзину юзера
      usersApi.updateCart(JSON.stringify(newCart))
    } else {
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
  }

  function handleRemoveFromCart(productId) {
    const newCart = { ...cart };
    delete newCart[productId];
    setCart(newCart);
    if (isLoggedIn) {
      usersApi.updateCart(JSON.stringify(newCart))
    } else {
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
  }

  // function update(productId, checked, size) {
  //   const newCart = {...cart};
  //   newCart[productId] = {checked, size}; // это типа как эррэй
  //   setCart(newCart);
  //   if (isLoggedIn) {
  //     console.log(newCart)
  //     // патчим в корзину юзера
  //     usersApi.updateCart(JSON.stringify(newCart))
  //   } else {
  //     localStorage.setItem('cart', JSON.stringify(newCart));
  //   }
  // }

  // переход в товар
  function handleCardClick(newSlug) {
    navigate(`/product/${newSlug}`, { replace: true }); // replace is messing up the history
    // history.push ?
  }

  // лайк
  function handleLikeClick(id) {
    if (isLoggedIn) {
      if (savedProducts.includes(id)) {
        usersApi.removeProduct(id)
          .then(() => {
            setSavedproducts(savedProducts.filter(item => item !== id));
          })
          .catch(err => console.log(err));
      } else {
        usersApi.saveProduct(id)
          .then(() => {
            setSavedproducts([id, ...savedProducts]);
          })
          .catch(err => console.log(err));
      }
    }
    else {
      navigate('/signin', { replace: true }); // replace is messing up the history
    }
  }


  // Аутентификация, ставит current-юзера
  // useEffect(() => {
  //   const token = localStorage.getItem('jwt');
  //   const savedСart = localStorage.getItem('cart');
  //   // console.log(token)
  //   if (token) {
  //     usersApi.checkToken(token)
  //       .then((res) => {
  //         usersApi.getSavedProducts()
  //         .then((wishlist) => {
  //           res.wishlist = wishlist;
  //         });
  //       })

  //       .then((res) => {
  //         setCurrentUser(res);
  //           setCart(JSON.parse(res.cart));
  //           setIsLoggedIn(true);
  //       })
  //       .catch((err) => {
  //         console.log(err)
  //       })
  //   }
  // }, [isLoggedIn]);

  // Аутентификация, ставит current-юзера, корзину
  useEffect(() => {
    const token = localStorage.getItem('jwt');
    const savedCart = localStorage.getItem('cart');

    if (token) {
      usersApi.checkToken(token)
        .then((res) => {
          return Promise.all([
            res,
            usersApi.getSavedProducts(),
            savedCart ? usersApi.updateCart(savedCart) : res.cart
          ]);
        })
        .then(([userRes, wishlist, updatedCart]) => {
          // userRes.wishlist = wishlist;
          setCurrentUser(userRes);
          // console.log(currentUser);
          setCart(JSON.parse(updatedCart));
          setIsLoggedIn(true);
        })
        .catch((err) => console.error('Error while updating cart:', err));
    }
  }, []);

  // ставит избранное и заказы
  useEffect(() => {
    if (isLoggedIn) {
      usersApi.getUsersProfile()
        .then((data) => {
          setSavedproducts(data[0])
          setOrders(data[1])
          // console.log(data)
        })
        .catch((err) => {
          console.log(err)
        })
    }
  }, [isLoggedIn])


  // Получаем продукты и рендерим товары
  //   useEffect(() => {
  //     productsApi.getAllProducts()
  //       .then((data) => {
  //         console.log(data);
  //         setProducts(data);
  //       }).catch((err) => {
  //         console.log(err);
  //       })
  //   }, []) // когда в скобках products, запросы идут бесконечно. Почему?

  // console.log(currentUser)



  // РУБРИКИ

  useEffect(() => {
    productsApi.getTerms()
      .then((data) => {
        setTerms(data);
        console.log(data);
      }).catch((err) => {
        console.log(err);
      })
  }, [])

  useEffect(() => {

    // обновляем url просто
    if (window.location.pathname == "/catalog/") { // kolkhoz
      if (tags && tags.length > 0) {
        searchParams.set('tags', tags);
        navigate('/catalog/?' + searchParams.toString());
      } else {
        searchParams.delete('tags');
        navigate('/catalog/?' + searchParams.toString());
      }
    }

    productsApi.filterProducts(category ?? '', tags ?? '')
      .then((data) => {
        setProducts(data);
      }).catch((err) => {
        console.log(err);
      })

  }, [category, tags]);

  function handleCategorySelection(categoryId) {
    setCategory(categoryId);
    if (categoryId) {
      navigate('/catalog/?category=' + categoryId);
    } else {
      navigate('/catalog/');
    }
    setTags([]);
  }

  function handleTagSelection(activeTags) {
    setTags(activeTags);
  }


  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        {!window.location.pathname.includes("administration") &&
          <div className='header_block_1'>
            <div className='header__container'>
              <NavLink to='/contacts' className='navlink_size_s color_white' >Контакты</NavLink>
              <NavLink to='/' className='navlink_size_s color_white' >О нас</NavLink>
              <p className='.navlink_size_s color_white'>8 800 200 17 05</p>
            </div>
          </div>
        }
        <div className='content'>
          <Routes>
              <Route path="/" element={<MainPage
                onCardClick={handleCardClick}
                onLikeClick={handleLikeClick}
                products={products}
                savedProducts={savedProducts}
                isLoggedIn={isLoggedIn}
                terms={terms}
                onCatClick={handleCategorySelection}
                category={category}
                isBurgerOpen={isBurgerOpen}
                onBurgerClick={handleBurgerOpening}
                onBurgerClose={handleBurgerClosing}
              />} />
              <Route path="/contacts" element={<ContactPage
                terms={terms}
                onCatClick={handleCategorySelection}
                category={category}
                isBurgerOpen={isBurgerOpen}
                onBurgerClick={handleBurgerOpening}
                onBurgerClose={handleBurgerClosing}
              />} />
              <Route path="/catalog/" element={<CatalogPage
                onCardClick={handleCardClick}
                onLikeClick={handleLikeClick}
                products={products}
                savedProducts={savedProducts}
                isLoggedIn={isLoggedIn}
                terms={terms}
                category={category}
                tags={tags}
                onCatClick={handleCategorySelection}
                onTagClick={handleTagSelection}
                isBurgerOpen={isBurgerOpen}
                onBurgerClick={handleBurgerOpening}
                onBurgerClose={handleBurgerClosing}
              />} />
              <Route path="/product/:newSlug" element={<ProductPage
                productData={productData}
                onLikeClick={handleLikeClick}
                savedProducts={savedProducts}
                isLoggedIn={isLoggedIn}
                onAddClick={handleAddToCart}
                cart={cart}
                onCardClick={handleCardClick}
                terms={terms}
                onCatClick={handleCategorySelection}
                isBurgerOpen={isBurgerOpen}
                onBurgerClick={handleBurgerOpening}
                onBurgerClose={handleBurgerClosing}
              />} />
              <Route path="/signup" element={<RegisterForm
                onRegistration={handleRegistration}
                inputError={inputError}
                terms={terms}
                category={category}
                onCatClick={handleCategorySelection}
                isBurgerOpen={isBurgerOpen}
                onBurgerClick={handleBurgerOpening}
                onBurgerClose={handleBurgerClosing}
              />} />
              <Route path="/signin" element={<LoginForm
                onLogin={handleLogin}
                inputError={inputError}
                terms={terms}
                category={category}
                onCatClick={handleCategorySelection}
                isBurgerOpen={isBurgerOpen}
                onBurgerClick={handleBurgerOpening}
                onBurgerClose={handleBurgerClosing}
              />} />
              <Route path="/me" element={<ProtectedRoute
                isLoggedIn={isLoggedIn}
                element={Profile}
                onUpdateUser={handleUpdateUser}
                onSignout={handleSignOut}
                onChangeClick={handleChangeClick}
                onCloseClick={handleClosePopupClick}
                savedProducts={savedProducts}
                orders={orders}
                isPopupOpen={isProfilePopupOpen}
                terms={terms}
                category={category}
                onCatClick={handleCategorySelection}
                isBurgerOpen={isBurgerOpen}
                onBurgerClick={handleBurgerOpening}
                onBurgerClose={handleBurgerClosing}
              />} />
              <Route path="/me/orders" element={<ProtectedRoute
                isLoggedIn={isLoggedIn}
                orders={orders}
                element={Orders}
                terms={terms}
                category={category}
                onCatClick={handleCategorySelection}
                isBurgerOpen={isBurgerOpen}
                onBurgerClick={handleBurgerOpening}
                onBurgerClose={handleBurgerClosing}
              />} />
              <Route path="/me/wishlist" element={<ProtectedRoute
                isLoggedIn={isLoggedIn}
                element={Wishlist}
                onCardClick={handleCardClick}
                onLikeClick={handleLikeClick}
                savedProducts={savedProducts}
                terms={terms}
                category={category}
                onCatClick={handleCategorySelection}
                isBurgerOpen={isBurgerOpen}
                onBurgerClick={handleBurgerOpening}
                onBurgerClose={handleBurgerClosing}
              />} />

              <Route path="/cart" element={<ShoppingCart
                cart={cart}
                onCardClick={handleCardClick}
                isLoggedIn={isLoggedIn}
                onRemoveClick={handleRemoveFromCart}
                savedProducts={savedProducts}
                onLikeClick={handleLikeClick}
                terms={terms}
                category={category}
                onCatClick={handleCategorySelection}
                isBurgerOpen={isBurgerOpen}
                onBurgerClick={handleBurgerOpening}
                onBurgerClose={handleBurgerClosing}
              />} />
              <Route path="/ordering" element={<OrderingPage
                cart={cart}
                // onCardClick={handleCardClick}
                // isLoggedIn={isLoggedIn}
                // onRemoveClick={handleRemoveFromCart}
                // savedProducts={savedProducts}
                // onLikeClick={handleLikeClick}
                // terms={terms}
                // category={category}
                // onCatClick={handleCategorySelection}
                // isBurgerOpen={isBurgerOpen}
                // onBurgerClick={handleBurgerOpening}
                // onBurgerClose={handleBurgerClosing}
              />} />
            <Route path="*" element={<NotFoundPage />} />
            <Route path="/administration/product/:productId?" element={<ProductConstructor
              terms={terms}
            />} />

            <Route path="/administration/productlist" element={<ProductList
              terms={terms}
            />} />

          </Routes>
          {/* <MobileNavBar/>
          <BurgerMenu isOpen={isBurgerOpen} onClose={handleBurgerClosing}/> */}
        </div>
        <Footer/>
      </div>
    </CurrentUserContext.Provider>
  )
}

export default App;

