import './GalleryPopup.css';
import closeIcon from '../../../images/x.svg';

export default function GalleryPopup({ mainImage, images, isOpen, onClose }) {

  // console.log(images);
  return (
<div className={isOpen ? 'gallery-popup popup_opened' : 'gallery-popup'}>

  <div className='gallery-popup__container'>

  {/* {pictures.map((picture) => (
              <div className='' key={pictures.indexOf(picture)}> 
                <img className={`gallery__preview ${(pictures.indexOf(picture) === 0) && 'gallery_active-border'}`} src={picture} onClick={(evt) => {
                  // console.log('и че')
                  setIsPreview(picture);
                  document.querySelector('.gallery_active-border').classList.remove('gallery_active-border');
                  evt.target.classList.add('gallery_active-border');
                }}></img>
              </div>
            ))} */}

    {
      images.map((img) => { 
        return (
        <img className='gallery-popup__image' src={img} id={`${'img' + images.indexOf(img)}`}></img>
      )})
    }

    <div className='gallery-popup__preview-container'>
    {
      images.map((img) => {
        return (
          // сделать scroll into 
        // <a href={`${'#' + 'img' + images.indexOf(img)}`}>
        <span onClick={(evt) => {
            // document.querySelector('.gallery_active-border').classList.remove('gallery_active-border');
            evt.target.classList.add('gallery_active-border');
            document.querySelector('#' + 'img' + images.indexOf(img)).scrollIntoView({ behavior: "smooth" }); 
          }}>
          <img className='gallery-popup__image-preview' src={img}></img>
        </span>
        
      )})
    }
    </div>

  </div>
  <button type='button' className='close-button'>
    <img className='close-icon' src={closeIcon} onClick={onClose}></img>
  </button>
</div>
  )
}
