import React, { useState } from 'react';
import Header from '../Header/Header';
import './ProductConstructor.css';
import FileUploader from './FileUploader';

function ProductConstructor({ terms }) {

    const [productName, setProductName] = useState('');
    const [productDescription, setProductDescription] = useState('');
    const [category, setCategory] = useState([]);
    const [color, setColor] = useState([]);
    const [length, setLength] = useState('');
    const [weight, setWeight] = useState('');
    const [stoneOrigin, setStoneOrigin] = useState('');
    const [metalType, setMetalType] = useState('');
    const [stoneSize, setStoneSize] = useState('');
    const [production, setProduction] = useState('');
    const [price, setPrice] = useState('');
    const [salePrice, setSalePrice] = useState('');
    const [discount, setDiscount] = useState(0);
    const [articleNumber, setArticleNumber] = useState('');
    const [inStock, setInStock] = useState('');
    
    const [buttonColor, setButtonColor] = useState('');
    // const [stoneColor, setStoneColor] = useState([]);
    const [additionalProperties, setAdditionalProperties] = useState([]);
    // const [favorite, setFavorite] = useState(false);
    // const [status, setStatus] = useState('draft');
    const [radioValue, setRadioValue] = useState('');

    const [attachedFiles, setAttachedFiles] = useState([]);

    const [isUnique, setIsUnique] = useState(0);
    const [isIrid, setIsIrid] = useState(0);
    const [isMinimalism, setIsMinimalism] = useState(0);
    const [isMasterpiece, setIsMasterpiece] = useState(0);
    // console.log(attachedFiles)

    function handleFileAttachment(files, color ) {
      setAttachedFiles(files);
      setButtonColor(color);
      
    }

    const handleFormSubmit = (e) => {
        e.preventDefault();
    };



    //   function handleDrop(e) {
    //     e.preventDefault();
    //     const files = [...e.dataTransfer.files];
    //     setAttachedFiles([...attachedFiles, ...files]);
    //   };

    //   function handleFileInputChange(e) {
    //     const files = [...e.target.files];
    //     setAttachedFiles([...attachedFiles, ...files]);
    //   };

    //   function handleAttachButtonClick() {
    //     document.getElementById('fileInput').click();
    // };

    function handleCategoryChange(e) {
      setCategory(e.target.value);
      // они будут просто переключаться, разобраться
    }

    function handleColorChange(e) {
      setColor(e.target.value);
    }

    return (
      <>
      {/* <Header 
      terms={terms}
      activeCategory={category_alreadyDeclared}
      onCatClick={onCatClick}
      /> */}
      <main className='product-constructor'>
          <form className='add-form' onSubmit={handleFormSubmit}>
            {/* <h2>Добавление и редактирование товара</h2> */}
            <div className='form-container'>


            <div className="left-block">
                  <input className='input_size_long input-title' type="text" id="productName" value={productName} placeholder='Название товара' onChange={(e) => setProductName(e.target.value)} />
                  <textarea className='input_size_long input_size_big' id="productDescription" value={productDescription} placeholder='Описание товара' onChange={(e) => setProductDescription(e.target.value)} />
                  {/* для фоток и видео */}

                  <FileUploader onAttach={handleFileAttachment}/>

                  {/* <div className="input-files" onDrop={handleDrop} onDragOver={(e) => e.preventDefault()}>
                    <input type="file" id="fileInput" multiple style={{ display: 'none' }} onChange={handleFileInputChange} />
                    {attachedFiles.map((file, index) => (
                        <div className='file-preview' key={index}>
                          <div className=''>
                            <p>{file.name}</p>
                            <button className=''>удалить</button>
                          </div>
                        </div>
                    ))}
                  </div>
                  <button type="button" className='constructor-button' onClick={handleAttachButtonClick}>Прикрепить файлы</button> */}
              </div>


              <div className="right-block">
                <div className='status__container'>
                  <h3>Статус товара</h3>
                 
                  <label>
                      <input className='radio_type_normal' type="radio" value="1" checked={radioValue === '1'} onChange={() => setRadioValue('1')} />
                      Опубликовать
                  </label>
                  <label>
                      <input className='radio_type_normal' type="radio" value="0" checked={radioValue === '0'} onChange={() => setRadioValue('0')} />
                      Оставить черновик
                  </label>
                  {/* <input type="date" />  */}
                  {/* <a href="#" target="_blank">Посмотреть товар</a> */}
                  <button className='submit-adding' type="submit">Сохранить</button>
                </div>

                              {/* ИНПУТЫ ПО ЦЕНАМ, НАЛИЧИЮ */}
              <div className='chars__container'>
              <h3>Цена, наличие:</h3>
              <div className='chars__item'>
              <label htmlFor='input-price' className=''>Полная цена:</label>
              <input className='input_size_small' type="number" placeholder="3590" id='input-price' value={price} onChange={(e) => setPrice(e.target.value)} />
              </div>
              <div className='chars__item'>
              <label htmlFor='input-sale-price' className=''>Цена по скидке:</label>
              <input className='input_size_small' type="number" placeholder="Необязательно" id='input-sale-price' value={salePrice} onChange={(e) => setSalePrice(e.target.value)} />
              </div>
              <div className='chars__item'>
              <label htmlFor='input-discount' className=''>Размер скидки: {discount}%</label> {/*сделать чтобы автоматом заполнялась сейлпрайс*/}
              <input className='input_size_small' type="range" min="0" max="95" placeholder="0" id='input-discount' value={discount} onChange={(e) => setDiscount(e.target.value)} />
              </div>
              <div className='chars__item'>
              <label htmlFor='input-stock' className=''>Штук в наличии:</label>
              <input className='input_size_small' type="number" placeholder="0" id='input-stock' value={inStock} onChange={(e) => setInStock(e.target.value)} />
              </div>
              
              <h3>Размеры:</h3>
                  <input className='input_size_medium' type="text" placeholder="Начни вводить размер" />
              
              Размеры есть у колец и браслетов. У колец есть ещё и детальные размеры типа 15.6. Что же лучше сделать? Может, вообще текст?
              </div>
              </div>

      
              <div className="left-block">
                  {/* ЧЕКБОКСЫ КАТЕГОРИЙ  */}
                <h3>Категория товара:</h3>
                <div className='cats-div'>
                  
                    <label htmlFor='cat-beads' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={6} id='cat-beads' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Бусы</div> 
                    </label>
                    <label htmlFor='cat-complects' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={167} id='cat-complects' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Комплекты</div>
                    </label>
                    <label htmlFor='cat-rings' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={40} id='cat-rings' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Кольца</div>
                    </label>
                    <label htmlFor='cat-earrings' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={44} id='cat-earrings' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Серьги</div>
                    </label>
                    <label htmlFor='cat-pendants' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={102} id='cat-pendants' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Подвески</div>
                    </label>
                    <label htmlFor='cat-braceletes' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={25} id='cat-braceletes' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Браслеты</div>
                    </label>
                    <label htmlFor='cat-necklaces' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={28} id='cat-necklaces' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Колье</div>
                    </label>
                    <label htmlFor='cat-broaches' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={107} id='cat-broaches' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Броши</div>
                    </label>
                </div>
                
                <div className='stone-input__container'>
                  <h3>Камни:</h3>
                  <input className='input_size_long input_size_medium margin-zero' type="text" placeholder="Начни вводить название" />
                </div>
                
                {/* ИНПУТЫ ДЛЯ ХАРАКТЕРИСТИК ДЛЯ ТАБЛИЧКИ В ТОВАРЕ */}
                <div className='chars__container'>
                <h3>Характеристики:</h3>
               
                    
                </div>
                  
                  
                  <div className='chars__item'>
                    <label htmlFor='input-length' className=''>Длина в см:</label>
                    <input className='input_size_small' id='input-length' type="number" placeholder="22,5" value={length || ''} onChange={(e) => setLength(e.target.value)}/>
                  </div>

                  <div className='chars__item'>
                    <label htmlFor='input-weight' className=''>Вес в граммах:</label>
                    <input className='input_size_small' id='input-weight' type="number" placeholder="25" value={weight || ''} onChange={(e) => setWeight(e.target.value)}/>
                  </div>
                  
                  <div className='chars__item'>
                    <label htmlFor='input-origin' className=''>Происхождение камня:</label>
                    <input className='input_size_small' id='input-origin' type="text" placeholder="Якутия" value={stoneOrigin || ''} onChange={(e) => setStoneOrigin(e.target.value)}/>
                  </div>

                  <div className='chars__item'>
                    <label htmlFor='input-metal' className=''>Металл:</label>
                    <input className='input_size_small' id='input-metal' type="text" placeholder="Мельхиор" value={metalType || ''} onChange={(e) => setMetalType(e.target.value)}/>
                  </div>

                  <div className='chars__item'>
                    <label htmlFor='input-stonesize' className=''>Размер камня в см</label>
                    <input className='input_size_small' id='input-stonesize' type="text" placeholder="1,2 х 0,3" value={stoneSize || ''} onChange={(e) => setStoneSize(e.target.value)}/>
                  </div>

                  <div className='chars__item'>
                    <label htmlFor='input-production' className=''>Производство:</label>
                    <input className='input_size_small' id='input-production' type="text" placeholder="Россия, Екатеринбург" value={production || ''} onChange={(e) => setProduction(e.target.value)}/>
                  </div>

                  <div className='chars__item'>
                    <label htmlFor='input-sku' className=''>Артикул</label>
                    <input className='input_size_small' id='input-sku' type="number" placeholder="12345" value={articleNumber || ''} onChange={(e) => setArticleNumber(e.target.value)}/>
                  </div>
                 
                </div>
                <div className="right-block">
                <div className='chars__container'>
 
 <h3>Дополнительные рубрики</h3>
 <label className='checkbox-label' htmlFor='checkbox-unique'>
   <input className='checkbox_type_normal' type="checkbox" id='checkbox-unique' name="special-tags" checked={isUnique === '1'} onChange={() => setIsUnique(isUnique === '1' ? '0' : '1')}></input>
   В единственном экземпляре
 </label>
 <label className='checkbox-label' htmlFor='checkbox-irid'>
   <input className='checkbox_type_normal' type="checkbox" id='checkbox-irid' name="special-tags" checked={isIrid === '1'} onChange={() => setIsIrid(isIrid === '1' ? '0' : '1')}></input>
   С переливом
 </label>
 <label className='checkbox-label' htmlFor='checkbox-minimalism'>
   <input className='checkbox_type_normal' type="checkbox" id='checkbox-minimalism' name="special-tags" checked={isMinimalism === '1'} onChange={() => setIsMinimalism(isMinimalism === '1' ? '0' : '1')}></input>
   Минималистичное украшение
 </label>
 <label className='checkbox-label' htmlFor='checkbox-masterpiece'>
   <input className='checkbox_type_normal' type="checkbox" id='checkbox-masterpiece' name="special-tags" checked={isMasterpiece === '1'} onChange={() => setIsMasterpiece(isMasterpiece === '1' ? '0' : '1')}></input>
   Авторская работа
 </label>
</div>
                 <h3>Дополняющие товары (upsells)</h3>
                   <input className='input_size_medium' type="text" placeholder="Начни вводить название" />
                 
                 <h3>Выбрать цвета камней</h3>
                 <div className='colors-container'>
                     <label htmlFor='color-white' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'white'} id='color-white' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-white'>Белый</div> 
                     </label>
                     <label htmlFor='color-black' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'black'} id='color-black' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-black white-font'>Чёрный</div> 
                     </label>
                     <label htmlFor='color-burgundy' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'burgundy'} id='color-burgundy' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-burgundy white-font'>Тём.красный</div> 
                     </label>
                     <label htmlFor='color-brown' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'brown'} id='color-brown' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-brown white-font'>Коричневый</div> 
                     </label>
                     <label htmlFor='color-light-blue' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'light-blue'} id='color-light-blue' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-light-blue'>Голубой</div> 
                     </label>
                     <label htmlFor='color-blue' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'blue'} id='color-blue' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-blue'>Синий</div> 
                     </label>
                     <label htmlFor='color-yellow' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'yellow'} id='color-yellow' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-yellow'>Жёлтый</div> 
                     </label>
 
                     <label htmlFor='color-orange' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'orange'} id='color-orange' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-orange'>Оранжевый</div> 
                     </label>
                     <label htmlFor='color-light-green' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'light-green'} id='color-light-green' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-light-green'>Свет.зелёный</div> 
                     </label>
                     <label htmlFor='color-green' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'green'} id='color-green' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-green'>Зелёный</div> 
                     </label>
                     <label htmlFor='color-pink' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'pink'} id='color-pink' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-pink'>Розовый</div> 
                     </label>
                     <label htmlFor='color-red' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'red'} id='color-red' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-red'>Красный</div> 
                     </label>
                
                     <label htmlFor='color-grey' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'grey'} id='color-grey' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-grey'>Серый</div> 
                     </label>
                     <label htmlFor='color-purple' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'purple'} id='color-purple' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-purple'>Фиолетовый</div> 
                     </label>
                     <label htmlFor='color-pied' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'pied'} id='color-pied' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-pied'>Разноцветный</div> 
                     </label>
                     <label htmlFor='color-clear' className='cat-checkbox__label'>
                       <input className='cat-checkbox' type='checkbox' name='color' value={'clear'} id='color-clear' onChange={handleColorChange}/> 
                       <div className='color-pseudo-button pointer color-clear'>Прозрачный</div> 
                     </label>
                     </div>
               </div>
               
 
               <div className="left-block">
 



   {/* <button className='submit-adding' type="submit">Сохранить</button> */}
</div>
 
               <div className="right-block">

             </div>
              </div> 
              
            
          </form>
        </main>
    </>);
};

export default ProductConstructor;




// Нужно написать jsx компонент добавления товара на сайт, сверстать такую структуру: 

// блок 1 слева +
// - название
// - редактор текста 
// - прикрепить или перетащить фото и видео 

// блок 1 справа 
// - радио баттон статус товара: черновик или опубликовано
// - дата публикации
// - кнопка сабмита всей формы
// - посмотреть товар, ссылка на созданный товар открывается в новом окне

// блок 2 слева +
// - чекбоксы выбора категорий: Кольца, Бусы, Серьги, Подвески, Комплекты, Браслеты, Колье, Броши
// - окно поиска по тегам по камням, где можно найти и выбрать несколько
