import './BurgerMenu.css';
import { NavLink } from 'react-router-dom';
import closeIcon from '../../../images/x-bold.svg'

export default function BurgerMenu({ isOpen }) {

  return (
   <div className={(isOpen ? 'burger__popup burger__visible' : 'burger__popup')}>
    <div className='burger__container'>
      <nav className='burger__navigation'>
        <button type="button" className="burger__close-button"></button>
            <NavLink to='/' className='burger__link'>Каталог</NavLink>
            <NavLink to='/' className='burger__link'>Бусы</NavLink>
            <NavLink to='/' className='burger__link'>Серьги</NavLink>
            <NavLink to='/' className='burger__link'>Кольца</NavLink>
            <NavLink to='/' className='burger__link'>Подвески</NavLink>
            <NavLink to='/' className='burger__link'>Комплекты</NavLink>
            <NavLink to='/' className='burger__link'>Броши</NavLink>
            <NavLink to='/' className='burger__link'>Новинки</NavLink>
            <NavLink to='/' className='burger__link'>Распродажа</NavLink>
      </nav>
    </div>
   </div>
  )
}

