import './Tags.css';
import { useEffect, useState } from 'react';
import SearchForm from '../SearchForm/SearchForm';
import caret from '../../images/caret-down_mini.svg';
import { NavLink } from 'react-router-dom';

export default function Tags({ tags, categories, onTagClick, activeCategory, activeTags }) {
    const [selectedTags, setSelectedTags] = useState([]);
    const [filteredTags, setFilteredTags] = useState([]);
    const [openDropdownIndex, setOpenDropdownIndex] = useState(null);
    const specialIds = [47, 23, 292, 46, 105];
    const majorIds = [47, 23, 292, 46, 105];
    let specialTags = filteredTags.filter((tag) => specialIds.includes(tag.termId)); // таким образом перестает работать актуализация, типа инфа там не обновляется когда выбирают другие теги
    let stoneTags = filteredTags.filter((tag) => !specialIds.includes(tag.termId)); // перестает показывать кол-во
    // let majorTags = filteredTags.filter((tag) => specialIds.includes(tag.termId));
    // console.log(stoneTags)

    const handleTagToggle = (tag) => {
        let activeTagsTemporary = [];
        if (selectedTags.includes(tag)) {
            activeTagsTemporary = selectedTags.filter(selectedTag => selectedTag !== tag)
        } else {
            activeTagsTemporary = [...selectedTags, tag];
        }
        setSelectedTags(activeTagsTemporary);
        onTagClick(activeTagsTemporary);
    };

    const toggleDropdown = (index, event) => {
      event.stopPropagation(); // Останавливаем распространение события клика
      setOpenDropdownIndex(prevIndex => (prevIndex === index ? null : index)); // Открываем или закрываем текущий выпадающий список
  };
  

    useEffect(() => {
        if (tags) {
            setFilteredTags(tags.filter(tag => tag.count !== 0));
            if (activeCategory && activeCategory.length > 0) {
                const activeCategoryContent = categories.filter(item => item.termId == activeCategory)[0];
                setFilteredTags(tags.filter(tag => activeCategoryContent.category_tags.includes(tag.termId)));
                // console.log(filteredTags)
            }
        }
    }, [tags, activeCategory]);

    useEffect(() => {
        setSelectedTags([]);
    }, [activeCategory]);

    useEffect(() => {
        setSelectedTags(activeTags);
    }, []);

    useEffect(() => {
      const handleOutsideClick = (event) => {
          const dropdowns = document.querySelectorAll('.sorting-dropdown');
          for (const dropdown of dropdowns) {
              if (!dropdown.contains(event.target)) {
                  setOpenDropdownIndex(null); // Закрываем выпадающий список
              }
          }
      };
  
      // Добавляем обработчик события клика на документ
      document.addEventListener('click', handleOutsideClick);
  
      // Очищаем обработчик при размонтировании компонента
      return () => {
          document.removeEventListener('click', handleOutsideClick);
      };
  }, []);

    return (
        <section className='tag-section'>
            <div className='sorting-area'>
            <div className='sorting-dropdown'>
            <button type='button' className='sorting-dropdown__button pointer' onClick={(event) => toggleDropdown(0, event)}>Сортировка  <img src={caret} alt="caret" /></button>

                    <div className={`sorting-content ${openDropdownIndex === 0 ? 'sorting-content_visible' : ''}`}>
                        <div className="tags__dropdown">
                            
                        </div>
                    </div>
                </div>
                <div className='sorting-dropdown'>
                <button type='button' className='sorting-dropdown__button pointer' onClick={(event) => toggleDropdown(3, event)}>Особенности  <img src={caret} alt="caret" /></button>

                    <div className={`sorting-content ${openDropdownIndex === 3 ? 'sorting-content_visible' : ''}`}>
                        <div className="tags__dropdown">
                            {tags && specialTags.map(tag => (
                                <label key={tag.termId} className='tag-label'>
                                    <input
                                        type="checkbox"
                                        value={tag.termId}
                                        className='tag-button_dropdown'
                                        checked={selectedTags && selectedTags.some(selectedTag => selectedTag === tag.termId) ? 'checked' : ''}
                                        onChange={(event) => {
                                          event.stopPropagation(); // Остановка распространения события клика
                                          handleTagToggle(tag.termId);
                                      }}
                                    />
                                    <div className='tag-pseudo-checkbox pointer' onClick={(event) => event.stopPropagation()}>{tag.name}{!activeCategory && (<span className="tag-count" >{tag.count}</span>)} </div>
                                </label>
                            ))}
                            <button type='button' className='sorting-button'>Готово</button>
                        </div>
                        
                    </div>
                </div>
                <div className='sorting-dropdown'>
                <button type='button' className='sorting-dropdown__button pointer' onClick={(event) => toggleDropdown(5, event)}>Камни  <img src={caret} alt="caret" /></button>

                    <div className={`sorting-content ${openDropdownIndex === 5 ? 'sorting-content_visible' : ''}`}>
                        <div className="tags__dropdown">
                            <SearchForm />
                            {tags && stoneTags.map(tag => (
                                <label key={tag.termId} className='tag-label'>
                                    <input
                                        type="checkbox"
                                        value={tag.termId}
                                        className='tag-button_dropdown'
                                        checked={selectedTags && selectedTags.some(selectedTag => selectedTag === tag.termId) ? 'checked' : ''}
                                        onChange={(event) => {
                                          event.stopPropagation(); // Остановка распространения события клика
                                          handleTagToggle(tag.termId);
                                      }}
                                    />
                                    <div className='tag-pseudo-checkbox pointer' onClick={(event) => event.stopPropagation()}>{tag.name}{!activeCategory && (<span className="tag-count">{tag.count}</span>)} </div>
                                </label>
                            ))}
                            {/* <button type='button' className='sorting-button'>Готово</button> */}
                        </div>
                        <button type='button' className='sorting-button'>Готово</button>
                    </div>
                </div>
            </div>
            <div className='tag-area'>
             {tags && specialTags.map(tag => (
               <label key={tag.termId} className='tag-label'>
                <input
                  type="checkbox"
                  value={tag.termId}
                  className='tag-button'
                  checked={selectedTags && selectedTags.some(selectedTag => selectedTag === tag.termId) ? 'checked' : ''}
                  onChange={() => handleTagToggle(tag.termId)}
                />
                <div className='tag-pseudo-button pointer'>{tag.name}{!activeCategory && (<span className="tag-count">{tag.count}</span>)} </div>

              </label>
            ))}

            </div>
        </section>
    );
};


                 {/* {tags && filteredTags.map(tag => (
               <label key={tag.termId} className='tag-label'>
                <input
                  type="checkbox"
                  value={tag.termId}
                  className='tag-button'
                  checked={selectedTags && selectedTags.some(selectedTag => selectedTag === tag.termId) ? 'checked' : ''}
                  onChange={() => handleTagToggle(tag.termId)}
                />
                <div className='tag-pseudo-button pointer'>{tag.name}{!activeCategory && (<span className="tag-count">{tag.count}</span>)} </div>
                выше этот восклицательный потому что у категорий нет отдельного подсчёта пока
              </label>
            ))} */}
