import './Profile.css';
import Header from '../Header/Header';
import MobileNavBar from "../MobileNavBar/MobileNavBar";
import productsApi from '../../utils/Api';
import { useNavigate, NavLink } from 'react-router-dom';
import { useMediaQuery } from 'react-responsive';
import { useState, useEffect } from "react";

export default function Profile({ currentUser, savedProducts, cart }) {
  // const isBigScreen = useMediaQuery({ minWidth: 768 }); // надо бы перенести в константы
  // const isSmallScreen = useMediaQuery({ maxWidth: 767 });

  const [cartProducts, setCartProducts] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  let wishlistPreviews = wishlist.slice(-6);
  // const previewPic = `https://dolina.shop/photos/${pictures[0].path.slice(0, -4) + `_preview.jpg`}`;

  useEffect(() => {
    const productIds = Object.keys(cart);
    productsApi.getProductsById([productIds])
      .then((res) => {
        setCartProducts(res)
        // console.log(res)
      })
      .catch((err) => console.log(err))
  }, [cart])

  useEffect(() => {
    productsApi.getProductsById([savedProducts])
      .then((res) => {
        setWishlist(res)
        console.log(res)
      })
      .catch((err) => console.log(err))
  }, [savedProducts])

  return (
    <>
    <Header/>

    <main className='profile'>
      <h1 className='wishlist-header'>Личный кабинет</h1>
      <section className='user-info'>
        <div className='user-info__avatar'>
          <p className='user-info__avatar-caption'>M</p>
        </div>
        <div className='user-info__container'>
          <p className='user-info__name'>{currentUser.name}</p>
          <p className='user-info__email'>{currentUser.email}</p>
        </div>
        <NavLink to='/' className='navlink_size_s '>Редактировать </NavLink>
      </section>
      <div className='flex_type_row'>
        <section className='profile__orders'>
          <h2 className='profile__subheader'>Заказы</h2>
        </section>
        <section className='profile__wishlist'>
          <h2 className='profile__subheader'>Избранное</h2>
          <ul className='profile__wishlist_ul'>
            {wishlistPreviews.map((product) => (
              <li className='profile__wishlist_li'>
                <img className='profile__wishlist-pic' src={`https://dolina.shop/photos/${product.pictures[0].path.slice(0, -4) + `_preview.jpg`}`}></img>
              </li>
            ))}
            <li className='profile__wishlist_li'>{`${(products.length - 6)}`}</li>
          </ul>
        </section>
      </div>
      
      <MobileNavBar/>
    </main>

    </>
  )
}

