import './Profile.css';
import Header from '../Header/Header';
import MobileNavBar from "../MobileNavBar/MobileNavBar";
import productsApi from '../../utils/Api';
import ProfilePopup from './ProfilePopup';
import { useNavigate, NavLink } from 'react-router-dom';
import { useMediaQuery } from 'react-responsive';
import { useState, useEffect, useContext } from "react";
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

export default function Profile({ savedProducts, orders, onUpdateUser, onSignout, onChangeClick, onCloseClick, isPopupOpen, terms, onCatClick, onBurgerClick, onBurgerClose, isBurgerOpen, search, setSearch, cart, isLoggedIn }) {
  // const isBigScreen = useMediaQuery({ minWidth: 768 }); // надо бы перенести в константы
  // const isSmallScreen = useMediaQuery({ maxWidth: 767 });

  useEffect(() => {
    document.title = 'Мой профиль — Долина самоцветов';
  }, []);

  const currentUser = useContext(CurrentUserContext);
  // const [isProfilePopupOpen, setIsProfilePopupOpen] = useState(false);
  const [wishlist, setWishlist] = useState([]);
  // const [userName, setUserName] = useState(currentUser.name);
  // const [userMail, setUserMail] = useState(currentUser.email);
  let wishlistPreviews = wishlist.slice(-4);
  let ordersPreviews = orders.slice(0, 1);

  // function signOutAndPopup() {
    
  // }

  function getDate(timestamp) {
    return new Date(timestamp * 1000).toLocaleDateString('ru-RU');
  }

  const getStatusName = (index) => {
    switch (index) {
        case 0:
            return 'Новый';
        case 1:
            return 'Собираем заказ';
        case 2:
            return 'В пути';
        case 3:
            return 'Доставлен';
        case -1:
            return 'Отменён';
        default:
            return '';
    }
};

const getStatusClass = (index) => {
  switch (index) {
      case 0:
          return 'status-new';
      case 1:
          return 'status-collecting';
      case 2:
          return 'status-on-the-way';
      case 3:
          return 'status-delivered';
      case -1:
          return 'status-cancelled';
      default:
          return '';
  }
};


  useEffect(() => {
    productsApi.getProductsById([savedProducts])
      .then((res) => {
        setWishlist(res)
        // console.log(res)
      })
      .catch((err) => console.log(err))
  }, [savedProducts])


  // useEffect(() => {
  //   console.log(isPopupOpen);
  // }, [isPopupOpen]);

  // useEffect(() => {
  //   setUserName(currentUser.name);
  //   setUserMail(currentUser.email);
  // }, [currentUser]);



  // function onChangeClick2() {
  //   onChangeClick();
  // }


  return (
    <>

      <main className='profile'>
        <h1 className='wishlist-header'>Личный кабинет</h1>
        <section className='user-info'>
          <div className='user-info__avatar'>
            <p className='user-info__avatar-caption'>{currentUser?.name?.slice(0, 1)}</p>
          </div>
          <div className='user-info__container'>
            <p className='user-info__name'>{currentUser.name}</p>
            <p className='user-info__email'>{currentUser.email}</p>
            <NavLink to='' onClick={onChangeClick} className='navlink_size_s '>Редактировать </NavLink>
          </div>
          {/* <NavLink to='/' className='navlink_size_s '>Редактировать </NavLink> */}
        </section>
        <div className='profile_flex'>
          <section className='profile__orders'>
            <NavLink to='/me/orders' className='profile__link'>


              <h2 className='profile__subheader'>Заказы &#8594;</h2>
              <ul className='profile__orders_ul'>
                {ordersPreviews.map((order) => (

                  // <li className='order' key={order.orderId} style={{margin: '0'}}>
                  //   <div className='flex_type_row order__head'>
                  //     <h3 className='order__header'>Заказ №{order.orderId} от {getDate(order.createdAt)}</h3>
                  //     <p className='order__price'>{order.totalPrice} &#8381;</p>
                  //   </div>
                  //   <div className='flex_type_row order__body' style={{padding: '10px'}}>
                  //     <div className=''>
                  //       {/* <p className='order__status'>{order.orderStatus === 0 ? "Создан" : "нет"}</p> */}
                  //       <p className={`order__status ${getStatusClass(order.orderStatus)}`} style={{marginLeft:'15px'}}>{getStatusName(order.orderStatus)}</p>
                  //       {order.tracking > 0 ? (
                  //         <p className='order__main-text'>Трек-номер: <span className='order_tracking-number'>{order.tracking}</span></p>
                  //       ) : (
                  //         null
                  //       )}
                  //     </div>

                  //     <div className='order__products'>
                  //       {order.orderedProducts.slice(0, 3).map((product) => (
                  //         <img className='order__pic_mini' alt={product.title} src={`https://dolina.shop/photos/${product.path.slice(0, -4) + `_preview.jpg`}`}></img>
                  //       ))}

                  //     </div>
                  //   </div>
                  // </li>
                  <li className='order' key={order.orderId}>
                      <div className='flex_type_row order__head'>
                        <h3 className='order__header'>Заказ от {getDate(order.createdAt)}</h3>
                        <p className='order__total-price'>{order.totalPrice + order.deliveryPrice} &#8381;</p>
                        

                      </div>
                      <div className='order__body'>
                        <div className='order__main-info'>
                            {/* <p className={`order__status ${getStatusClass(order.orderStatus)}`}>{getStatusName(order.orderStatus)}</p> */}
                            {order.tracking > 0 ? (
                            <p className='order__main-text'>Трек-номер: <span className='order_tracking-number'>{order.tracking}</span></p>
                          ) : (
                            null
                          )}
                          <p className='order__main-text'>Номер заказа: {order.orderId}</p>
                          <p className='order__main-text'>{getDeliveryTypeName(order.deliveryType)}: {order.deliveryOffice ? order.deliveryOffice : `${order.city + ', ' + order.deliveryAddress}`}</p>
                          {/* <p className='order__main-text'>Получатель: {order.fullName}</p> */}
                          {!order.comment ? (null) : (
                            <p className='order__main-text'>Комментарий к заказу: {order.comment}</p>
                          )}

                          <p className='order__main-text'>Стоимость доставки: {order.deliveryPrice} &#8381;</p>
                        

                            {order.orderStatus === 4
                              ? (null)
                              : (
                                order.paid === 1 ? (
                                  <p className='order_paid'>Оплачен</p>
                                ) : (
                                  (order.paymentType !== 3 ? <><p className='order_not-paid'>Не оплачен</p><span className='orders_pay'>Оплатить</span></> : <p className='order_not-paid'>Не оплачен</p>)
                                )
                              )}


                          {/* </div> */}
                          <p className={`order__status ${getStatusClass(order.orderStatus)}`}>{getStatusName(order.orderStatus)}</p>

                        </div>


                        <div className='order__products'>
                          {order.orderedProducts.map((product) => (
                            <div className='order__product-container'>
                              
                              <div className='order_flex_row'>
                              <a key={product.id} href={`${'/product/' + product.slug + '-' + product.productId}`} target="_blank" without rel="noreferrer">
                                <img className='order__pic' alt={product.title} src={`https://dolina.shop/photos/${product.path.slice(0, -4) + `_preview.jpg`}`}></img>
                              </a>
                              <div className=''>
                              <a className='order__product-title' href={`${'/product/' + product.slug + '-' + product.productId}`} target="_blank" without rel="noreferrer"><h3 className='order__product-title'>{product.title}</h3></a>
                                {/* {cartDetails && <p className='cart-product__size'>Размер: {cartDetails.size || 1}</p>} */}
                               {(product.size > 0) ? <p className='cart-product__size'>Размер: {product.size}</p> : null}
                              </div>


                    
                              </div>
                              <p className='order__product-title'>{product.price} &#8381;</p>
                            </div>


                          ))}
                        </div>
                      </div>
                    </li>

                ))}
              </ul>
            </NavLink>
          </section>
          <section className='profile__wishlist'>
            <NavLink to='/me/wishlist' className='profile__link'>
              <h2 className='profile__subheader'>Избранное &#8594;</h2>
              <ul className='profile__wishlist_ul'>
                {wishlistPreviews.map((product) => (
                  <li className='profile__wishlist_li' key={product.pictures[0].path}>
                    <img className='profile__wishlist-pic' src={`https://dolina.shop/photos/${product.pictures[0].path.slice(0, -4) + `_preview.jpg`}`}></img>
                  </li>
                ))}
                {(wishlist.length > 4) &&
                  <li className='profile__wishlist_li'>{`+${savedProducts.length - 4}`}</li>
                }
                {/* <li className='profile__wishlist_li'>{`+${savedProducts.length - 4}`}</li> */}
              </ul>
            </NavLink>
          </section>
        </div>
        <ProfilePopup isOpen={isPopupOpen} onClose={onCloseClick} onUpdateUser={onUpdateUser} onSignout={onSignout} />
        <MobileNavBar
          onBurger={onBurgerClick}
          isBurgerOpen={isBurgerOpen}
          onBurgerClose={onBurgerClose}
          cart={cart}
          orders={orders}
          onCatClick={onCatClick}
          isLoggedIn={isLoggedIn}
        />
      </main>

    </>
  )
}

